/*Table structure for table `newspdf` */

DROP TABLE IF EXISTS `newspdf`;

CREATE TABLE `newspdf` (
  `pid` int(11) NOT NULL AUTO_INCREMENT,
  `pdfname` varchar(45) NOT NULL,
  `pdfdate` varchar(55) NOT NULL,
  PRIMARY KEY (`pid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `newspdf` */

/*Table structure for table `newstory` */

DROP TABLE IF EXISTS `newstory`;

CREATE TABLE `newstory` (
  `nsid` int(11) NOT NULL AUTO_INCREMENT,
  `ntitle` varchar(500) DEFAULT NULL,
  `nsub` varchar(512) DEFAULT NULL,
  `newstory` varchar(5000) DEFAULT NULL,
  `nimage` varchar(512) DEFAULT NULL,
  `newstype` varchar(45) DEFAULT NULL,
  `newsdate` varchar(100) DEFAULT NULL,
  `mycolor` varchar(45) NOT NULL,
  `priority` varchar(45) NOT NULL,
  `rid` varchar(25) NOT NULL,
  PRIMARY KEY (`nsid`)
) ENGINE=InnoDB AUTO_INCREMENT=68 DEFAULT CHARSET=utf8;

/*Data for the table `newstory` */

insert  into `newstory`(`nsid`,`ntitle`,`nsub`,`newstory`,`nimage`,`newstype`,`newsdate`,`mycolor`,`priority`,`rid`) values (1,'ଏନ୍‌ସେଫାଲାଇଟିସ୍‌: ୪୬ ଦିନରେ ୭୧ ଜଣ ମୃତ୍ୟୁ',NULL,'ମାଲକାନଗିରି: ମାଲକାନଗିରି ଜିଲ୍ଲାରେ ଆତଙ୍କ ସୃଷ୍ଟି କରିଥିବା ଜାାପାନୀ ଏନ୍‌ସେଫାଲାଇଟିସ୍‌ କମିବାର ନାଁ ନେଉ ନାହିଁ । ଆଜି ମଧ୍ୟ ମାଲକାନଗିରି ଜିଲ୍ଲାର ମୁଖ୍ୟ ଚିକିତ୍ସାଳୟରେ ଜାପାନୀ ଏନ୍‌ସେଫାଲାଇଟିସ୍‌୍‌ ରୋଗରେ ଆକ୍ରାନ୍ତ ହୋଇ ଚିକିତ୍ସାଧୀନ ଥିବା ଗୋଟିଏ ଶିଶୁର ମୃତ୍ୟୁ ଘଟିଛି । ସୋମବାର ମୃତ୍ୟୁ ବରଣ କରିଥିବା ଶିଶୁଟିର ନାଁ ମନୋଜ ବରାଳ(୫) । ଏବଂ ଘର ମାଲକାନଗିରି ଜିଲ୍ଲାର ବରଳା ଗାଆଁରେ ବୋଲି ଜଣାପଡ଼ିଛି । ସୋମବାର ମନୋଜଙ୍କ ମୃତ୍ୟୁ ସହ ଏହି ରୋଗରେ ମାଲକାନଗିରି ଜିଲ୍ଲାରେ ଗତ ୪୬ ଦିନରେ ୭୧ ଜଣ ମୃତ୍ୟୁ ବରଣ କରି ସାରିଲେଣି । \r\n \r\nଅନ୍ୟପକ୍ଷରେ କେନ୍ଦ୍ରୀୟ ଟିମ୍‌ ସୋମବାର ମାଲକାନଗିରିର ଜାାପାନୀ ଏନ୍‌ସେଫାଲାଇଟିସ୍‌ ପ୍ରଭାବିତ ଅଞ୍ଚଳ ପରିଦର୍ଶନ କରି ସ୍ଥିତି ଅନୁଧ୍ୟାନ କଲା ପରେ ଜାପାନୀ ଜ୍ୱର ନିୟନ୍ତ୍ରଣରେ ଅଛି ବୋଲି ମତ ଦେଇଛନ୍ତି । କେନ୍ଦ୍ରୀୟ ଟିମ୍‌ରେ ବିଶେଷଜ୍ଞ ଦଳର ସଦସ୍ୟ ଥିବା କଳ୍ପନା ବରୁଆ ଗଣମାଧ୍ୟମକୁ ସୂଚନା ଦେଉ କହିଛନ୍ତି ଯେ ଏଣିକି ପ୍ରଭାବିତ ଅଞ୍ଚଳରେ ଜାପାନୀ ଜ୍ୱରର ପ୍ରତିଷେଧକ ଲୋକମାନଙ୍କୁ ଦିଆଯିବ ।','2016_10_25_00_57_37_en.jpg','Newstory','27-01-2017','','',''),(2,'ସାଇକ୍ଲୋନ୍‌ କ୍ୟାଣ୍ଟ: କିଏ କ’ଣ ସୂଚନା ଦିଅନ୍ତି',NULL,'ଭୁବନେଶ୍ୱର: ପୋର୍ଟବ୍ଲେୟାର ଠାରୁ ୫୨୦ କିଲୋମିଟର ଦୂରରେ ସାଇକ୍ଲୋନ କ୍ୟାଣ୍ଟ ରହିଛି ବୋଲି ଆଇଏମଡି ସାଇକ୍ଲୋନ ୱାର୍ଣ୍ଣିଂ ମୁଖ୍ୟ ମୃତ୍ୟୁଞ୍ଜୟ ମହାପାତ୍ର ସୂଚନା ଦେଇଛନ୍ତି। ୨୭ ତାରିଖ ଦିନ ଉତ୍ତର-ପୂର୍ବ ବଙ୍ଗୋପସାଗରରେ ସାଇକ୍ଲୋନ କ୍ୟାଣ୍ଟ ପହଞ୍ଚିବ। ଏହାର ପ୍ରଭାବରେ ୨୭ ତାରିଖରୁ ଉପକୂଳ ଓଡ଼ିଶାରେ ୪୫ କିଲୋମିଟର ବେଗରେ ପବନ ବହିବାର ସମ୍ଭାବନା ରହିଥିବା ଦୃଷ୍ଟିରୁ ମତ୍ସଜୀବୀମାନଙ୍କୁ ସମୁଦ୍ର ଭିତରକୁ ଯିବାକୁ ବାରଣ କରାଯାଇଛି। \r\n\r\nସେହିପରି ବଙ୍ଗୋପସାଗରରେ ସୃଷ୍ଟି ହୋଇଥିବା ଲଘୁଚାପ ଡିପ ଡିପ୍ରେସନର ରୂପରେ ଉତ୍ତର ଦିଗରେ ଗତି କରୁଥିବା ସ୍କାଏମେଟ ସୂଚନା ଦେଇଛି। ମିଆଁମାରରୁ କ୍ୟାଣ୍ଟ ଉତ୍ତର ପଶ୍ଚିମ ବଙ୍ଗୋପସାଗର ଆଡ଼କୁ ଗତି କରିବ । ୨୭ତାରିଖ ସୁଦ୍ଧା ଏହା ଓଡ଼ିଶା ଉପକୂଳ ଛୁଇଁବ ବୋଲି ସ୍କାଏମେଟ ପୂର୍ବାନୁମାନ କରିଛି। \r\n\r\n \r\nବଙ୍ଗୋପସାଗରରେ ସୃଷ୍ଟି ହୋଇଥିବା ଲଘୁଚାପ ଡିପ୍‌ ଡିପ୍ରେସନର ରୂପ ନେଇ ଏବେ ସମୁଦ୍ର ଉପର ଦେଇ ଗତି କରୁଛି। ଏବେ ଗୋପାଳପୁରର ଦକ୍ଷିଣକୁ ୯୩୦ କିଲୋମିଟର ଦୂରରେ ଅବସ୍ଥିତ ଏହି ଲଘୁଚାପ ଏବେ ଘଣ୍ଟା ପ୍ରତି  ୮ କିଲୋମିଟର ବେଗରେ ଗତି କରୁଛି। ସନ୍ଧ୍ୟା ସୁଦ୍ଧା ଏହା ଘୂର୍ଣ୍ଣିଝଡ଼ର ରୂପ ନେଇପାରେ ବୋଲି ଅନୁମାନ କରୁଛନ୍ତି ବିଟ୍ରିଶ ଷ୍ଟ୍ରର୍ମ ଟ୍ରାକର।','2016_10_25_00_59_11_cy.png','Newstory','27-01-2017','','',''),(4,'ଅବ୍‌ଦୁଲାତି ବିଶ୍ୱର ସବୁଠୁ ପୃଥୁଳା ମହିଳା',NULL,'କାଇରୋ(ଇଜିପ୍ଟ) : ଇଜିପ୍ଟର ୩୬ ବର୍ଷୀୟା ଇମାମ୍‌ ଅହମଦ ଅବ୍‌ଦୁଲାତିଙ୍କୁ ଆଖିରେ ନଦେଖିଲେ ବିଶ୍ୱାସ କରିହେବନି। ବିଶ୍ୱର ସବୁଠୁ ପୃଥୁଳକାୟ ମହିଳା ହେବାର ବିରଳ ରେକର୍ଡକୁ ହାତେଇଥିବା ଅବ୍‌ଦୁଲାତିଙ୍କର ଓଜନ ଏବେ ୫୦୦ କେଜି। \r\n \r\nତାଙ୍କର ଏହି ବିଶାଳ ଚେହେରା ଯୋଗୁଁ ଗତ ୨୫ ବର୍ଷ ହେଲା ସେ ଘରୁ ବାହାରକୁ ଆସିନାହାନ୍ତି କି ନିଜର ଶଯ୍ୟାରୁ ଓହ୍ଲାଇପାରିନାହାନ୍ତି ମଧ୍ୟ। ଏପରିକି ଏଭଳି ଅନିୟନ୍ତ୍ରିତ ପୃଥୁଳତା ଯୋଗୁଁ ତାଙ୍କ ପାଇଁ ନିର୍ମିତ ସ୍ୱତନ୍ତ୍ର ଖଟ ଉପରେ ପଡ଼ି ରହି ସେ ମଧ୍ୟ କଡ଼ ଲେଉଟାଇ ପାରୁନାହାନ୍ତି। ନିତ୍ୟକର୍ମ ସାରିବା, ବସ୍ତ୍ର ବଦଳାଇବା ଏବଂ ଖାଇବା ପରି ଦୈନନ୍ଦିକ କାର୍ଯ୍ୟ ପାଇଁ ସେ ନିଜ ମା’ ଏବଂ ଭଉଣୀଙ୍କ ଉପରେ ନିର୍ଭର କରୁଛନ୍ତି। \r\n \r\nସୂଚନାଯୋଗ୍ୟ, ଜନ୍ମ ବେଳେ ମାତ୍ର ୫ କେଜି ଓଜନର ହୋଇଥିବା ଅବ୍‌ଦୁଲାତି ‘ଏଲିଫାଣ୍ଟିଆସିସ୍‌’ ନାମକ ଏକ ପରଜୀବୀ ସଂକ୍ରମଣର ଶିକାର ହୋଇଥିଲେ ଯାହାକି ତାଙ୍କ ଅଙ୍ଗପ୍ରତ୍ୟଙ୍ଗର ଅସ୍ୱାଭାବିକ ବୃଦ୍ଧି ଘଟାଇ ଚାଲିଲା। ଏଥି ସହିତ ତାଙ୍କ ଗୁରୁତ୍ୱପୂର୍ଣ୍ଣ ଗ୍ରନ୍ଥିରେ ମଧ୍ୟ ବ୍ୟତିକ୍ରମ ହେତୁ ଶରୀରରେ ଆବଶ୍ୟକତାଠାରୁ ଯଥେଷ୍ଟ ଗୁଣ ଅଧିକ ଜଳ ଠୁଳ ହେବାକୁ ଲାଗିଲା। ଫଳରେ ୧୧ ବର୍ଷ ବୟସ ବେଳୁ ଅବ୍‌ଦୁଲାତି ଏଭଳି ପୃଥୁଳ ହେବାକୁ ଲାଗିଲେ ଯେ ସେ ଆପେ ଆପେ ଠିଆହୋଇ ପାରିଲେନି ବୋଲି ତାଙ୍କୁ ଦେଖାଶୁଣା କରୁଥିବା ଡାକ୍ତରମାନେ କହିଛନ୍ତି। ଦୁର୍ଭାଗ୍ୟକୁ ସେତେବେଳକୁ ପ୍ରାଥମିକ ବିଦ୍ୟାଳୟରେ ପଢୁଥିବା ଅବ୍‌ଦୁଲାତି ମସ୍ତିଷ୍କଘାତର ଶିକାର ହୋଇଥିଲେ ଯାହା କି ତାଙ୍କୁ ସମ୍ପୂର୍ଣ୍ଣ ଶଯ୍ୟାଶାୟୀ କରିଦେଇଥିଲା। ସେବେଠାରୁ ସେ ପ୍ରାୟତଃ ଅଚଳପ୍ରାୟ ହୋଇ ସାମାନ୍ୟତମ କାମ କରିବାକୁ ମଧ୍ୟ ସକ୍ଷମ ହୋଇପାରିଲେନି।','2016_10_25_01_03_13_moti.jpeg','Newstory','27-01-2017','','',''),(5,'ପାକିସ୍ତାନରେ ‘ସର୍ଜିକାଲ ଷ୍ଟ୍ରାଇକ୍‌’ ପାଇଁ ଆମେରିକ',NULL,'ଓ୍ଵାଶିଂଟନ : ଆବଶ୍ୟକ ପଡ଼ିଲେ ଆମେରିକା ପାକିସ୍ତାନରେ ବିସ୍ତାର ଲାଭ କରୁଥିବା ଆତଙ୍କବାଦୀ ସଂଗଠନର ମୂଳପୋଛ ପାଇଁ  ସେଠାରେ ଦୃଢ଼ ପଦକ୍ଷେପ ନେବ। ପାକିସ୍ତାନରେ ପଶି ଏକପାଖିଆ ଆକ୍ରମଣ କରି ଆତଙ୍କବାଦୀଙ୍କୁ ନିପାତ କରାଯିବ ବୋଲି ଆଜି ପାକିସ୍ତାନକୁ ରୋକଠୋକ୍‌ ଶୁଣାଇ ଦେଇଛି ଆମେରିକା । ବାରମ୍ବାର ଚେତାବନୀ ସତ୍ତ୍ୱେ ଆତଙ୍କବାଦୀଙ୍କ ବିରୋଧରେ ଉପଯୁକ୍ତ କାର୍ଯ୍ୟାନୁଷ୍ଠାନ ଗ୍ରହଣ କରୁନଥିବାରୁ ପାକିସ୍ତାନରେ ସର୍ଜିକାଲ ଷ୍ଟ୍ରାଇକ୍‌ ପାଇଁ ଆମେରିକା ପ୍ରଥମ ଥର ପାଇଁ ଚେତାବନୀ ଦେଇଛି।\r\n \r\nପାକିସ୍ତାନ ଏହା ମାଟିରେ ବିସ୍ତାର ଲାଭ କରୁଥିବା ଆତଙ୍କବାଦୀ ସଂଗଠନଗୁଡ଼ିକୁ ରୋକିବାରେ ବିଫଳ ହୋଇଛି।   ପାକିସ୍ତାନ ଗୁଇନ୍ଦା ସଂସ୍ଥା ଆଇଏସଆଇ ଆତଙ୍କବାଦୀଙ୍କୁ ପ୍ରଶୟ ଦେଇଚାଲିଛି। ତେଣୁ ଆବଶ୍ୟକ ପଡ଼ିଲେ ଆମେରିକା ନିଜଆଡ଼ୁ ଏସବୁ ଆତଙ୍କବାଦୀ ସଂଗଠନର ନିପାତ ପାଇଁ ଉଦ୍ୟମ କରିବ ବୋଲି ଆମେରିକା ଆତଙ୍କବାଦ ବିରୋଧୀ ସଂଗଠନର କାର୍ଯ୍ୟକାରୀ ସଚିବ ଆଦାମ ଯୁବେନ୍‌ କହିଛନ୍ତି। ଆତଙ୍କବାଦୀଙ୍କ ବିରୋଧରେ କଡ଼ା କାର୍ଯ୍ୟାନୁଷ୍ଠାନ ପାଇଁ ଆମେରିକା ବାରମ୍ବାର ପାକିସ୍ତାନକୁ ଚେତାଇ ଆସିଛି। ଏଥିପାଇଁ  ଆମେରିକା  ସମସ୍ତ ସହାୟତା ମଧ୍ୟ  ଯୋଗାଇ ଦେଇଛି।  ମାତ୍ର ଏହା ସତ୍ତ୍ୱେ ବି ପାକିସ୍ତାନ ଯଦି ଆତଙ୍କବାଦୀଙ୍କ ବିରୋଧରେ କାର୍ଯ୍ୟାନୁଷ୍ଠାନ ଗ୍ରହଣ କରିବା ବିଫଳ ହୁଏ ତାହେଲେ ଆମେରିକା ଏଦିଗରେ ପ୍ରତ୍ୟକ୍ଷ କାର୍ଯ୍ୟାନୁଷ୍ଠାନ ପାଇଁ ବାଧ୍ୟ ହେବ।   ପାକିସ୍ତାନ ନିଜେ ମଧ୍ୟ ଆତଙ୍କବାଦ ଦ୍ୱାରା ପ୍ରପୀଡ଼ିତ। ଏହାସହ ପାକିସ୍ତାନ ସେଠାର କିଛି ଆତଙ୍କବାଦୀଙ୍କ ବିରୋଧରେ ଜାରି ରଖିଥିବା ଅଭିଯାନରେ  ସଫଳ ମଧ୍ୟ ହୋଇଛି। ମାତ୍ର ପାକ୍‌ ଗୁଇନ୍ଦା ଆଇଏସଆଇର ଆତଙ୍କବାଦୀ ପ୍ରତି ସମର୍ଥନ ଏଦିଗରେ ମୁଖ୍ୟ ବାଧକ ସାଜିଛି। ପାକିସ୍ତାନ ସରକାର ଓ ଗୁଇନ୍ଦା ସମର୍ଥନରେ ବିଭିନ୍ନ ଆତଙ୍କବାଦୀ ସଂଗଠନ ସେଠାରେ ଅଧିକ ଶକ୍ତିଶାଳୀ ହେଉଥିବା ସେ କହିଛନ୍ତି।','2016_10_25_01_05_14_sur.jpg','Newstory','27-01-2017','','',''),(6,'୨୫ ଓ ୨୬ରେ ପ୍ରବଳ ବର୍ଷା ସମ୍ଭାବନା',NULL,'ଭୁବନେଶ୍ୱର : ବଙ୍ଗୋପସାଗରରେ ସୃଷ୍ଟି ହୋଇଥିବା ଲଘୁଚାପ ଡିପ୍ ଡିପ୍ରେସନର ରୂପ ନେଇଥିବା ଜଣାପଡ଼ିଛି । ଗୋପାଳପୁରରୁ ଦକ୍ଷିଣକୁ ୯୫୦ କିଲୋମିଟର ଦୂରରେ ରହିଛି ଏହି ଲଘୁଚାପ । ଘଣ୍ଟା ପ୍ରତି ୧୦କିଲୋମିଟର ବେଗରେ ଗତି କରୁଛି ବୋଲି ପାଣିପାଗ ବିଭାଗ ସୂଚନା ଦେଇଛି । ସଂଧ୍ୟା ସୁଦ୍ଧା ଘୂର୍ଣ୍ଣିଝଡର ରୂପ ନେଇପାରେ ବୋଲି ଅନୁମାନ କରାଯାଉଛି । ତେବେ ଆସନ୍ତା ୨୪ ଘଣ୍ଟା ମଧ୍ୟରେ ଏହା ଓଡ଼ିଶା ଉପକୂଳ ଛୁଇଁବା ପାରେ।   ଏବେ ଏହା ମିଆଁମାର ଆଡକୁ ଗତି କରୁଛି ।  ଯଦି ଏହା ଦିଗ ପରିବର୍ତନ କରେ  ତେବେ ଏହି ସାମୁଦ୍ରିକ ଝଡ ଓଡ଼ିଶା ଉପକୂଳକୁ  ପ୍ରଭାବିତ କରିବାର ଆଶଙ୍କା ରହିଛି ।\r\n\r\n \r\nଆଞ୍ଚଳିକ ପାଣିପାଗ କାର୍ଯ୍ୟାଳୟର ସୂଚନା ଅନୁସାରେ ଏହାର ପ୍ରଭାବରେ ୨୫ ଓ ୨୬ ତାରିଖରେ ଉପକୂଳ ଓଡିଶାରେ ବର୍ଷା ହେବାର ସମ୍ଭାବନା ରହିଛି। ୨୭ ତାରିଖରେ ଉତ୍ତର-ଦକ୍ଷିଣ ଓଡ଼ିଶାରେ ପ୍ରବଳ ବର୍ଷା ହେବାର ସମ୍ଭାବନା ରହିଛି । ତେବେ ଆଇଏମଡିର ସୂଚନା ଅନୁଯାୟୀ ବାତ୍ୟା ସ୍ଥଳଭାଗକୁ ମାଡ କରିବ ନା ସମାନ୍ତରାଳ ଗତି କରିବ ତାହା ଏଯାଏଁ ଅସ୍ପଷ୍ଟ । ସତର୍କତା ଦୃଷ୍ଟିରୁ ସମସ୍ତ ରାଜ୍ୟର ବନ୍ଦରରେ ୧ ନମ୍ବର ବିପଦ ସଂକେତ ଜାରି କରାଯାଇଛି । ସେହିପରି ମତ୍ସ୍ୟଜୀବୀଙ୍କ ପାଇଁ ମଧ୍ୟ ସତର୍କ ସୂଚନା ଜାରି କରିଛି ପାଣିପାଗ ବିଭାଗ ।','2016_10_25_01_06_46_rain.jpg','Newstory','27-01-2017','','',''),(7,'ଦରମା ୮ ହଜାର, ୩ କୋଟିର ମାଲିକ',NULL,'ଭୋପାଳ: ମାସିକ ଆଠ ହଜାର ଟଙ୍କା ଦରମା ପାଉଥିବା ଜନୈକ ପ୍ରବନ୍ଧକଙ୍କ ଘରୁ ଶନିବାର ୫୦ କିଲୋଗ୍ରାମ ରୁପା, ୧୫୦ ଗ୍ରାମ ସୁନା ଓ ୧୭ଟି ବ୍ୟାଙ୍କ ପାସ୍‌ ବୁକ ଜବତ କରିଛି ଗୋଆଲିୟର ଆୟକର ବିଭାଗର ସ୍ୱତନ୍ତ୍ର ଟିମ୍‌। ମଧ୍ୟପ୍ରଦେଶର ଗୁଣା ସମବାୟ ସମିତିରେ ମ୍ୟାନେଜର ଥିବା ଅଶୋକ ଶ୍ରୀବାସ୍ତବଙ୍କ ଦୁଇଟି ଘରୁ ମିଳିଛି ଏହି ସମ୍ପତ୍ତି। \r\n \r\nଆୟର୍ବହିଭୂତ ସମ୍ପତ୍ତି ଠୁଳ କରିବା ଅଭିଯୋଗରେ ଅଶୋକଙ୍କ ଘର ଉପରେ ଆୟକର ବିଭାଗ ପକ୍ଷରୁ ଚଢ଼ାଉ କରା ଯାଇଥିଲା। ଚଢ଼ାଉ ସମୟରେ ଅଶୋକ ଶ୍ରୀବାସ୍ତବଙ୍କ ଘରୁ ପାଖାପଖି ୧କୋଟି ୩୫ ଲକ୍ଷ ଟଙ୍କାର ୪୫ଟି ବୀମା କାଗଜପତ୍ର ଜବତ କରାଯାଇଛି। ମ୍ୟାନେଜରଙ୍କ ଘରୁ ମିଳିଥିବା ୧୭ଟି ବ୍ୟାଙ୍କ ପାସବୁକରେ ୧୫ ଲକ୍ଷ ଟଙ୍କା ଜମା ଥିବା ଜଣାପଡ଼ିଛି। ଏଥିରେ ୧.୫ଲକ୍ଷ ଟଙ୍କାର ସ୍ଥାୟୀ ଜମା ମଧ୍ୟ ସାମିଲ ଅଛି। ଏହା ବ୍ୟତୀତ ଅଶୋକଙ୍କ ଗୋଟିଏ ଘରୁ ୭.୬୧ଲକ୍ଷ ଓ ଅନ୍ୟ ଘରୁ ୨୬,୦୦୦ ନଗଦ ଟଙ୍କାକୁ ମଧ୍ୟ ଜବତ କରିଛଇ ଆୟକର ବିଭାଗ। \r\n \r\nଆୟକର ବିଭାଗର ଜନୈକ ବରିଷ୍ଠ ଅଧିକାରୀଙ୍କ କହିବା ଅନୁସାରେ ଅଶୋକଙ୍କ ବର୍ତ୍ତମାନ ଦରମା ଆଠ ହଜାର ଟଙ୍କା। ହେଲେ ସେ ପାଖାପାଖି ୩କୋଟି ଟଙ୍କାର ମାଲିକ। ଅଶୋକ ୧୯୮୯ରୁ ୧୯୯୩ ପର୍ଯ୍ୟନ୍ତ ସମବାୟ ସମିତିରେ ଏଜେଣ୍ଟ ଭାବରେ କାମ କରୁଥିଲେ। ଏହାପରେ ସେ ମ୍ୟାନେଜର ହୋଇଥିଲେ।','2016_10_25_01_28_14_sal.jpg','Newstory','27-01-2017','','',''),(8,'ସୁନା ବିସ୍କୁଟ୍‌ ଜବତ',NULL,'କୋଲକାତା: ବିମାନ ବନ୍ଦରରୁ ଜଣେ ଯାତ୍ରୀଙ୍କଠାରୁ ୩ଟି ସୁନା ବିସ୍କୁଟ୍‌ ଜବତ ହୋଇଛି। ସୋମବାର ଦିଲ୍ଲୀର ଜଣେ ବ୍ୟକ୍ତି ବ୍ୟାଙ୍କକ୍‌ରୁ ପାରୋ ଯାଉଥିଲେ ଭାୟା କୋଲକାତା ହୋଇ। ନେତାଜୀ ସୁବାଷ ଚନ୍ଦ୍ର ବୋଷ ଇଣ୍ଟରନ୍ୟାସନାଲ ବିମାନବନ୍ଦରରେ ତନଖିବେଳେ ବିମାନ ଗୁଇନ୍ଦା ବିଭାଗ ଜନୈକ ବ୍ୟକ୍ତିଙ୍କଠାରୁ ୨୫୧ ଗ୍ରାମ ଓଜନର ୩ଟି ସୁନା ବିସ୍କୁଟ୍‌ ଜବତ କରିଥିଲେ। ଏହାର ଆନୁମାନିକ ମୂଲ୍ୟ ୮ ଲକ୍ଷ ଟଙ୍କା ହେବ।','2016_10_25_22_58_44_Gold.jpg','Newstory','27-01-2017','','',''),(9,'ଦୁଷ୍କର୍ମ ଶିକାର ଯୁବତୀଙ୍କ ଆତ୍ମହତ୍ୟା',NULL,'ଜଳନ୍ଧର:ଏକ ଆଶ୍ରମର ବାବା ଜଣେ ଯୁବତୀଙ୍କୁ ଦୁଷ୍କର୍ମ କରିବା ପରେ ହତ୍ୟା ଧମକ ଦେବା ପରେ ଥାନାରେ ଅଭିଯୋଗ କରିବା ସତ୍ତ୍ୱେ କୌଣସି ସୁଫଳ ନ ମିଳିବା କାରଣରୁ ଯୁବତୀ ଟ୍ରେନରୁ ଡେଇଁ ଆତ୍ମହତ୍ୟା କରିବା ଘଟଣା ଚର୍ଚାର ବିଷୟ ହୋଇଛିା ପ୍ରାପ୍ତ ସୂଚନା ଅନୁଯାୟୀ ଜଳନ୍ଧରର ସରବତ ଦାଭଲା ଆଶ୍ରମର ସଞ୍ଚାଳକ ବାବା ଜଗୀର ସିଂହ ସ୍ଥାନୀୟ ଏକ ହସ୍ପିଟାଲରେ କାର୍ଯ୍ୟରତ ଜଣେ ଷ୍ଟାଫ୍‌ ନର୍ସଙ୍କୁ ଗତ ବର୍ଷ ଡିସେମ୍ବର ୨୪ ତାରିଖରେ ଦୁଷ୍କର୍ମ କରିଥିଲୋ ତାପର ଦିନ ଉକ୍ତ ଯୁବତୀ ବାବାଙ୍କ ସଂପର୍କରେ ଥାନାରେ ଅଭିଯୋଗ କଲୋ କିନ୍ତୁ ଘଟଣାର ପ୍ରାୟ ଏଗାର ମାସ ବିତିବା ପରେ ମଧ୍ୟ ପୁଲିସ୍‌ କୌଣସି କାର୍ଯ୍ୟାନୁଷ୍ଠାନ ନ କରିବା ଯୋଗୁଁ ବାଧ୍ୟ ହୋଇ ଟ୍ରେନରୁ ଡେଇଁ ପଡ଼ି ଆତ୍ମହତ୍ୟା କରିଛନ୍ତି ପୀଡିତାା\r\n \r\nପୀଡିତାଙ୍କ ମାଆଙ୍କ ଅଭିଯୋଗ ଅନୁଯାୟୀ ଉକ୍ତ ବାବା ତାଙ୍କ ଝିଅ ସହ ଦୁଷ୍କର୍ମ କରିବାପରେ ମଧ୍ୟ ବିଭିନ୍ନ ସମୟରେ ହତ୍ୟା ଧମକ ଦେବା ସହ ଅଶାଳୀନ କଥା କହୁଥିଲେଯେଉଁଥିପାଇଁ ଝିଅ ସବୁବେଳେ ମାନସିକ ଚାପରେ ରହୁଥିଲାା ପୁଲିସ ଗତକାଲି ଯୁବତୀର ମୃତ ଦେହ ଟ୍ରେନ୍‌ ଲାଇନ୍‌ ଉପରୁ ଉଦ୍ଧାର କରିବାପରେ ଶବ ବ୍ୟବଛେଦ ପାଇଁ ପଠାଇବା ସହ ଘଟଣାର ତଦନ୍ତ ଆରମ୍ଭ କରିଛିା','2016_10_25_23_00_04_jubati.jpg','Newstory','27-01-2017','','',''),(10,'ଦୁର୍ଘଟଣାରେ ଅଭିନେତା ଅମ୍ଲାନ ଆହତ',NULL,'କଟକ: କାର୍‌ ଦୁର୍ଘଟଣାରେ ଓଲିଉଡ୍ ଅଭିନେତା ଅମ୍ଲାନ ଦାସ ଆହତ ହୋଇଛନ୍ତି। ତାଙ୍କୁ ଘରୋଇ ହସପିଟାଲରେ ଭର୍ତ୍ତି କରାଯାଇଛି। ଅମ୍ଲାନଙ୍କ ମୁଣ୍ଡରେ ଆଘାତ ଲାଗିଥିବା ଜଣାପଡ଼ିଛି। କାର୍‌ରେ ଥିବା ଅନ୍ୟ ଦୁଇ ଜଣ ଆହତ ହୋଇଛନ୍ତି। ସେମାନଙ୍କୁ ଏକ ଘରୋଇ ହସପିଟାଲରେ ଭର୍ତ୍ତି କରାଯାଇଛି।\r\n\r\nଗତକାଲି ବିଳମ୍ବିତ ରାତିରେ କଟକ ମର୍କତନଗର ଥାନା ଅନ୍ତର୍ଗତ ରିଂବନ୍ଧ ନିକଟରେ ଅମ୍ଲାନଙ୍କ କାର୍‌ ଭାରସାମ୍ୟ ହରାଇ ତଳକୁ ଖସିପଡ଼ିଥିଲା। ତାଙ୍କ ସ୍ୱାସ୍ଥ୍ୟାବସ୍ଥା ଭଲ ଥିବା ତାଙ୍କ ବାପା ମିହିର ଦାସ କହିଛନ୍ତି।','2016_10_25_23_06_04_Amlan.jpg','Newstory','27-01-2017','','',''),(11,'୫ଜି ମୋଡେମ୍‌: ସେକେଣ୍ଡରେ ୫ଟି ଫିଲ୍ମ ହେବ ଡାଉନ୍‌ଲୋ',NULL,'ନୂଆଦିଲ୍ଲୀ: ହାର୍ଡେଓ୍ଵୟାର କମ୍ପାନି କ୍ୱାଲକମ୍‌ ପୋର୍ଟେବଲ ୫ଜି ମୋଡେମ୍‌ ନେଇ ଟେଲିକମ୍‌ ସେକ୍ଟରରେ ପ୍ରବେଶ କରିବ। ଏହି ମୋଡେମ୍‌ ୨୦୧୭ରେ ଲଞ୍ଚ ହେବ। ଏହି ମୋଡମ୍‌କୁ ଏକ୍ସ ୫ଜି ଭାବେ ନାମିତ କରାଯାଇଛି। ସୂଚନା ଅନୁଯାୟୀ, ଏହା ହେବ ସବୁଠାରୁ ବେଗବାନ୍‌ ଇଣ୍ଟରନେଟ୍‌ ମୋଡେମ। ଏଥିରେ ୨୮GHzର ମିଲିମିଟର େଓ୍ଵବ ସ୍ପେକ୍‌ଟ୍ରୋମ ବ୍ୟବହାର କରାଯିବ। ଏହାପୂର୍ବରୁ କୌଣସି ସେଲ୍ୟୁଲାର ନେଟଓ୍ଵାର୍କରେ ଏହା ବ୍ୟବହାର କରାଯାଇ ନଥିଲା।\r\n \r\nକ୍ୱାଲକମ୍‌ ଟେକ୍‌ନୋଲୋଜି ମାର୍କେଟିଂ ମ୍ୟାନେଜର ସରିଫ ହିନ୍ନାଙ୍କ କହିବା ଅନୁସାରେ ଏକ୍ସ ୫ଜିକୁ ହେଡହେଲଡ୍‌ ମୋବାଇଲ ଡିଭାଇସ ସହିତ ହୋମ ବ୍ରୋଡବ୍ରାଣ୍ଡ ପାଇଁ ଡିଜାଇନ୍‌ କରାଯାଇଛି। ଏହି ମୋଡେମରେ ଏକ ସେକେଣ୍ଡରେ ୫ଜିବି ଡାଟା ଡାଉନଲୋଡ କରାଯାଇ ପାରିବ। ଅର୍ଥାତ୍‌ ୧ସେକେଣ୍ଡରେ ୫ରୁ ଅଧିକ ଫିଲ୍ମ ଡାଏନଲୋଡ କରିହେବ।\r\n \r\nଉଲ୍ଲେଖଯୋଗ୍ୟ, ସେପ୍ଟେମ୍ବର ୩ରେ ଟେଲିକମ୍‌ ମାର୍କେଟରେ ଲଞ୍ଚ୍‌ ହୋଇଥିବା ରିଲାଏନ୍ସ ୪ଜି ଦ୍ୱାରା ଏକ ସେକେଣ୍ଡରେ ୨୫ଏମ୍‌ବି ଡାଟା ଡାଉନ୍‌ଲୋଡ କରାଯାଇ ପାରୁଛି। ଏଥିରୁ ତୁଳନା କରାଯାଇ ପାରେ ୫ଜି କେତେ ଦ୍ରୁତଗତିରେ କାମ କରି ପାରିବ। ୨୦୧୭ରେ ଏହି ମୋଡେମ ଆସିବା ପରେ କମ୍ପାନି ଏହାକୁ ଭାରତ ତଥା ଭାରତ ବାହାରେ  ଲଞ୍ଚ୍‌ କରିବାର ଯୋଜନା ରଖିଛି।\r\n \r\nଅନ୍ୟପକ୍ଷରେ ସରିଫ ହିନ୍ନା କହିଛନ୍ତି ଏହି ଟେକନିକ୍‌କୁ ବର୍ତ୍ତମାନ କୌଣସି ଫୋନ୍‌ ସୋପୋର୍ଟ କରୁନାହିଁ। ଆଶା ଅଛି ୨୦୧୭ରେ ଏମିତି କିଛି ଫୋନ୍‌ ଆସିଥିବ ଯାହା ଗିଗାବାଇଟ୍‌ କାପାବିଲିଟିକୁ ସୋପୋର୍ଟ କରି ପାରୁଥିବ। ବର୍ତ୍ତମାନ କେବଳ ୧୬ପ୍ରତିଶତ ଫୋନ୍‌ ଅପରେଟର ଗିଗାବାଇଟ ଏଲଟିଇକୁ ସୋପାର୍ଟ କରୁଛି।','2016_10_25_23_07_32_5G.jpg','Newstory','27-01-2017','','',''),(12,'ଯୁବକଙ୍କ ଗୁପ୍ତାଙ୍ଗ କାଟିଲେ ଦୁର୍ବୃତ୍ତ',NULL,'ସୋର : ସୋର ଥାନାର ଦହିସଡା ଗାଁର ଏକ ପୋଖରୀରୁ ଆଜି ସକାଳେ ଜଣେ ଯୁବକଙ୍କୁ ଉଦ୍ଧାର କରାଯାଇଛି। ଉଦ୍ଧାର ଯୁବକଙ୍କ ଗୁପ୍ତାଙ୍ଗ କଟାଯାଇଛି ଓ ସେ ଗୁରୁତର ଅଛନ୍ତି। ରକ୍ତାକ୍ତ ଅବସ୍ଥାରେ ସ୍ଥାନୀୟ ଲୋକେ ତାଙ୍କୁ ଉଦ୍ଧାର କରି ୧୦୮ ଆମ୍ବୁଲାନ୍ସ ଯୋଗେ ସୋର ଡାକ୍ତରଖାନାରେ ଭର୍ତ୍ତି କରିଛନ୍ତି। ଯୁବକ ଜଣକ ସ୍ଥାନୀୟ ମାଣତ୍ରୀ ପଞ୍ଚାୟତ ବଗଦା ଗ୍ରାମର ମୁକୁନ୍ଦ ମଲ୍ଲିକଙ୍କ ପୁଅ କାଳିଆ(୩୫) ବୋଲି ଜଣାପଡ଼ିଛି। କାଳିଆଙ୍କୁ ଉଦ୍ଧାର କରାଯିବା ପରେ ତାଙ୍କ ଗୁପ୍ତାଙ୍ଗ କଟାଯାଇଥିବା ଜଣା ପଡିଥିଲା। କେଉଁ ପରିସ୍ଥିତିରେ ଗୁପ୍ତାଙ୍ଗ କଟିଛି ସେ ସମ୍ବନ୍ଧରେ କାଳିଆ ଠିକ୍‌ ଭାବେ କିଛି କହୁ ନାହାନ୍ତି। କେତେବେଳେ ସେ ନିଜେ କାଟିଥିବା କହୁଛନ୍ତି ତ କେତେବେଳେ କିଛି ଦୁର୍ବୃତ୍ତ ଏଭଳି କାଣ୍ଡ କରି ତାଙ୍କୁ ପାଣିରେ ପକାଇ ଦେଇଥିବା କହୁଛନ୍ତି। ଏ ନେଇ ସାଧାରଣରେ ଭିନ୍ନ ଭିନ୍ନ କଥା ଶୁଣିବାକୁ ମିଳୁଛି। ଘଟଣା ପ୍ରଚାରିତ ହେବା ପରେ ସୋର ଅଂଚଳରେ ବେଶ୍‌ ଚର୍ଚ୍ଚା ଜମିଛି। ଖବର ପାଇ କାଳିଆଙ୍କ ପରିବାର ଲୋକେ ଡାକ୍ତରଖାନାରେ ପହଞ୍ଚିଛନ୍ତି। ଏଣେ ସୋର ସ୍ୱାସ୍ଥ୍ୟକେନ୍ଦ୍ରର ଡା. ଏସ୍‌ଏନ୍‌ ନାୟକ ଓ ଡା. ଡିକେ ଷଡ଼ଙ୍ଗୀ ଅକ୍ଳାନ୍ତ ଉଦ୍ୟମ କରି ଯୁବକଙ୍କ ଗୁପ୍ତାଙ୍ଗ ଯୋଡ଼ିବାରେ ସଫଳ ହୋଇଛନ୍ତି। ସୋର ପୁଲିସ ଡାକ୍ତରଖାନାରେ ପହଞ୍ଚି ଘଟଣାର ତଦନ୍ତ କରୁଛି।','2016_10_25_23_09_25_joni.jpg','Newstory','27-01-2017','','',''),(13,'ପିଲାଙ୍କୁ ଓଜନିଆ ବ୍ୟାଗ୍‌ ମନା',NULL,'ଭୁବନେଶ୍ୱର : ସେଣ୍ଟ୍ରାଲ ବୋର୍ଡ ଅଫ୍‌ ସେକେଣ୍ଡାରି ଏଜୁକେସନ (ସିବିଏସ୍‌ଇ) ଅଧୀନରେ ଥିବା ବିଦ୍ୟାଳୟର ଛାତ୍ରଛାତ୍ରୀଙ୍କୁ ଅଧିକ ଓଜନର ବ୍ୟାଗ୍‌ ମନା ହେବାର ଦୁଇ ମାସ ପରେ ବିଦ୍ୟାଳୟ ଓ ଗଣଶିକ୍ଷା ବିଭାଗ ନିଦ ଭାଙ୍ଗିଛି। ରାଜ୍ୟ ସରକାରଙ୍କ ଅଧୀନରେ ଥିବା ବିଦ୍ୟାଳୟଗୁଡ଼ିକରେ ପଢ଼ୁଥିବା ଛାତ୍ରଛାତ୍ରୀ କାନ୍ଧରେ ଯେପରି ଓଜନିଆ ବ୍ୟାଗ୍‌ ପକାଇବେ ନାହିଁ ସେନେଇ ଗାଇଡ୍‌ଲାଇନ୍‌ରେ ଜାରି ହୋଇଛି। ବିଦ୍ୟାଳୟ ପ୍ରଧାନଶିକ୍ଷକ, ଶିକ୍ଷକ ଏବଂ ଅଭିଭାବକ ଏଦିଗରେ ପଦକ୍ଷେପ ନେବାକୁ କୁହାଯାଇଛି। ଯଦି କୌଣସି ବିଦ୍ୟାଳୟ ନିୟମକୁ ଉଲ୍ଲଂଘନ କରନ୍ତି ତାଙ୍କ ବିରୋଧରେ ଦୃଢ଼ କାର୍ଯ୍ୟାନୁଷ୍ଠାନ ଗ୍ରହଣ କରାଯିବ ବୋଲି ସ୍ପଷ୍ଟ ଭାବେ କୁହାଯାଇଛି।\r\n \r\nପ୍ରାରମ୍ଭିକ ପର୍ଯ୍ୟାୟରେ ଓପେକା ଏବଂ ମାଧ୍ୟମିକ ଶିକ୍ଷା ନିର୍ଦେଶକଙ୍କୁ ଗାଇଡ୍‌ଲାଇନ୍‌କୁ କଡ଼ାକଡ଼ି କରିବାକୁ ନିର୍ଦେଶ ଦିଆଯାଇଛି। ପ୍ରଥମରୁ ୧୦ମ ଶ୍ରେଣୀ ପର୍ଯ୍ୟନ୍ତ ଛାତ୍ରଛାତ୍ରୀଙ୍କ ବ୍ୟାଗ୍‌ ଓଜନ କମାଇବା ଦିଗରେ ପଦକ୍ଷେପ ନିଆଯିବ। ଜଣେ ପିଲାର ଓଜନ ଯେତିକି ହୋଇଥିବ ତାଙ୍କର ସ୍କୁଲ ବ୍ୟାଗ୍‌ ଓଜନ ତାହାର ୧୦ ପ୍ରତିଶତରୁ ଅଧିକ ହେବ ନାହିଁ। ପାଣି ବୋତଲ ବ୍ୟାଗ୍‌ରେ ଭର୍ତ୍ତି କରାଯିବ ନାହିଁ। ପ୍ରଥମରୁ ୩ୟ ଶ୍ରେଣୀ ମଧ୍ୟରେ ପଢ଼ୁଥିବା ପିଲାମାନେ ସ୍କୁଲ ବ୍ୟାଗ୍‌ରେ କୌଣସି ପ୍ରକାର ବହି କିମ୍ବା ଖାତା ନେବେ ନାହିଁ। ଏ ଦିଗରେ ବିଦ୍ୟାଳୟ ପ୍ରଧାନଶିକ୍ଷକ/ପ୍ରଧାନଶିକ୍ଷୟିତ୍ରୀ, ଶିକ୍ଷକ ଏବଂ ଅଭିଭାବକମାନେ ଗୁରୁତ୍ୱ ଦେବା ଆବଶ୍ୟକ। ବିଦ୍ୟାଳୟକୁ ପୁସ୍ତକ ନେବା ଲାଗି ଶିକ୍ଷକମାନେ ପିଲାଙ୍କୁ ବାଧ୍ୟ କରିବେ ନାହିଁ। ଯଦି କୌଣସି ଶିକ୍ଷକ ବାଧ୍ୟ କରନ୍ତି ପ୍ରଧାନଶିକ୍ଷକ ସମ୍ପୃକ୍ତ ଶିକ୍ଷକଙ୍କ ବିରୋଧରେ କାର୍ଯ୍ୟନୁଷ୍ଠାନ ନେଇପାରିବେ। ଅଭିଭାବକ ମଧ୍ୟ ସଚେତନ ହେବା ଆବଶ୍ୟକ। ପିଲାଙ୍କୁ ବିଦ୍ୟାଳୟ ପଠାଇବା ପୂର୍ବରୁ ସେମାନଙ୍କ ବ୍ୟାଗ୍‌କୁ ଠିକ୍‌ ଭାବେ ଯାଞ୍ଚ କରି ଛାଡ଼ିବା ଦରକାର। \r\n \r\nପିଲାମାନଙ୍କୁ ସଚେତନ କରିବା ଦିଗରେ ବିଦ୍ୟାଳୟ କର୍ତୃପକ୍ଷ ଗୁରୁତ୍ୱ ଦେବେ। ପର୍ଯ୍ୟାୟକ୍ରମେ ଏନେଇ ବିଦ୍ୟାଳୟରେ ସଚେତନତା ଏବଂ ତାଲିମ କାର୍ଯ୍ୟକ୍ରମ କରିବାକୁ କୁହାଯାଇଛି। ଏଥିରେ ବିଦ୍ୟାଳୟର ସମସ୍ତ ଛାତ୍ରଛାତ୍ରୀ ଏବଂ ଶିକ୍ଷକଙ୍କୁ ସାମିଲ କରାଯିବ। ବିଦ୍ୟାଳୟ କର୍ତୃପକ୍ଷ ମଧ୍ୟ ନିଜସ୍ତରେ ଏକ ଗାଇଡ୍‌ଲାଇନ୍‌ ଜାରି କରିବେ। ତାହାକୁ ବିଦ୍ୟାଳୟ ନୋଟିସ୍‌ ବୋର୍ଡରେ ଲଗାଇବାକୁ କୁହାଯାଇଛି। ପ୍ରତିଦିନ ଯେଉଁ ବିଷୟର ପିରିୟଡ୍‌ ରହିଥିବ ଛାତ୍ରଛାତ୍ରୀ କେବଳ ସେହି ବିଷୟର ପୁସ୍ତକ ଆଣିବେ। ପିଲାଙ୍କୁ ଯେପରି ସୁବିଧା ଏବଂ ସହଜ ହେବ ସେମିତି ବ୍ୟାଗ୍‌ କାନ୍ଧରେ ପକାଇ ବିଦ୍ୟାଳୟକୁ ଆସିବାକୁ ଗାଇଡ୍‌ଲାନ୍‌ରେ କୁହାଯାଇଛି। \r\n \r\nକେବଳ ଛାତ୍ରଛାତ୍ରୀ ନୁହଁନ୍ତି, ଶିକ୍ଷକ/ଶିକ୍ଷୟିତ୍ରୀମାନେ ମଧ୍ୟ ଏଦିଗରେ ଗୁରୁତ୍ୱପୂର୍ଣ୍ଣ ଭୂମିକା ଗ୍ରହଣ କରିବେ। ଛାତ୍ରଛାତ୍ରୀ କେଉଁ ସବୁ ପୁସ୍ତକ ଏବଂ ଖାତା ଆଣି ବିଦ୍ୟାଳୟ ଆସିବେ ଶିକ୍ଷକମାନେ ପୂର୍ବଦିନ ଏ ବିଷୟରେ ପିଲାଙ୍କୁ କହିବେ। ଗାଇଡ୍‌ଲାଇନ୍‌କୁ ଉଲ୍ଲଂଘନ କରୁ','2016_10_25_23_21_26_pila.jpg','Newstory','27-01-2017','','',''),(14,'ଭଗବତୀ ଚରଣଙ୍କ ପରି ବିପ୍ଳବୀ ନେତା ବିରଳ',NULL,'ଭୁବନେଶ୍ୱର : ଓଡ଼ିଆ ସାହିତ୍ୟ ଜଗତର ଅନ୍ୟତମ ପ୍ରମୁଖ ବିନ୍ଧାଣୀ ତଥା କମ୍ୟୁନିଷ୍ଟ ନେତା ଭଗବତୀ ଚରଣ ପାଣିଗ୍ରାହୀଙ୍କ ୭୪ତମ ଶ୍ରାଦ୍ଧ ବାର୍ଷିକୀ ପାଳିତ ହୋଇଯାଇଛି। ଭଗବତୀ ପାଣିଗ୍ରାହୀ ପ୍ରତିଷ୍ଠାନ ପକ୍ଷରୁ ଇଡ୍‌କଲ ଅଡିଟୋରିୟମରେ ଆୟୋଜିତ ଏହି କାର୍ଯ୍ୟକ୍ରମରେ ଭଗବତୀ ଚରଣଙ୍କ ଜୀବନୀକୁ ନେଇ ଆଶିଷ ମହାପାତ୍ରଙ୍କ ରଚିତ ‘ଯୁଗସ୍ରଷ୍ଟା ବିପ୍ଳବୀ ଭଗବତୀ ଚରଣ’ ପୁସ୍ତକକୁ ଅତିଥିମାନେ  ଉନ୍ମୋଚିତ କରିଥିଲେ। ଏଥିରେ ବିଶିଷ୍ଟ ସାହିତ୍ୟିକ ମନୋଜ ଦାସ ମୁଖ୍ୟ ଅତିଥିଭାବେ ଯୋଗଦେଇ କହିଲେ, ପରିବର୍ତନର ପକ୍ରିୟା କେବେହେଲେ ଦିଗହରା ହୋଇନାହିଁ। କେବଳ ସାମୂହିକ ପ୍ରଚେଷ୍ଟା ଦରକାର। ଯେଉଁ ପ୍ରଚେଷ୍ଟା ପାଇଁ ଭଗବତୀ ଚରଣ ନିଜ ଜୀବନକୁ ସମର୍ପିତ କରିଥିଲେ। ତାହା ଏହି ପୁସ୍ତକରେ ଉପସ୍ଥାପିତ ହୋଇଛି। ଏହି ପୁସ୍ତକରେ ଓଡ଼ିଶା ଇତିହାସ ସମ୍ପର୍କରେ ଅନେକ ତଥ୍ୟ ରହିଛି।\r\n \r\nସେହିପରି ଔପନ୍ୟାସିକ ବିଭୂତି ପଟ୍ଟନାୟକ ସମ୍ମାନିତ ଅତିଥିଭାବେ ଯୋଗଦେଇ କହିଲେ, ଭଗବତୀ ଚରଣଙ୍କ ପରି ସାମ୍ୟବାଦୀ ଓ ବିପ୍ଳବୀ ନେତା ବିରଳ। ସାହିତ୍ୟ ଓ ସଂସ୍କୃତି କ୍ଷେତ୍ରରେ ସମାଜବାଦ ଅନ୍ଦୋଳନକୁ ଆଗେଇ ନେବାରେ ତାଙ୍କର ଭୂମିକା  ଅତୁଳନୀୟ। ତାଙ୍କ ବିଷୟରେ ଅନେକ ଅଜଣା କଥା ଏହି ପୁସ୍ତକରେ ସ୍ଥାନିତ ହୋଇଛି। \r\n \r\nଏହି କାର୍ଯ୍ୟକ୍ରମରେ ବିଧାୟକ ଶଶିଭୂଷଣ ବେହେରା, ସାହିତ୍ୟିକ ତଥା ଗବେଷକ ବିଜୟ କୁମାର ଶତପଥୀ ଅନ୍ୟତମ ସମ୍ମାନିତ ଅତିଥିଭାବେ ଯୋଗଦେଇ ଭଗବତୀ ପାଣିଗ୍ରାହୀଙ୍କ ଜୀବନୀ ସଂପର୍କରେ ଆଲୋଚନା କରିଥିଲେ। ଜଗନ୍ନାଥ ପଟ୍ଟନାୟକଙ୍କ ସଭାପତିତ୍ୱରେ ଆୟୋଜିତ ଏହି କାର୍ଯ୍ୟକ୍ରମରେ ପ୍ରତିଷ୍ଠାନର ସମ୍ପାଦକ ପ୍ରଦୋଷ ପଟ୍ଟନାୟକ ସ୍ୱାଗତଭାଷଣ ଦେଇଥିିବା ବେଳେ ପ୍ରଣତି ରାଉତ ଧନ୍ୟବାଦ ଦେଇଥିଲେ।','2016_10_25_23_26_03_Bhagabati.jpg','Newstory','27-01-2017','','',''),(16,'ଜାଭିୟର ସ୍କୁଲ ଅଭିଭାବକ ସଭାରେ ତୁମୁଳ କାଣ୍ଡ',NULL,'ଭୁବନେଶ୍ୱର:ସତ୍ୟନଗର ଜାଭିୟର ସ୍କୁଲ ଅଭିଭାବକ ସଭାରେ ତୁମୁଳ କାଣ୍ଡକୁ ନେଇ ଚର୍ଚ୍ଚା ଜୋର ଧରିଛି । ଜଣାପଡିଛି ଯେ,ଗତ ରବିବାର ଦିନ ସ୍ଥାନୀୟ ରୋଟାରୀ କ୍ଲବ ପରିସରରେ ଆହୁତ ଏହି ବୈଠକରେ କେତେକ ମୁଷ୍ଟିମେୟ ବ୍ୟକ୍ତି ସ୍କୁଲ କର୍ତ୍ତୃପକ୍ଷଙ୍କ ହାତ ବାରିସୀ ସାଜି ଅଭିଭାବକମାନଙ୍କ ହିତକୁ ଅଣଦେଖା କରିବାରେ ଲାଗିଛନ୍ତି । ଏହି ବ୍ୟକ୍ତିଗଣ ଏକ ସମାନ୍ତରାଳ ସଂଗଠନ ସୃଷ୍ଟି କରିବା ସହ ସ୍କୁଲ କର୍ତ୍ତୃପକ୍ଷଙ୍କ ନିକଟରେ ବସଉଠ ହେଉଥିବାରୁ ଛାତ୍ରଛାତ୍ରୀ ତଥା ଅଭିଭାବକ ମାନଙ୍କ ସ୍ୱାର୍ଥ ରକ୍ଷାରେ ପ୍ରତିବନ୍ଧକ ସୃଷ୍ଟି କରୁଛି । ଆଇନ ସଙ୍ଗତ ଭାବେ ଗଠିତ ସଙ୍ଘ ଏହି ବୈଠକକୁ ବିରୋଧ କରିବା ସହ ବୈଠକ ସମ୍ପର୍କରେ ପ୍ରଶ୍ନ ଉଠାଇଥିଲା । ସ୍କୁଲ କର୍ତ୍ତୃପକ୍ଷଙ୍କ ମନମୁଖୀ କାର୍ଯ୍ୟକୁ ବିରୋଧକରି ପୁର୍ବରୁ କୋର୍ଟରେ ମାମଲା ଚାଲିଥିବା ବେଳେ ଏଭଳି ବୈଠକ ସମ୍ପର୍କରେ ବିଭିନ୍ନ ପ୍ରଶ୍ନ ଉଠିଥିଲା । ତେବେ ସନ୍ତୋଷ ଜନକ ଉତ୍ତର ନପାଇ ଅଭିଭାବକଗଣ ପୋଲିସ ପ୍ରଶାସନର ଦ୍ୱାରସ୍ତ ହୋଇଥଲେ । ଏହାପରେ ତରବରିଆ ଭାବେ ସଭାକୁ ବନ୍ଦ କରିଦିଆ ଯାଇଥିଲା । ଏହି ସମୟରେ କୌଣସି ଅପ୍ରିତିକର ପରିସ୍ଥିତିକୁ ଏଡାଇବା ପାଇଁ ଖାରବେଳ ନଗର ପୋଲିସ ଘଟଣା ସ୍ଥଳରେ ପହଞ୍ଚିଥିଲା । ଉପସ୍ଥିତ ଅଭିଭାବକଗଣ ଓ ସଭାପତି ସ୍କୁଲରେ ଚାଲୁଥିବା ଅନିୟମିତତା ସମ୍ପର୍କରେ ପୋଲିସକୁ ଅବଗତ କରାଇବା ସହ କାର୍ଯ୍ୟାନୁଷ୍ଠାନ ଗ୍ରହଣ କରିବାକୁ ନିବେଦନ କରିଥିଲେ ।','2016_10_26_18_36_27_IMG-20161025-WA0020.jpg','Newstory','27-01-2017','','',''),(17,'ହେମାମାଲିନୀଙ୍କୁ ମୁଁ  ଭଲ ପାଏ : ଲାଲୁ',NULL,'ପାଟନା:ହେମାମାଲିନୀ ଜାଣି ନଥିବେ ମୁଁ ତାଙ୍କୁ କେତେ ଭଲ ପାଏ । କେବଳ ଭଲ ପାଏ ନାହିଁ; ତାଙ୍କରି ନାମଅନୁସାରେ ମୁଁ ମୋ ଝିଅର ନାମକାରଣବି କରିଛି।’  ବିହାର ରାଜଧାନୀ ପାଟନାରେ ମଙ୍ଗଳବାର ସନ୍ଧ୍ୟାରେ ଅଭିନେତ୍ରୀ ହେମାମାଲିନୀଙ୍କ ଏକ ନୃତ୍ୟ କାର୍ଯ୍ୟକ୍ରମ ଆୟୋଜିତ ହୋଇଥିଲା। ଏହି କାର୍ଯ୍ୟକ୍ରମ ଦେଖିବାକୁ ଆର୍‌ଜେଡି ସୁପ୍ରିମୋ ଲାଲୁପ୍ରସାଦ ଯାଦବ ଯାଇ ଥିଲେ। ସେଠାରେ ସେଠାରେ ହେମାମାଳିନୀଙ୍କ ନୃତ୍ୟ ଦେଖି ଏତକ କହୁ କହୁ କହିଦେଲେ ଲାଲୁ ।\r\n‘ହେମାମାଲିନୀଙ୍କ ପ୍ରଶଂସା ଓ ସମ୍ମାନ ଦେଶ ଓ ଦେଶ ବାହାରରେ ରହିଛି। ପ୍ରାୟତଃ ଶୁଣିବାକୁ ମିଳେ କି କେହି ବିହାର ଆସିବାକୁ ପସନ୍ଦ କରନ୍ତି ନାହିଁ, କାରଣ ଲୋକମାନଙ୍କ ଭାବନା ଏଠାରେ ବହୁ ଗୁଣ୍ଡା, ଡକାୟତ ବାସ କରନ୍ତି। କିନ୍ତୁ, ହେମା ପାଟନା ଆସି ଏସବୁ ଭାବନାକୁ ଭଙ୍ଗ କରି ଦେଇଛନ୍ତି। ଏମିତିରେବି ତ ସବୁସ୍ଥାନରେ ଗୁଣ୍ଡା, ଡକାୟତମାନେ ଅଛନ୍ତି,’ ବୋଲି କହିଥିଲେ ଲାଲୁପ୍ରସାଦ।\r\n ହେମାମାଲିନୀଙ୍କୁ ପ୍ରଶଂସା କରିବା ଲାଲୁଙ୍କର ଏହା ପ୍ରଥମ ନୁହେଁ; ସେ ମୁଖ୍ୟମନ୍ତ୍ରୀ ଥିବା ସମୟରେ ମଧ୍ୟ ହେମାଙ୍କ ପ୍ରଶଂସା କରି କହିଥିଲେ, ‘ବିହାରର ସଡ଼କକୁ ମୁଁ ହେମାଙ୍କ ଗାଲ ଭଳି ଚିକ୍କଣ କରିଦେବି।’','2016_10_26_19_43_45_lalu.jpg','Newstory','27-01-2017','','',''),(18,'ନଭେମ୍ବରରୁ  କୃଷକ ଜାଗରଣ ସମାବେଶ',NULL,'ଭୁବନେଶ୍ୱର :ଉଭୟ କେନ୍ଦ୍ର ଓ ରାଜ୍ୟ ସରକାରଙ୍କ ଚାଷୀ ମାରଣ ନୀତିର ପ୍ରତିବାଦ କରି ପ୍ରଦେଶ କଂଗ୍ରେସ କୃଷକ ସେଲ୍‌ ସମସ୍ତ ଜିଲ୍ଲାରେ କୃଷକ ଜାଗରଣ ସମାବେଶ ଅୟୋଜନ କରିବ। ପଶ୍ଚିମ ଓଡ଼ିଶାର ସମସ୍ତ ଜିଲ୍ଲା କ୍ଷେତ ମଜଦୁର କଂଗ୍ରେସ କର୍ମକର୍ତ୍ତାମାନଙ୍କ ଗୁରୁତ୍ୱପୂର୍ଣ୍ଣ ବୈଠକରେ ସେଲ୍‌ର ଅଧ୍ୟକ୍ଷ ଅମୀୟ କୁମାର ପଟ୍ଟନାୟକ ଏହି ସୂଚନା ଦେଇଛନ୍ତି। ଚାଷୀମାନଙ୍କ ହକ୍‌ ପୂରଣ ନିମନ୍ତେ ୨୧ ଦଫା ଦାବି ନେଇ ନଭେମ୍ବର ପ୍ରଥମ ସପ୍ତାହରୁ ଏହି କାର୍ଯ୍ୟକ୍ରମ ଆରମ୍ଭ ହେବ ବୋଲି ଶ୍ରୀ ପଟ୍ଟନାୟକ କହିଛନ୍ତି।\r\n \r\nଧାନର ସହାୟକ ମୂଲ୍ୟ ୨୫୦୦ ଟଙ୍କାକୁ ବୃଦ୍ଧି, ଧାନର ପ୍ରତି କୁଇଣ୍ଟାଲ୍‌ରେ ରାଜ୍ୟ ସରକାରଙ୍କ ପକ୍ଷରୁ ୫୦୦ ଟଙ୍କା ଅତିରିକ୍ତ ସହାୟତା, ଗତବର୍ଷର ଫସଲବୀମା ପ୍ରଦାନ, ଚାଷ ପାଇଁ ମାଗଣାରେ ବିଜୁଳି, ବିଭିନ୍ନ ଜଳସେଚନ ଯୋଜନାରେ ଶତ ପ୍ରତିଶତ ଜଳସେଚନ, ଅଧିକ ଅମଳକ୍ଷମ ଫସଲଗୁଡ଼ିକକୁ ବୀମାଭୁକ୍ତ ଏବଂ ପ୍ରତି ରାଜସ୍ୱ ଗ୍ରାମକୁ ଏକ ଫସଲ ବୀମା ୟୁନିଟ୍‌ ଭାବେ ଗ୍ରହଣ କରିବା, ୫୦ ବର୍ଷରୁ ଊର୍ଧ୍ୱ ସମସ୍ତ ଚାଷୀଙ୍କୁ ମାସିକ ସର୍ବନିମ୍ନ ୩୦୦୦ ଟଙ୍କା ପେନ୍‌ସନ୍‌ ପ୍ରଦାନ ଆଦି ଦାବିରେ ଏହି ଆନ୍ଦୋଳନ କରାଯିବ ବୋଲି ଶ୍ରୀ ପଟ୍ଟନାୟକ ସୂଚନା ଦେଇଛନ୍ତି। ବୈଠକରେ ଜଗନ୍ନାଥ ଜେନା, ସନ୍ତୋଷ ସାହୁ, ଅଶୋକ ପୁରୋହିତ, ଚୈତରାମ ନାଏକ, ଧରମସିଂହ ବଡ଼ପଣ୍ଡା, ସଚିଦାନନ୍ଦ ମହାକୁଡ଼, ସାଧାରଣ ସଂପାଦକ ରାଜେନ୍ଦ୍ର ପ୍ରସାଦ ମିଶ୍ର, ସୁବୋଧ ବେହେରା, ଗୋବିନ୍ଦ ପଟେଲ ପ୍ରମୁଖ କଂଗ୍ରେସ କୃଷକ ସେଲ୍‌ ରାଜ୍ୟ ଓ ଜିଲ୍ଲା କମିଟିର କର୍ମକର୍ତ୍ତାବୃନ୍ଦ ଏଥିରେ ଭାଗ ନେଇ ବିଜେଡି ସରକାରର ଚାଷୀମାରଣ ନୀତି ବିରୋଧରେ ଦୃଢ଼ ସଂଗ୍ରାମ କରାଯିବ ବୋଲି ଚେତାବନୀ ଦେଇଥିଲେ।','2016_10_26_19_50_12_amiya.jpg','Newstory','27-01-2017','','',''),(19,'ଗୋପନ ପ୍ରେମ ପଦାରେ ପକାଇଲା ଶୁଆ',NULL,'କୁଏତ୍‌ରେ ଗୋଟାଏ ଶୁଆ ହୋଇଛି ଦାମ୍ପତ୍ୟ କଳହର କାରଣ।  ଚାକରାଣୀ ସହ ଖାଉନ୍ଦଙ୍କ ଗୋପନ ପ୍ରେମ କଥା ତାଙ୍କ ପତ୍ନୀଙ୍କୁ କହି ବଡ଼ ଅଡୁଆ ପରିସ୍ଥିତି କରିପକାଇଛି। ଖାଉନ୍ଦ ଚାକରାଣୀକୁ କହିଥିବା ଟୁପୁରୁ ଟାପୁର କଥା ସବୁକୁ ଶୁଆଟି ରଟିବାରୁ ଖାଉନ୍ଦଙ୍କ ସ୍ତ୍ରୀର ସନ୍ଦେହ ହେଲା। କାରଣ, ଦୀର୍ଘ ଦିନ ହେବ ସେ ଏ ପରି ରସରସିଆ କଥା କାହାଠାରୁ ଶୁଣି ନ ଥିଲେ। ଅନେକ ସମୟ ଭାବିଲା ପରେ- ଏ ପ୍ରେମାଳାପ ତାଙ୍କ ସ୍ୱାମୀଙ୍କର ବୋଲି ସେ ଜାଣିପାରିଲେ। କିନ୍ତୁ, କାହାକୁ ସେ ଏ ସବୁ କହୁଛନ୍ତି? ଶେଷରେ ଚାକରାଣୀ ଉପରେ ତାଙ୍କର ସନ୍ଦେହ ହେଲା। \r\n \r\nଦିନେ ସ୍ୱାମୀ ଓ ଚାକରାଣୀ ଘରେ ଏକାନ୍ତରେ ଥିବାବେଳେ ତାଙ୍କ କଥାବାର୍ତ୍ତାକୁ ଶୁଆ ନିଜ ଅଭ୍ୟାସ ମୁତାବକ ରଟିହେଲା। ଅଗତ୍ୟା ସ୍ତ୍ରୀ ନିଜ ଯୋଜନା ମୁତାବକ ଅବେଳାରେ ନିଜ କାମରୁ ଚାଲିଆସି ଘରେ ପହଞ୍ଚିଲାରୁ ଉଭୟ ଏକାନ୍ତରେ ଘରେ ଥିବାର ଜଣାପଡ଼ିଲା।\r\n \r\nକୁଏତ୍‌ରେ ପରକୀୟା ପ୍ରେମ ବେଆଇନ୍‌। ଶୁଆକୁ ସାକ୍ଷୀରୂପେ ଧରି ନେଇ ସ୍ତ୍ରୀ ଯାଇ ହାଜର ହେଲେ ଥାନାରେ। ଖାଉନ୍ଦଙ୍କ ଭାଗ୍ୟ ଭଲ, ପୁଲିସବାଲା ଏତଲା ନେଲେ ନାହିଁ। ଓଲଟି ସେମାନେ କହିଲେ କି, ଏ ମଧୁର ଆଳାପ ଶୁଆଟି ଟେଲିଭିଜନ୍‌ କିଂବା ରେଡ଼ିଓରୁ ଶୁଣି ମନେରଖିଥାଇ ପାରେ! \r\n \r\nପରେ ଅବଶ୍ୟ ସ୍ତ୍ରୀଲୋକଟି ସ୍ୱାମୀଙ୍କ ପ୍ରତି ନିଜ ସନ୍ଦେହର କଥା ବଖାଣିଲେ, କିନ୍ତୁ, ଥାନାବାବୁ ସେ କଥା ଶୁଣିଲେ ନାହିଁ।','2016_10_26_19_57_39_kut.jpg','Newstory','27-01-2017','','',''),(20,'୩୦ଜଣ ଆଫଗାନଙ୍କୁ ହତ୍ୟା କଲା ଆଇଏସ',NULL,'ହେରାତ: ଆଫଗାନିସ୍ତାନର ଘୋର ଅଞ୍ଚଳରେ ତିରିଶ ଜଣ ନିରିହ ଜନତାଙ୍କୁ ଆଇଏସ ଆତଙ୍କବାଦୀମାନେ ହତ୍ୟା କରିଥିବା ସନ୍ଦେହ କରାଯାଉଛି। ଜଣେ ଆଇଏସ କମାଣ୍ଡର ମୃତ୍ୟୁର ପ୍ରତିଶୋଧ ନେବା ପାଇଁ ଆଇଏସ ଏଭଳି କାଣ୍ଡ ଘଟାଇଥିବା ଆଜି ଆଫଗାନିସ୍ତାନ ପ୍ରଦେଶର ଜଣେ ଅଧିକାରୀ ସୂଚନା ଦେଇଛନ୍ତି। \r\n \r\nସୂଚନାଯୋଗ୍ୟ ମଙ୍ଗଳବାର ଘୋର ପ୍ରଦେଶରେ ଏକ ଅପରେସନ ଚାଲିଥିବା ସମୟରେ ପୁଲିସ ଜଣେ ଦୈଶ କମାଣ୍ଡରକୁ ହତ୍ୟା କରିଥିଲା। ଏହାର ଜବାବରେ ଦୈଶ ବିକ୍ଷୋଭକାରୀମାନେ ପ୍ରଦେଶର ରାଜଧାନୀ ନିକଟରୁ ତିରିଶ ଜଣ ସାଧାରଣ ଜନତାଙ୍କୁ ଅପହରଣ କରିବା ସହ ସମସ୍ତଙ୍କୁ ଗୁଳି କରି ହତ୍ୟା କରିଥିବା ବିଷୟ ରାଜ୍ୟପାଳଙ୍କ ମୁଖ୍ୟ ବକ୍ତା ଅବଦୁଲ ହାଇଖାତିବ୍‌ ସୂଚନା ଦେଇଛନ୍ତି।','2016_10_26_20_00_28_af.jpg','Newstory','27-01-2017','','',''),(21,'ସ୍କୋର୍ପିଓ ଉପରକୁ ବୋମା ଓ ଗୁଳିମାଡ଼:ଜଣେ ମୃତ',NULL,'ପାରାଦୀପ: ପାରାଦୀପର କେନ୍ଦ୍ରୀୟ ବିଦ୍ୟାଳୟ ନିକଟରେ ଚଳନ୍ତା ମହିନ୍ଦ୍ରା ସ୍କୋର୍ପିଓ ଗାଡ଼ି ଉପରୁକ ବୋମା ଓ ଗୁଳିମାଡ଼ ହୋଇଛି। ଗୁଳିମାଡ଼ରେ ସି ୱେଜ୍‌ ପରିବହନ କମ୍ପାନିର ମ୍ୟାନେଜର ମହେନ୍ଦ୍ର ସ୍ୱାଇଁ ନିହତ ହୋଇଛନ୍ତି। ମହେନ୍ଦ୍ର ଅଫିସ୍‌ କାମରେ  ଏକ ସ୍କୋର୍ପିଓ ଗାଡ଼ିରେ ଯାଉଥିବା ବେଳେ ୨ଟି ବାଇକ୍‌ରେ ସବାର ଦୁର୍ବୃତ୍ତମାନେ ଆସି ତାଙ୍କ ଗାଡ଼ିର ଆଗପଟକୁ ବୋମା ଫିଙ୍ଗିଥିଲେ।\r\n \r\nବୋମାମାଡ଼ ପରେ  ଗାଡ଼ିରେ ବସିଥିବା ମହେନ୍ଦ୍ର ସ୍ୱାଇଁଙ୍କୁ ଅତି ନିକଟରୁ ଗୁଳି କରିଥିଲେ। ଅତି ନିକଟରୁ ମହେନ୍ଦ୍ରଙ୍କୁ ଦୁର୍ବୃତ୍ତମାନେ ତିନି ରାଉଣ୍ଡ ଗୁଳି କରିଥିଲେ। ଘଟଣାସ୍ଥଳରେ ମହେନ୍ଦ୍ରଙ୍କ ମୃତ୍ୟୁ ହୋଇଛି। ପୁଲିସ୍‌ ଘଟଣାସ୍ଥଳରେ ପହଞ୍ଚି ତଦନ୍ତ ଆରମ୍ଭ କରିଛି।\r\n \r\nପ୍ରକାଶଯୋଗ୍ୟ ଯେ ପାରାଦୀପରେ ମାଲ୍‌ ପରିବହନକୁ ନେଇ ଗତ କିଛି ମାସ ହେଲା ବିଭିନ୍ନ ପରିବହନ ସଂସ୍ଥା ଓ ଠିକା ଶ୍ରମିକ ସଂସ୍ଥା ମଧ୍ୟରେ ବିବାଦ ଆରମ୍ଭ ହୋଇଛି। ଆଜି ସେ ବିବାଦ ଉଗ୍ର ରୂପ ନେଇଥିବା ସ୍ଫଷ୍ଟ ହୋଇଛି।','2016_10_26_20_15_37_dead.jpg','Newstory','27-01-2017','','',''),(22,'ବିଲଡିଂରେ ଅଗ୍ନିକାଣ୍ଡ',NULL,'ନୂଆଦିଲ୍ଲୀ: ନୂଆଦିଲ୍ଲୀର ବାରଖମ୍ବା ରୋଡରେ ଥିବା ଏକ ବିଲଡିଂରେ ଅଗ୍ନିକାଣ୍ଡ ହୋଇଛି। ବିଲଡିଂ୧୫ ମହଲାରେ ଅଗ୍ନିକାଣ୍ଡ ହୋଇଥିବା ବେଳେ କେହି ଆହତ ହୋଇ ନ ଥିବା କୁହାଯାଇଛି। ନିଆଁ ଲିଭାଇବା ପାଇଁ ୧୫ଟି ଦମକଳକୁ ନିୟୋଜିତ କରାଯାଇଥିଲା। ନିଆଁକୁ କାବୁ କରିବା ପାଇଁ ଅଧ ଘଣ୍ଟାରୁ ଅଧିକ ସମୟ ଲାଗିଥିଲା ବୋଲି ଜଣେ ଅଗ୍ନିଶମ ଅଧିକାରୀ କହିଛନ୍ତି। ଓଭେନର ବିଦ୍ୟୁତରେ ସଟ୍‌ସର୍କିଟ ହୋଇ ଏହି ଅଗ୍ନିକାଣ୍ଡ ଘଟିଥିବା କୁହାଯାଇଛି।','2016_10_26_20_20_52_build.jpg','Newstory','27-01-2017','','',''),(23,'ବାଣ କାରଖାନାରେ ବିସ୍ଫୋରଣ',NULL,'ବାରଣାସୀ: ବାରଣାସୀର ଏକ ବେଆଇନ ବାଣ କାରଖାନାରେ  ମଙ୍ଗଳବାର ରାତିରେ ବିସ୍ଫୋରଣ ଘଟିବାରୁ ୪ଜଣ ମୃତ୍ୟୁବରଣ କରିବା ସହ ୬ଜଣ ଆହତ ହୋଇଛନ୍ତି। ଦୁଇ ଜଣଙ୍କୁ ଆହତଙ୍କୁ ଡାକ୍ତରଖାନାରେ ଭର୍ତ୍ତି କରାଯାଇଥିଲା। ପ୍ରାଥମିକ ଉପଚାର ପରେ ସେମାନଙ୍କୁ ଛାଡ଼ି ଦିଆଯାଇଛି। ବିସ୍ଫୋରକ ସାମଗ୍ରୀରେ ନିଆଁ ଲାଗିଯିବାରୁ ନିଆଁ ପାଖ ଘରକୁ ମଧ୍ୟ ମାଡ଼ି ଯାଇଥିଲା। ପୁଲିସ ଓ ଜାତୀୟ ବିପର୍ଯ୍ୟୟ ପ୍ରଶମନ ବଳ(ଏନ୍‌ଡିଆର୍‌ଏଫ୍‌) ଘଟଣା ସ୍ଥଳରେ ପହଞ୍ଚି ଉଦ୍ଧାର କାର୍ଯ୍ୟ ଆରମ୍ଭ କରିଥିଲେ।','2016_10_26_20_22_37_exp.jpg','Newstory','27-01-2017','','',''),(24,'୫୦୦ ଓ ୧୦୦୦ ଟଙ୍କିଆ ନେବାବେଳେ ସତର୍କ ରୁହନ୍ତୁ',NULL,'ମୁମ୍ବାଇ : ଭାରତରେ ନକଲି ନୋଟ୍ର ପ୍ରସାର ବୃଦ୍ଧି ପାଇବାକୁ ନେଇ ରିଜର୍ଭ ବ୍ୟାଙ୍କ୍ ଅଫ୍ ଇଣ୍ଡିଆ ଚିନ୍ତା ପ୍ରକଟ କରିଛି । ନକଲି ନୋଟ୍ର ପ୍ରସାର ରୋକିବା ପାଇଁ ବିଭିନ୍ନ ଉଦ୍ୟମ ସତ୍ତେ୍ୱ ଏହା କମିବାର ନାମ ଧରୁନାହିଁ । ଏହାକୁ ରୋକିବା ନିମନ୍ତେ ରିଜର୍ଭ ବ୍ୟାଙ୍କ୍ ସାଧାରଣ ଜନତାଙ୍କୁ ସତର୍କ ରହିବାକୁ ପରାମର୍ଶ ଦେଇଛି । ୫୦୦ ଟଙ୍କିଆ ଏବଂ ୧୦୦୦ ଟଙ୍କିଆ ନୋଟ୍ ଗ୍ରହଣ କରିବା ପୂର୍ବରୁ ଏହାକୁ ଠିକ୍ ଭାବେ ଯାଞ୍ଚ କରି ଗ୍ରହଣ କରନ୍ତୁ ବୋଲି ରିଜର୍ଭ ବ୍ୟାଙ୍କ୍ ପକ୍ଷରୁ କୁହାଯାଇଛି । କେତେକ ଅସାମାଜିକ ଗୋଷ୍ଠୀ  ବଜାରରେ ବହୁ ପରିମାଣରେ ନକଲି ନୋଟ୍ ପ୍ରସାର କରୁଛନ୍ତି । ଏଥିପ୍ରତି ସର୍ତକତା ଅବଲମ୍ବନ କରିବାକୁ ରିଜର୍ଭ ବ୍ୟାଙ୍କ୍ ପକ୍ଷରୁ କୁହାଯାଇଛି ।','2016_10_27_20_29_39_Npte.jpg','Newstory','27-01-2017','','',''),(25,'ମୋଦିଙ୍କୁ ଥରୁରଙ୍କ ପ୍ରଶଂସା',NULL,'ନୂଆଦିଲ୍ଲୀ: ପୂର୍ବତନ କେନ୍ଦ୍ରମନ୍ତ୍ରୀ ତଥା କଂଗ୍ରେସ ସାଂସଦ ଶଶି ଥରୁର ପୁଣିଥରେ ଖୋଲାଖୋଳି ଭାବେ ପ୍ରଧାନମନ୍ତ୍ରୀ ନରେନ୍ଦ୍ର ମୋଦିଙ୍କୁ ପ୍ରଶଂସା କରିଛନ୍ତି । ଏକ ଟେଲିଭିଜନ୍ ଚ୍ୟାନେଲ୍କୁ ସାକ୍ଷାତକାର ଦେବା ଅବସରରେ ସେ ମୋଦିଙ୍କ ଶକ୍ତି ଏବଂ ତାଙ୍କର ଉତ୍ସାହ ଉତ୍ସାହକୁ ପ୍ରଶଂସା କରିଛନ୍ତି । ଏହି ଅବସରରେ ଥରୁର ସର୍ଜିକାଲ୍ ଷ୍ଟ୍ରାଇକ୍କୁ ମଧ୍ୟ ପ୍ରଶଂସା କରିଥିଲେ । ତେବେ ବେଲୁଚିସ୍ତାନ ମାମଲାରେ କେନ୍ଦ୍ର ସରକାରଙ୍କୁ ସେ ସମାଲୋଚନା କରିଛନ୍ତି । କାଶ୍ମୀର ସହିତ ବେଲୁଚିସ୍ତାନ ପ୍ରସଙ୍ଗ ଉଠାଇବା ଠିକ୍ ନୁହେଁ ବୋଲି ଥରୁର୍ କହିଛନ୍ତି । ସେ କହିଲେ ଯେ, ଆମେ କାହାର ଆନ୍ତରିକ ମାମଲାରେ ମୁଣ୍ଡ ଖେଳାଇବା ଉଚିତ୍ ନୁହେଁ । ପାକିସ୍ତାନକୁ ଆନ୍ତର୍ଜାତୀୟ ସ୍ତରରେ ଏକଘରିଆ କରିବା ପ୍ରସଙ୍ଗରେ ଥରୁର କହିଲେ ଯେ, ପାକିସ୍ତାନକୁ ଅଦ୍ୟାବଧି ଏକଘରିଆ କରାଯାଇ ପାରିନାହିଁ । ଆମେରିକା ସମେତ ଅନେକ ଦେଶ ଏବେବି ପାକିସ୍ତାନ ସହିତ ଅଛନ୍ତି ବୋଲି ଥରୁର କହିଛନ୍ତି ।','2016_10_27_20_32_22_tharoor.jpg','Newstory','27-01-2017','','',''),(26,'ସପା କଳିରୁ ଫାଇଦା ଚକ୍କରରେ କଂଗ୍ରେସ',NULL,'ନୂଆଦିଲ୍ଲୀ, ସମାଜବାଦୀ ପାର୍ଟିରେ ଲାଗି ରହିଥିବା ବିବାଦ ଉପରେ କଂଗ୍ରେସ ସତର୍କ ଦୃଷ୍ଟି ରଖିଛି । ବିଧାନସଭା ନିର୍ବାଚନ ପୂର୍ବରୁ ଯଦି ଏହି ବିବାଦ ଅଧିକ ଘନିଭୂତ ହୋଇ ଦଳରେ ବିଭାଜନ ଘଟାଏ, ତେବେ ଅଖିଳେଶଙ୍କ ଗୋଷ୍ଠୀ ସହ ଆସନ ବୁଝାମଣା ଚକ୍କରରେ କଂଗ୍ରେସ ଅଛି କାରଣ କଂଗ୍ରେସ ଉପସଭାପତି ରାହୁଳ ଗାନ୍ଧି ଏବଂ ଅଖିଳେଶଙ୍କ ମଧ୍ୟରେ ଉତ୍ତମ ସଂପର୍କ ରହିଛି । ସମାଜବାଦୀ ପାର୍ଟିରେ ଯେଉଁ ସବୁ ନାଟକ ଚାଲିଛି ଏଥିପାଇଁ ଦଳର ସୁପ୍ରିମୋ ମୁଲାୟମ ସିଂ ଯାଦବ ଦାୟୀ ବୋଲି କଂଗ୍ରେସ ବିଚାର କରୁଛି । ଏହି ନାଟକର କାହାଣୀ, ଚିତ୍ରନାଟ୍ୟ, ସଂଳାପ ଏବଂ ନିର୍ଦ୍ଦେଶନା ଖୋଦ୍ ମୁଲାୟମ ଦେଉଛନ୍ତି । \r\nଉଭୟ ରାହୁଲ ଓ ଅଖିଳେଶ ନିଜ ନିଜ ଦଳରେ ଯୁବ ଗୋଷ୍ଠୀର ପ୍ରତିନିଧିତ୍ୱ କରୁଛନ୍ତି ଏବଂ ସେମାନେ ଦଳର ପୁରୁଣା କାଳିଆ ନେତାମାନଙ୍କ ମନୋଭାବ ଓ କାର୍ଯ୍ୟ ଶୈଳୀକୁ ପସନ୍ଦ କରୁନାହାନ୍ତି । ତେଣୁ ସମାଜବାଦୀ  ଦଳରେ ଯଦି ବିଭାଜନ ଘଟେ ତେବେ ୨୦୧୭ ବିଧାନସଭା ନିର୍ବାଚନରେ ଏକ ନୂଆ ମେଣ୍ଟ ମୁଣ୍ଡ ଟେକିପାରେ ବୋଲି ରାଜନୈତିକ ପର୍ଯ୍ୟବେକ୍ଷକମାନେ ବିଚାର କରୁଛନ୍ତି । ଅଖିଳେଶ ଏବଂ ଚାଚା ଶିବପାଲଙ୍କ ମଧ୍ୟରେ ଲାଗି ରହିଥିବା ବିବାଦ ପ୍ରେକ୍ଷାପଟ୍ଟରେ ୟୁପି କଂଗ୍ରେସ ନେତୃବୃନ୍ଦଙ୍କ ସହ ନିକଟରେ ପ୍ରୀୟଙ୍କା ଗାନ୍ଧି ଏକତ୍ର ହୋଇ ଆଲୋଚନା କରିଥିଲେ । ଯଦି ବିଧାନସଭା ନିର୍ବାଚନ ପୂର୍ବରୁ ସମାଜବାଦୀ ପାର୍ଟିରେ ବିଭାଜନ ଘଟେ ତେବେ ରାଜ୍ୟ ବିଧାନସଭାରେ ଆସ୍ଥା ଭୋଟରେ କଂଗ୍ରେସ ଅଖିଳେଶଙ୍କ ଗୋଷ୍ଠୀକୁ ସମର୍ଥନ କରିବ ବୋଲି ମଧ୍ୟ ଏହି ବୈଠକରେ ନିଷ୍ପତ୍ତି ହୋଇଥିବା ଶୁଣିବାକୁ ମିଳୁଛି । ପ୍ରୀୟଙ୍କାଙ୍କ ଦ୍ୱାରା ଆହୂତ ଏହି ବୈଠକରେ କଂଗ୍ରେସର ମୁଖ୍ୟମନ୍ତ୍ରୀ ପ୍ରାର୍ଥୀ ଶିଲା ଦୀକ୍ଷିତ୍ ଓ ଦଳର ରାଜ୍ୟ ସଭାପତି ରାଜ୍ ବବ୍ର ମଧ୍ୟ ଉପସ୍ଥିତ ଥିଲେ । ତେବେ କଂଗ୍ରେସର ଏହି ନିଷ୍ପତ୍ତି ସାଧାରଣରେ ପ୍ରକାଶ କରାଯାଇ ନାହିଁ । ବର୍ତ୍ତମାନ ଆଧୁନିକଶୈଳୀରେ ଦଳର ପୁର୍ନଗଠନ କରିବାକୁ ଅଖିଳେଶ ଚାହୁଁଛନ୍ତି ।  ମାତ୍ର ଚାଚା ଶିବପାଲଙ୍କ ପରି ପୁରୁଣା ନେତାମାନେ ଏହାକୁ ସମର୍ଥନ କରୁନାହାନ୍ତି । କଂଗ୍ରେସ-ଅଖିଳେଶ ମେଣ୍ଟ ସମ୍ଭବ ହେଲେ ଉତ୍ତରପ୍ରଦେଶର ମୁସଲିମ୍ ଭୋଟ ଏହି ମେଣ୍ଟକୁ ସମର୍ଥନ କରିବ ବୋଲି କଂଗ୍ରେସ ଆଶା କରୁଛି ।','2016_10_27_20_34_21_Cong.jpg','Newstory','27-01-2017','','',''),(27,'ବସ୍ ହେବ ତ ଏମିତି','ଦୀପାବଳୀ ବୋନସ୍ ରେ ଘର ଆଉ କାର','ସୁରଟ:ଗୁଜୁରାଟର ଜଣେ ହୀରା ବ୍ୟବସାୟୀ ସାବାଜୀ ଡୋଲ୍କିଆ ନିଜ କର୍ମଚାରୀଙ୍କୁ ଦୀପାବଳୀ ବୋନସ୍ ରେ ଫ୍ଲାଟ ଏବଂ କାର ଦେଉଛନ୍ତି ।ଏହି ସମ୍ବାଦ ଅନ୍ୟମାନଙ୍କ ପାଇଁ ଇଷ୍ୱର୍ାର କାରଣ ହେଲେ ବି ସତ କଥା । ହୀରା ବ୍ୟବସାୟୀ ଜଣକ ନିଜ କର୍ମଚାରୀ ମାନଙ୍କୁ ୪୦୦ଫ୍ଲାଟ ଏବଂ ୧୨୬୦ କାର ପ୍ରଦାନ କରିବା ପାଇଁ ନିଷ୍ପତି ନେଇଛନ୍ତି । ଡୋଲକିଆଙ୍କ କମ୍ପାନୀ ହରେକିୃଷ୍ନା ଏକ୍ସପୋର୍ଟ ଏହି ଅବସରରେ ୫୧ କୋଟି ଟଙ୍କା ଖର୍ଚ୍ଚ କରିବ ।୧୭୧୬ ଜଣ କର୍ମଚାରୀ ନିଜର ଶ୍ରେଷ୍ଠ ଦକ୍ଷତା ପାଇଁ ଏହି ବୋନସ୍ ପାଇବେ । \r\nଡୋଲକିଆ ଗତ ୨୦୧୧ ମସିହାରୁ ଏହି ଶ୍ରେଷ୍ଠ ଦକ୍ଷତା ବୋନସ ପ୍ରଦାନ କରି ଆସୁଛନ୍ତି । ଗତ ବର୍ଷ ମଧ୍ୟ ସେ ୪୯୧ କାର ଏବଂ ୨୦୦ ଫ୍ଲାଟ ପ୍ରଦାନ କରିଥିଲେ ।ଏ ବାବଦରେ ତାଙ୍କର ୫୦ କୋଟି ଟଙ୍କା ଖର୍ଚ୍ଚ ହୋଇଥିଲା । ସାବଜୀ ଡୋଲକିଆ ଅମରେଲି ଜିଲ୍ଲାର ଏକ ଅଖ୍ୟାତ ଗ୍ରାମରୁ ଆସି ମଧ୍ୟ ନିଜର ଜଣେ ସମ୍ପର୍କୀୟଙ୍କ ଠାରୁ ଅଳ୍ପ କିଛି ଋଣନେଇ ବ୍ୟବସାୟ ଆରମ୍ଭ କରିଥିଲେ । ନିଜର ନିଷ୍ଠା ଓ ମେହେନତ୍  ବଳରେ ଏପରି ସ୍ତରକୁ ଉଠି ପାରିଛନ୍ତି । ଲୋକେ କହୁଛନ୍ତି ବସ୍ ହେବ ତ ଏମିତି।','2016_10_28_00_22_48_Sabaji.jpg','Newstory','27-01-2017','','',''),(28,'ସୈତନାର ବେଦ ପାଠ','ସରିଫ୍ କହିଲେ ପାକିସ୍ତାନ ଶାନ୍ତିପ୍ରିୟ ଦେଶ','ଇସଲାମାବାଦ : ପାକିସ୍ତାନ ଏକ ଶାନ୍ତିପ୍ରିୟ ଦେଶ । ଅନ୍ୟ ପକ୍ଷରେ ଭାରତ ପକ୍ଷରୁ ବାରମ୍ବାର ସୀମାନ୍ତରେ ଅସ୍ତ୍ରବିରତି ଉଲ୍ଲଘଂନ କରାଯାଇ ବ୍ୟତିବ୍ୟସ୍ତ କରାଯାଉଛି । ଭାରତର ଏପରି ଉଦ୍ୟମ ଜାରି ରହିଲେ, ପାକିସ୍ତାନ ଉପଯୁକ୍ତ ଶିକ୍ଷା ନଦେଇ ଛାଡିବ ନାହିଁ ବୋଲି ମିଆଁ ନୱାଜ ସରିଫ ଓଲଟା ଭାରତ ବିରୋଧରେ ଅଭିଯୋଗ କରିଛନ୍ତି ।\r\nରେଡିଓ ପାକିସ୍ତାନ ସୂତ୍ରରୁ ମିଳିଥିବା ଖବରରୁ ଜଣାଯାଇଛି ଯେ ଅସ୍ତ୍ରବିରତି କ୍ଷେତ୍ରରେ ପାକିସ୍ତାନ ସର୍ବାଧିକ ଧର୍ଯ୍ୟ ଓ ସହନଶୀଳତାର ପ୍ରମାଣ ଦେଇଛି ବୋଲି କୁଆଡେ ସରିଫ କହନ୍ତି । ସେ ପୁଣି କହନ୍ତି ଯେ, ପାକିସ୍ତାନ ବହୁ ସଂଜମ ଆଚରଣ କଲାଣି । ଏଣିକି ଆଉ ନୁହେଁ, ଏଣିକି ଅସ୍ତ୍ରବିରତି ଉଲ୍ଲଘଂନ କଲେ ଭାରତକୁ ଛାଡିବ ନାହିଁ । ପାକିସ୍ତାନ ଏକ ଶାନ୍ତିପ୍ରୀୟ ଦେଶ ବୋଲି ଦୋହରାଇବା ସହ ସରିଫ କହନ୍ତି ଯେ ଆଲୋଚନା ଜରିଆରେ ଅସମାହିତ ପ୍ରଶ୍ନର ସମାଧାନ ପାଇଁ ପାକିସ୍ତାନ ସର୍ବଦା ଉଦ୍ୟମ ଚଳାଇ ଆସୁଛି ।\r\nସେ ପୁଣି କହିଛନ୍ତି ଯେ ଏକ ଦାୟିତ୍ୱ ସଂପନ୍ନ ରାଷ୍ଟ୍ର ରୂପେ ଦକ୍ଷିଣ ଏସିଆରେ ଶାନ୍ତି ଓ ଆଞ୍ଚଳିକ ସ୍ଥିରତା ପାଇଁ ପାକିସ୍ତାନ ସର୍ବଦା ଉଦ୍ୟମ କରୁଛି । ମାତ୍ର ଦୁର୍ଭାଗ୍ୟର କଥା ଯେ, ପାକିସ୍ତାନର ଏହି ଭାବନାକୁ ଭାରତ ସମ୍ମାନ ଦେଉନାହିଁ । ପାକିସ୍ତାନର ଶାନ୍ତିପ୍ରିୟ ମନୋଭାବକୁ ଭାରତ ଦୁର୍ବଳତା ବୋଲି ଭାବୁଛି ବୋଲି ସରିଫ କହନ୍ତି ।','2016_10_28_22_38_14_Nawaz.jpg','Newstory','27-01-2017','','',''),(30,'ପାକିସ୍ତାନପଟୁ ଗୁଳିମାଡ଼ ଜାରି','ଦୁଇ ଶିଶୁଙ୍କ ସମେତ ୭ମୃତ','ଜାମ୍ମୁ : ପାକିସ୍ତାନ ପକ୍ଷରୁ ମଙ୍ଗଳବାର ଦିନ ମଧ୍ୟ ଜାମ୍ମୁ-କାଶ୍ମୀରର ଅନେକ ସ୍ଥାନରେ ଅସ୍ତ୍ରବିରତିର ଉଲ୍ଲଙ୍ଘନ କରାଯାଇ ଗୁଳିମାଡ଼ କରାଯାଇଛି । ଗୁଳିମାଡ଼ରେ ଦୁଇଜଣ ଶିଶୁଙ୍କ ସମେତ ମୋଟ ୭ଜଣଙ୍କ ମୃତ୍ୟୁ ଘଟିଥିବାବେଳେ ୮ଜଣ ଆହତ ହୋଇଛନ୍ତି । ପୋଲିସ୍ ସୂତ୍ରରୁ ମିଳିଥିବା ସୂଚନା ଅନୁସାରେ ମଙ୍ଗଳବାର ଦିନ ପାକିସ୍ତାନୀ ସେନା ନୌଶେରା, ସାମ୍ବା, ଆର୍ଏସ୍ପୁରା ଏବଂ ରାଜୌରୀ ସେକ୍ଟରର ଅରନିଆରେ ଗୁଳିମାଡ଼ କରିଥିଲେ । ଏହି ଅବସରରେ ପାକିସ୍ତାନୀ ସେନା ଆରନିଆର ସୀମାବର୍ତ୍ତୀ ଅଞ୍ଚଳରେ ୮୨ମିମି ମୋର୍ଟାର ମାଡ଼ କରିଥିଲେ । ମୋର୍ଟାର ମାଡ଼ରେ ଏଠାକାର କେତେକ ଗ୍ରାମ କ୍ଷତିଗ୍ରସ୍ତ ମଧ୍ୟ ହୋଇଛି । ଭାରତୀୟ ସେନା ସୂତ୍ରରୁ ମିଳିଥିବା ଖବର ଅନୁସାରେ ମଙ୍ଗଳବାର ଦିନ ସକାଳେ ପ୍ରାୟ ୫ଟା ସମୟରେ ରାଜୌରୀ ଜିଲ୍ଲାର ନୌଶେରା ସେକ୍ଟରରେ ପାକିସ୍ତାନ ଗୁଳିମାଡ଼ କରିଥିଲା । ଏହାପରେ ସକାଳ ୬.୩୦ରେ ଜମ୍ମୁର ସାମ୍ବାସ୍ଥିତ ରାମଗଡ଼ରେ ଗୁଳିବର୍ଷଣ କରିଥିଲା । ଏଠାରେ ଗୁଳିମାଡ଼ରେ ଜେରଦା ଗ୍ରାମର ରାଜିନ୍ଦ୍ର କୌର ନାମ୍ନୀ ଜଣେ ୧୮ବର୍ଷୀୟ ଯୁବତୀଙ୍କର ମୃତ୍ୟୁ ଘଟିଛି । ସେହିଭଳି ଗୁଳିମାଡ଼ରେ ଦୁଇଜଣ ଶିଶୁ ଏବଂ ଅନ୍ୟ ଜଣେ ବ୍ୟକ୍ତିଙ୍କର ମୃତ୍ୟୁ ଘଟିଥିବା ଜଣାପଡ଼ିଛି । ସେହିଭଳି ଆର୍ଏସ୍ପୁରାରେ ମୋର୍ଟାର ମାଡ଼ ଯୋଗୁଁ ସୀମାବର୍ତ୍ତୀ ଗ୍ରାମର  ତିନିଜଣ ଆହତ ହୋଇଥିବା ସୂଚନା ମିଳିଛି । ଏହି ତିନିଜଣ ହେଲେ ଦର୍ଶନା ଦେବୀ (୫), ବୋଧ ରାଜ (୪୦) ଏବଂ ଚଂଚେଲୁ ଦେବୀ (୪୦) ବୋଲି ଜଣାପଡ଼ିଛି ।  ଆହତମାନଙ୍କୁ ଚିକିତ୍ସା ପାଇଁ ସ୍ଥାନୀୟ ସରକାରୀ ମେଡିକାଲ କଲେଜ ହସ୍ପିଟାଲରେ ଭର୍ତ୍ତି କରାଯାଇଛି । ବିଏସ୍ଏଫ୍ ପକ୍ଷରୁ ଏହାର ଦୃଢ଼ ଜବାବ ଦିଆଯାଇଛି । କୌଣସି ଭାରତୀୟ ସେନା ହତାହତ ହୋଇଥିବା ସୂଚନା ମିଳିନାହିଁ । \r\nପୂର୍ବରୁ ସୋମବାରଦିନ ପାକିସ୍ତାନୀ ସେନାର ଗୁଳିମାଡ଼ରେ ଜଣେ ଯବାନ ସହିଦ ହୋଇଥିବାବେଳେ ଜଣେ ମହିଳା ମୃତ୍ୟୁବରଣ କରିଥିଲେ ଏବଂ ୪ଜଣ ଯବାନଙ୍କ ସମେତ ମୋଟ ୭ଜଣ ଆହତ ହୋଇଥିଲେ । ସୂଚନାଯୋଗ୍ୟ ଯେ, ସେପ୍ଟେମ୍ବର ୨୯ରେ ଭାରତର ସର୍ଜିକାଲ୍ ଷ୍ଟ୍ରାଇକ୍ ପରେ ସୀମାରେ ପାକିସ୍ତାନର ଅସ୍ତ୍ର ବିରତି ଉଲ୍ଲଙ୍ଘନ ଜାରି ରହିଛି । ଅଦ୍ୟାବଧି ପାକିସ୍ତାନ ୬୩ଥର ଅସ୍ତ୍ରବିରତି ଉଲ୍ଲଙ୍ଘନ କରିଥିବା ସୂଚନା ମିଳିଛି । ଏହି କାରଣରୁ ୮ଜଣ ଭାରତୀୟ ସେନାଙ୍କ ସମେତ ମୋଟ ୧୩ଜଣ ମୃତ୍ୟୁବରଣ କରିଛନ୍ତି ଏବଂ ୪୦ରୁ ଅଧିକ ଆହତ ହୋଇଛନ୍ତି । \r\nଗଣମାଧ୍ୟମ ସୂତ୍ରରୁ ମିଳିଥିବା ଖବର ଅନୁସାରେ ପାକିସ୍ତାନ ସୀମାରେ ଅଧିକରୁ ଅଧିକ ସୈନ୍ୟ ନିୟୋଜନ କରିବା ସହ  ହେଲିକପ୍ଟର ଏବଂ ବିପୁଳ ପରିମାଣର ଅସ୍ତ୍ରଶସ୍ତ୍ର ଠୁଳ କିରବାରେ ଲାଗିଛି । ତେବେ ପାକିସ୍ତାନ ପକ୍ଷରୁ ଯେକୌଣସି ଆକ୍ରମଣର ଜବାବ ପାଇଁ ଭାରତ ପ୍ରସ୍ତୁତ ରହିଥିବା କୁହାଯାଉଛି ।','2016_11_01_23_55_11_Pak.jpg','Newstory','27-01-2017','#54b8ed','',''),(32,'ବିଷାକ୍ତ ବାତାବରଣରେ ରହୁଛନ୍ତି ବିଶ୍ୱର ୩୦କୋଟି ଶିଶୁ: ୟୁନିସେଫ୍',NULL,'ୱାଶିଂଟନ୍ : ମିଳିତ ଜାତିସଂଘ ଦ୍ୱାରା କରାଯାଇଥିବା ଏକ ଅନୁସନ୍ଧାନରୁ ଜଣାପଡ଼ିଛି ଯେ, ବିଶ୍ୱର ୩୦କୋଟି ଶିଶୁ ବିଷାକ୍ତ ବାୟୁ ସଂପର୍କରେ ଆସୁଛନ୍ତି । ରିପୋର୍ଟ ଅନୁସାରେ ବିଷାକ୍ତ ବାୟୁ ସଂପର୍କରେ ଆସିବା ଫଳରେ ସେମାନଙ୍କ ଗମ୍ଭୀର ଶାରୀରିକ ହାନି ଘଟିବାପରେ ଏବଂ ସେମାନଙ୍କ ମସ୍ତିଷ୍କ ବିକାଶରେ କୁପ୍ରଭାବ ପଡ଼ିବାର ଯଥେଷ୍ଟ ସମ୍ଭାବନା ରହିଛି । ୟୁନିସେଫ୍ ପକ୍ଷରୁ କରାଯାଇଥିବା ଏହି ଅନୁସନ୍ଧାରେ କୁହାଯାଇଛି ।  ବିଶ୍ୱରେ ପ୍ରତ୍ୟେକ ୭ଜଣ ଶିଶୁଙ୍କ ମଧ୍ୟରୁ ଜଣେ ଶିଶୁ ଏଭଳି ବିଷାକ୍ତ ବାୟୁରେ ନିଶ୍ୱାସ ପ୍ରଶ୍ୱାସ ନେଉଛି ଯେଉଁବାୟୁ ଆନ୍ତର୍ଜାତୀୟ ମାନଠାରୁ ୬ଗୁଣା ଅଧିକ ଦୂଷିତ । ଦୂଷିତଳ ବାୟୁ ବିଶ୍ୱରେ ଶିଶୁ ମୃତ୍ୟୁହାର ବୃଦ୍ଧିର କାରଣ ବୋଲି ରିପୋର୍ଟରେ କୁହାଯାଇଛି ।  ଚଳିତ ନଭେମ୍ବର ୭ରୁ ୧୮ ତାରିଖ ପର୍ଯ୍ୟନ୍ତ ମରକ୍କୋରୋ ହେବାକୁ ଥିବା ବାର୍ଷିକ ଜଳବାୟୁ ପରିବର୍ତ୍ତନ ସମ୍ମିଳନୀ ପୂର୍ବରୁ ୟୁନିସେଫ୍ ପକ୍ଷରୁ ଏହି ରିପୋର୍ଟ ପ୍ରକାଶ କରାଯାଇଛି । ଶିଶୁଙ୍କ କଲ୍ୟାଣ ନିମନ୍ତେ କାର୍ଯ୍ୟ କରୁଥିବା ୟୁନିସେଫ୍ ବିଶ୍ୱବ୍ୟାପୀ ବିଭିନ୍ନ ଦେଶରେ ରହିଥିବା ଏହାର ନେତାମାନଙ୍କୁ ନିଜ ନିଜର ଦେଶରେ ବାୟୁ ପ୍ରଦୂଷଣକୁ କମ୍ କରିବା ପାଇଁ ଆବଶ୍ୟକୀୟ ପଦକ୍ଷେପ ଗ୍ରହଣ କରବାକୁ ଅନୁରୋଧ କରିଛି । \r\nୟୁନିସେଫ୍ର କାର୍ଯ୍ୟକାରୀ ନିର୍ଦ୍ଦେଶକ ଆନ୍ଥୋନୀ ଲେକ୍ କହିଛନ୍ତି ଯେ, ପ୍ରତ୍ୟେକ ବର୍ଷ ବାୟୁ ପ୍ରଦୂଷଣ କାରଣରୁ ବିଶ୍ୱରେ ୬,୦୦,୦୦୦ ପାଞ୍ଚବର୍ଷରୁ କମ୍ ଶିଶୁଙ୍କର ମୃତ୍ୟୁ ଘଟିଛୁ । ଦିନକୁ ଦିନ ବାୟୁ ପ୍ରଦୂଷଣ ବୃଦ୍ଧି ପାଉଥିବାରୁ ଶିଶୁଙ୍କ ପାଇଁ ଭୟଙ୍କର ବାତାବରଣ ସୃଷ୍ଟି ହୋଇଛି । ପ୍ରଦୂଷିତ ବାୟୁ ଶିଶୁଙ୍କ ଶାରିରୀକ ବିକାଶରେ ବାଧା ସୃଷ୍ଟି କରିବା ସହ ମାନସିକ ଅବସ୍ଥାକୁ ମଧ୍ୟ ସ୍ଥାୟୀ ଭାବେ କ୍ଷତି ପହଞ୍ଚାଇବାର ସମ୍ଭାବନା ରହିଛି ବୋଲି ଆନ୍ଥୋନୀ ଲେକ୍ କହିଛନ୍ତି । ୟୁନିସେଫ୍ ପକ୍ଷରୁ କରାଯାଇଥିବା ଅନୁସନ୍ଧାନରେ ଆହୁରି କୁହାଯାଇଛି ଯେ, ବିଶ୍ୱର ପ୍ରାୟ ୨ ଅରବ୍ ଶିଶୁ ଏଭଳି ଅଞ୍ଚଳରେ ରହୁଛନ୍ତି ଯେଉଁ ଅଞ୍ଚଳର ବାୟୁ ବିଶ୍ୱ ସ୍ୱାସ୍ଥ୍ୟ ସଂଗଠନ ଦ୍ୱାରା ଜାରି ନ୍ୟୁନତମ ବାୟୁ ଗୁଣବତ୍ତାଠାରୁ ଅତ୍ୟନ୍ତ ଖରାପ । ରିପୋର୍ଟ ଅନୁସାରେ ଯାନବାହନ ଛାଡୁଥିବା ଧୂଅାଁ, କଳକାରଖାନାରୁ ନିର୍ଗତ ଇନ୍ଧନ, ଜଳି ଯାଉଥିବା ସାମଗ୍ରୀର ଅବଶେଷ ଏବଂ    ଅନ୍ୟ କେତେକ ପ୍ରଦୂଷକ ତତ୍ତ୍ୱ କାରଣରୁ ବାୟୁ ବିଷାକ୍ତ ହୋଇଯାଉଛି । ଦକ୍ଷିଣ ଏସିଆର ସର୍ବାଧିକ ଶିଶୁ ଏଭଳି ବିଷାକ୍ତ ବାତାବରଣରେ ଜୀବନଯାପନ କରୁଛନ୍ତି । ଏମାନଙ୍କ ସଂଖ୍ୟା ୬୨କୋଟି ବୋଲି ରିପୋର୍ଟରେ କୁହାଯାଇଛି । ଏହାପରେ ଆଫ୍ରିକା ରହିଛି । ଆଫ୍ରିକାର ପ୍ରାୟ ୫୨କୋଟି ଶିଶୁ ବିଷାକ୍ତ ବାତାବରଣରେ ରହୁଛନ୍ତି । ସେହିଭଳି ପଶ୍ଚିମ ଏସିଆରେ ୪୫ କୋଟି ଛାତ୍ର ବିଷାକ୍ତ ବାୟୁମଣ୍ଡଳରେ ରହୁଛନ୍ତି । ରିପୋର୍ଟରେ ଆହୁରି କୁହାଯାଇଛି ଯେ, ବାୟୁ ପ୍ରଦୂଷଣ କାରଣରୁ ଅନେକ ଶିଶୁ ନିମୋନିଆ ଏବଂ ନିଶ୍ୱାସପ୍ରଶ୍ୱାସ ନେବା ସମ୍ବନ୍ଧୀୟ ରୋଗରେ ପୀଡ଼ିତ ହେଉଛନ୍ତି । ପାଞ୍ଚବର୍ଷରୁ କମ୍ ବୟସର ୧୦ଜଣ ଶିଶୁଙ୍କ ମଧ୍ୟରୁ ଜଣେ ଶିଶୁର ଏହି କାରଣରୁ ମୃତ୍ୟୁ ହେଉଥିବା ରିପୋର୍ଟରେ କୁହାଯାଇଛି । ଏଭଳି ବାୟୁ ପ୍ରଦୂଷଣ ଆଗାମୀ ଦିନରେ ଶିଶୁଙ୍କ ସ୍ୱାସ୍ଥ୍ୟ ପାଇଁ ଭୟଙ୍କର ପରିସ୍ଥିତି ସୃଷ୍ଟି କରିବ ବୋଲି ରିପୋର୍ଟରେ କୁହାଯାଇଛି ।','2016_11_02_00_10_04_tox.jpg','Newstory','27-01-2017','#000000','',''),(33,'ରୋଲ୍ସ ରୟସ ଚୁକ୍ତିରେ ଦଲାଲି ୮୨କୋଟି','ବିବିସି ରିପୋର୍ଟରେ ପର୍ଦ୍ଦାଫାସ୍','ନୂଆଦିଲ୍ଲୀ : ଭାରତ ସହ ପ୍ରତିରକ୍ଷା ସମ୍ବନ୍ଧୀୟ ଚୁକ୍ତିରେ ଦୁର୍ନୀତି ଅଭିଯୋଗକୁ ନେଇ ପୁଣିଥରେ ଚର୍ଚ୍ଚା ଆରମ୍ଭ ହୋଇଯାଇଛି । ଏଥର ବ୍ରିଟେନ୍ କମ୍ପାନୀ ରୋଲ୍ସ ରୟସ୍ ବିରୋଧରେ ଏହି ଅଭିଯୋଗ ଆସିଛି । ଭାରତକୁ ହକ୍ ଏୟାରକ୍ରାପ୍ଟର ଇଞ୍ଜିନ୍ ବିକ୍ରୀ ନିମନ୍ତେ ଚୁକ୍ତି ହାସଲ କରିବା ପାଇଁ ଏହି ପ୍ରତିଷ୍ଠିତ କମ୍ପାନୀ ଜଣେ ଭାରତୀୟ ଦଲାଲଙ୍କୁ  ୧୦ମିଲିୟନ୍ ପାଉଣ୍ଡ ଅର୍ଥାତ୍ ୮୨କୋଟି ଟଙ୍କା ପ୍ରଦାନ କରାଯାଇଥିବା ଅଭିଯୋଗ ହୋଇଛି । ବ୍ରିଟିଶ ବ୍ରଡକାଷ୍ଟିଂ କର୍ପୋରେସନ୍ (ବିବିସି) ଏହି ଦୁର୍ନୀତିର ପର୍ଦ୍ଦାଫାସ୍ କରିଛି ।  ବିସିସି ସୂତ୍ରରୁ ମିଳିଥିବା ସୂଚନା ଅନୁସାରେ ରୋଲ୍ସ ରୟସ୍ କମ୍ପାନୀ ଜଣେ ଦଲାଲଙ୍କୁ  ଅଜ୍ଞାତ ଭାବେ ୮୨କୋଟି ଟଙ୍କା ପ୍ରଦାନ କରିଥିଲା । ଏ ନେଇ ପ୍ରମାଣ ଆମର ହସ୍ତଗତ ହୋଇଥିବା ବିବିସି ପକ୍ଷରୁ ଏକ ରିପୋର୍ଟରେ କୁହାଯାଇଛି । ଏହି ଦଲାଲଙ୍କ ନାମ ସୁଧୀର ଚୌଧୁରୀ ଏବେ ସେ ଜଣେ ଅସ୍ତ୍ରଶସ୍ତ୍ର ଡିଲର ଅଟନ୍ତି ବୋଲି ବିବିସି ପକ୍ଷରୁ କୁହାଯାଇଛି । ଭାରତୀୟ ବାୟୁସେନାରେ ବ୍ୟବହୃତ ହେଉଥିବା ହକ୍ ଏୟାରକ୍ରପ୍ଟର ଇଞ୍ଜିନ୍ ବିକ୍ରୀ ସମ୍ବନ୍ଧୀୟ କଣ୍ଟ୍ରାକ୍ଟର ହାସଲ କରିବା ପାଇଁ ଏହି ଅର୍ଥ ପ୍ରଦାନ କରାଯାଇଥାଇପାରେ ବୋଲି ବିବିସି ପକ୍ଷରୁ କୁହାଯାଇଛି । ଗୁରୁତ୍ୱପୂର୍ଣ୍ଣ କଥା ହେଉଛି ଯେ, ପୂର୍ବରୁ ମଧ୍ୟ ସୁଧୀର ଚୌଧୁରୀଙ୍କ ବିରୋଧରେ ପ୍ରତିରକ୍ଷା ଚୁକ୍ତିକୁ ନେଇ ଦଲାଲି କରିଥିବା ଅଭିଯୋଗ ରହିଛି । ଭାରତ ସରକାର ସୁଧୀର ଚୌଧୁରୀଙ୍କୁ ବ୍ଲାକ୍ ଲିଷ୍ଟ କରିଛନ୍ତି । ସଂପ୍ରତି ସେ ଲଣ୍ଡନରେ ରହୁଛନ୍ତି ।   ସୁଧୀା ଚୌଧୁରୀ ବ୍ରିଟେନର ଲିବରାଲ ଡେମୋକ୍ରାଟ୍ ନେତା ଟିମ୍ ଫେରନ୍ ଏବଂ ତାଙ୍କ ପରିବାର ପାଇଁ ଭାରତୀୟ ପରାମର୍ଶଦାତା ଭାବେ କାର୍ଯ୍ୟ କରୁଛନ୍ତି ।   ତେବେ ବିବିସି ପକ୍ଷରୁ କରାଯାଇଥିବା ଅଭିଯୋଗକୁ ସୁଧୀର ଚୌଧୁରୀଙ୍କ ପକ୍ଷରୁ ଖଣ୍ଡନ କରାଯାଇଛି । ସୁଧୀର ଚୌଧୁରୀଙ୍କ ପୁତ୍ର ଭାନୁ ଚୌଧୁରୀଭ୍କ ଓକିଲ କହିଛନ୍ତି ଯେ, ଭାରତକୁ ହକ୍ ଜେଟ୍ ବିକ୍ରୀ ନିମନ୍ତେ ରୋଲ୍ସ ରୟସ କମ୍ପାନୀ ପକ୍ଷରୁ  ସୁଧୀର ଚୌଧୁରୀ କୌଣସି ଅର୍ଥ ଗ୍ରହଣ କରିନାହାନ୍ତି ।  ଅନ୍ୟପକ୍ଷରେ ରୋଲ୍ଜ ରୟସ୍ କମ୍ପାନୀ ପକ୍ଷରୁ କୁହାଯାଇଛି ଯେ, ଏ ନେଇ  ବିବିସି ପକ୍ଷରୁ କରାଯାଉଥିବା ତଦନ୍ତରେ କମ୍ପାନୀ ସଂପୂର୍ଣ୍ଣ ସହଯୋଗ କରୁଅଛି ।','2016_11_02_01_43_11_Rolls.jpg','Newstory','27-01-2017','#0080ff','',''),(38,'ଶିଖ୍ ବିରୋଧୀ ଦଙ୍ଗା','କଂଗ୍ରେସ ଶାସନର ସର୍ବବୃହତ କଳଙ୍କ: ଜେଟଲୀ','ଅମୃତସର : କେନ୍ଦ୍ର ଅର୍ଥମନ୍ତ୍ରୀ ଅରୁଣ ଜେଟଲୀ ଏଠାରେ ପଞ୍ଜାବୀ ସୁବା ସ୍ୱର୍ଣ୍ଣ ଜୟନ୍ତୀ ପାଳନ ପାଇଁ ଆୟୋଜିତ ଏକ କାର୍ଯ୍ୟକ୍ରମରେ ଯୋଗଦେଇ କଂଗ୍ରେସକୁ କଡ଼ା ସମାଲୋଚନା କରିଛନ୍ତି । କଂଗ୍ରେସ ଶାସନ କାଳରେ ୧୯୮୪ର ଶିଖ୍ ବିରୋଧୀ ଦଙ୍ଗାକୁ ଦେଶରେ ବିନାଶର ସର୍ବବୃହତ ଉଦାହରଣ  ଆଖ୍ୟା ଦେଇଛନ୍ତି ଜେଟଲୀ । ଜେଟଲୀ ଆହୁରି କହିଲେ ଯେ, ମୁଁ ବେଳେବେଳେ ଭାବିଥାଏ ଯେତେବେଳେ ଇତିହାସ ଲେଖାଯିବ, ୧୯୮୪କୁ ବିନାଶର ବର୍ଷ ରୂପେ ଗ୍ରହଣ କରାଯିବ । କଂଗ୍ରେସ ଶାସନ କାଳରେ ଯେଉଁ ସବୁ ବିନାଶ ଦେଖିବାକୁ ପାଇଛୁ ସେଥିମଧ୍ୟରୁ ୧୯୮୪ ସର୍ବବୃହତ ଉଦାହରଣ ବୋଲି ସେ କହିଛନ୍ତି । କଂଗ୍ରେସର ଭୁଲ୍ କାରଣରୁ ଦେଶରେ ଆତଙ୍କବାଦୀ କାର୍ଯ୍ୟକଳାପ ବୃଦ୍ଧି ପାଇଥିବା ଜେଟଲୀ ଅଭିଯୋଗ କରିଛନ୍ତି । ୧୯୮୪ରେ ପଞ୍ଜାବ ଏବଂ ପଞ୍ଜାବବାସୀ ଏଥିରେ ବହୁ ପରିମାଣରେ ପ୍ରଭାବିତ ହୋଇଛନ୍ତି ବୋଲି ଜେଟଲୀ କହିଛନ୍ତି । ଜେଟଲୀ ଆହୁରି କହିଲେ ଯେ, ସରକାରଙ୍କୁ ବରଖାସ୍ତ କରିବା, ଆତଙ୍କବାଦ, ଗଣତନ୍ତ୍ରକୁ ବିପଦକୁ ଠେଲିଦେବା, ସରକାରଙ୍କୁ ତାଙ୍କ କାର୍ଯ୍ୟକାଳ ପୂରଣ କରିବାକୁ ନଦେବା, ଲୋକଙ୍କୁ ଜେଲ୍ରେ ଭର୍ତ୍ତି କରିବା ଏବଂ ଦେଶ ଉପରେ ୧୯୮୪ର କଳଙ୍କ ଲଗାଇବା ଭଳି ଘଟଣା କଂଗ୍ରେସ ଶାସନର ଗୋଟିଏ ଗୋଟିଏ ଅଂଶ । ଏହି ଅବସରରେ ସେ ସଂକଟଜନକ ସ୍ଥିତିରେ ସଫଳତାର ସହ ଲଢ଼େଇ କରିଥିବା ପଞ୍ଜାବ ଏବଂ ପଞ୍ଜାବବାସୀଙ୍କର ପ୍ରଶଂସା କରିଥିଲେ ।','2016_11_02_21_50_42_Sikh.jpg','Newstory','27-01-2017','#000000','',''),(39,'ଏଲ୍ଓସିରେ ଯୁଦ୍ଧ ପରିସ୍ଥିତି','ସମୀକ୍ଷା କଲେ ରାଜନାଥ','ନୂଆଦିଲ୍ଲୀ : ନିୟନ୍ତ୍ରଣ ରେଖା (ଏଲ୍ଓସି)ରେ ପାକିସ୍ତାନର କ୍ରମାଗତ ଅସ୍ତ୍ରବିରତି ଉଲ୍ଲଙ୍ଘନ ଏବଂ ପାକିସ୍ତାନ ସେନାର ଗୁଳିମାଡ଼ରେ ୮ଜଣ ଭାରତୀୟ ପ୍ରାଣ ହରାଇବା ପରେ ସ୍ୱରାଷ୍ଟ୍ରମନ୍ତ୍ରୀ   ରାଜନାଥ ସିଂହ ମଙ୍ଗଳବାର ଦିନ ଏକ ବୈଠକ ଡାକି ସୀମାରେ ଲାଗି ରହିଥିବା ଉତ୍ତେଜନାମୂଳକ ପରିସ୍ଥିତିର ସମୀକ୍ଷା କରିଛନ୍ତି । ତାଙ୍କ ବାସଭବନରେ ଆୟୋଜିତ ଏହି ସମୀକ୍ଷା ବୈଠକରେ ପାକିସ୍ତାନକୁ ଦୃଢ଼ ଜବାବ ଦେବାକୁ ନିଷ୍ପତ୍ତି ନଆଯାଇଛି । ବୈଠକରେ ଅନ୍ୟମାନଙ୍କ ମଧ୍ୟରେ ପ୍ରତିରକ୍ଷା ମନ୍ତ୍ରୀ ମନୋହର ପାରିକର, ସେନା ମୁଖ୍ୟ ଜେନେରାଲ୍ ଦଲବୀର ସିଂହ ସୁହାଗ, ରାଷ୍ଟ୍ରୀୟ ସୁରକ୍ଷା ପରାମର୍ଶଦାତା ଅଜିତ ଡୋଭାଲ ଏବଂ ସ୍ୱରାଷ୍ଟ୍ର ମନ୍ତ୍ରଣାଳୟର ଅନ୍ୟ ବରିଷ୍ଠ ଅଧିକାରୀମାନେ ଯୋଗ ଦେଇଥିଲେ ।  ସୀମାକୁ ସୁରକ୍ଷା ଦେବା ନେଇ ବୈଠକରେ ଆଲୋଚନା କରାଯିବା ସହ ଏଥିପାଇଁ ଉପଯୁକ୍ତ କାର୍ଯ୍ୟାନୁଷ୍ଠାନ ଗ୍ରହଣ କରିବା ପାଇଁ ନିଷ୍ପତ୍ତି ନିଆଯାଇଛି । ବିଶେଷକରି ଅପ୍ରୀତିକର ପରିସ୍ଥିତି ସୃଷ୍ଟି କରୁଥିବା ପାକିସ୍ତାନୀ ସେନାକୁ ଦୃଢ଼ ଜବାବ ଦେବାପାଇଁ ନିଷ୍ପତ୍ତି ନିଆଯାଇଛି । ସେହିଭଳି ଜାମ୍ମୁ-କାଶ୍ମୀରରେ ହିଂସାତ୍ମକ କାର୍ଯ୍ୟକଳାପରେ ଲିପ୍ତ ରହୁଥିବା ସଂଗଠନଗୁଡ଼ିକ ବିରୋଧରେ ମଧ୍ୟ ଦୃଢ଼ କାର୍ଯ୍ୟାନୁଷ୍ଠାନ ଗ୍ରହଣ କରାଯିବାକୁ ବୈଠକରେ ନିଷ୍ପତ୍ତି ହୋଇଥିବା ସୂଚନା ମିଳିଛି ।','2016_11_02_21_53_07_Raj.jpg','Newstory','27-01-2017','#000000','',''),(40,'ବିଜେପିର ପରିବର୍ତ୍ତନ ଯାତ୍ରା','ୟୁପିରୁ ଶୁଭାରମ୍ଭ କରିବେ ଅମିତ ଶାହା','ବାରାଣସୀ : ଆସନ୍ତା ବର୍ଷ ଉତ୍ତର ପ୍ରଦେଶ ନିର୍ବାଚନ ଅନୁଷ୍ଠିତ ହେବାକୁ ଯାଉଥିବାବେଳେ ଏହାକୁ ଦୃଷ୍ଟିରେ ରଖି ବିଜେପି ଏଠାରୁ ପରିବର୍ତ୍ତନ ଯାତ୍ରାର ଶୁଭାରମ୍ଭ କରିବାକୁ ନିଷ୍ପତ୍ତି ନେଇଛି । ନଭେମ୍ବର ୮ରୁ ଉତ୍ତର ପ୍ରଦେଶର ସୋନଭଦ୍ର ଜିଲ୍ଲାରୁ ବିଜେପିର ପରିବର୍ତ୍ତନ ଯାତ୍ରା ଶୁଭାରମ୍ଭ କରିବେ ଦଳର ରାଷ୍ଟ୍ରୀୟ ସଭାପତି ଅମିତ ଶାହା । ରାଜନୈତିକ ଦୃଷ୍ଟିକୋଣରୁ ଉତ୍ତର ପ୍ରଦେଶ ଅତ୍ୟନ୍ତ ମହତ୍ତ୍ୱପୂର୍ଣ୍ଣ ହୋଇଥିବାରୁ ବିଜେପି ଏହି ରାଜ୍ୟରେ ପ୍ରାୟ ଦେଢ଼ ଦଶନ୍ଧି ପରେ ପୁନର୍ବାର କ୍ଷମତାକୁ ଫେରିବା ଲକ୍ଷ୍ୟରେ ଏହି ଯାତ୍ରା କରି ଭୋଟରଙ୍କୁ  ହାତେଇବା ଲକ୍ଷ୍ୟରେ ରହିଛି । ଏହି ଯାତ୍ରା ପାଇଁ ରାଜ୍ୟ କାର୍ଯ୍ୟକର୍ତ୍ତାଙ୍କର ଏକ ସମୀକ୍ଷା ବୈଠକ ଅନୁଷ୍ଠିତ ହୋଇଯାଇଛି । ସମୀକ୍ଷା ବୈଠକରେ ବିଜେପି ନେତା ରତ୍ନାକର ଶାହା ସୂଚନା ଦେଇ କହିଛନ୍ତି ଯେ, ସୋନଭଦ୍ର ଜିଲ୍ଲାର ରବର୍ଟସ୍ଗଞ୍ଜଠାରୁ ଦଳ ଏହି ଯାତ୍ରାର ଶୁଭାରମ୍ଭ କରିବ । ଏଥିରେ ଏକଲକ୍ଷରୁ ଅଧିକ ଲୋକ ସାମିଲ ହେବାର ସମ୍ଭାବନା ରହିଛି ବୋଲି ସେ ସୂଚନା ଦେଇଛନ୍ତି ।','2016_11_02_21_55_25_amit.jpg','Newstory','27-01-2017','#000000','',''),(41,'ଦୂତାବାସରେ ଅଛନ୍ତି ଆହୁରି ୧୬ ଗୁପ୍ତଚର','ପାକ୍ ସ୍ପାଇର ଚାଞ୍ଚଲ୍ୟକର ବୟାନ','ନୂଆଦିଲ୍ଲୀ : ପାକିସ୍ତାନୀ ଗୁଇନ୍ଦା ଏଜେନ୍ସି ଆଇଏସ୍ଆଇ ପାଇଁ ଗୁପ୍ତଚର କାମ କରି ଧରା ପଡ଼ିଥିବା ପାକିସ୍ତାନ ଦୂତାବାସ କର୍ମଚାରୀ ମେହମୁଦ ଅଖତାର ଏକ ଚାଞ୍ଚଲ୍ୟକର ବୟାନ ଦେଇ ପାକ୍ ଦୂତାବାସରେ କାର୍ଯ୍ୟରତ ଥିବା ୧୬ଜଣ କର୍ମଚାରୀ ଗୁପ୍ତଚର କାର୍ଯ୍ୟ କରୁଥିବା ପ୍ରକାଶ କରିଛି । ଜଣେ ବରିଷ୍ଠ ପୋଲିସ୍ ଅଧିକାରୀ ସୂଚନା ଦେଇଛନ୍ତି ଯେ, ମଙ୍ଗଳବାର ଦିନ ପଚରାଉଚରା ସମୟରେ ଅଖତାର ଏହି ପର୍ଦ୍ଦାଫାସ୍ କରିବା ସହ ଗୁପ୍ତଚର କାର୍ଯ୍ୟରେ ଲିପ୍ତ ରହିଥିବା ପାକ୍ ଦୂତାବାସର ଅନ୍ୟ ୧୬ଜଣ କର୍ମଚାରୀଙ୍କ ନାମ ପ୍ରକାଶ କରିଥିବା କହିଛି । ଏମାନେ ଭାରତୀୟ ସେନା, ବିଏସ୍ଏଫ୍ ସମ୍ବନ୍ଧିତ ଗୁପ୍ତ ସୂଚନା ଏକାଠି କରିବା ପାଇଁ ଉଦ୍ୟମ କରୁଥିଲେ ବୋଲି ଅଖତର ସୂଚନା ଦେଇଛି । ସୂଚନାଯୋଗ୍ୟ ଯେ, ଭାରତ ସରକାର ଗୁପ୍ତଚର କାର୍ଯ୍ୟ କରୁଥିବା ଅଭିଯୋଗରେ ଗିରଫ ହୋଇଥିବା ଅଖତରଙ୍କୁ ଦେଶରୁ ନିଷ୍କାସିତ କରିଛନ୍ତି । \r\nପୋଲିସ ଅଧିକାରୀ ଜଣକ ଆହୁରି କହିଛନ୍ତି ଯେ, ଅଖତାରର ବୟାନ ପରେ ମାମଲାର ତଦନ୍ତ ଜାରି ରହିଛି, ଯଦି ଏହା ସତ୍ୟ ପ୍ରମାଣିତ ହୁଏ ତେବେ ପରବର୍ତ୍ତୀ କାର୍ଯ୍ୟାନୁଷ୍ଠାନ ପାଇଁ ବୈଦେଶିକ ମନ୍ତ୍ରଣାଳୟକୁ ଲେଖାଯିବ । ଉଲ୍ଲେଖଯୋଗ୍ୟ ଯେ, ଏହି ମାମଲାର ତନ୍ତ କରୁଥିବା କ୍ରାଇମ୍ବ୍ରାଞ୍ଚ ଟିମ୍ ଅଖତାରକୁ ଗୁପ୍ତ ସୂଚନା ପ୍ରଦାନ କରିଥିବା ବ୍ୟକ୍ତିଙ୍କୁ ଗିରଫ କରିବା ପାଇଁ ରାଜସ୍ଥାନର ବିଭିନ୍ନ ଅଞ୍ଚଳରେ ଚଢ଼ାଉ ଜାରି ରଖିଥିବା ସୂଚନା ମିଳିଛି । ଏକ ସୂତ୍ରରୁ ମିଳିଥିବା ଖବର ଅନୁସାରେ କେତେକ ଅବସରପ୍ରାପ୍ତ ସରକାରୀ ଅଧିକାରୀ ଗୁପ୍ତଚରଙ୍କ ସଂପର୍କରେ ରହିଥିବା କ୍ରାଇମ୍ବ୍ରାଞ୍ଚ ସନ୍ଦେହ କରୁଛି । ଉଲ୍ଲେଖଯୋଗ୍ୟ ଯେ, ଆଇଏସ୍ଆଇ ପାଇଁ ଗୁପ୍ତଚର କାର୍ଯ୍ୟ କରୁଥିବା ଅଭିଯୋଗରେ ପୋଲିସ୍ ପାକ୍ ଦୂତାବାସ କର୍ମଚାରୀ ଅଖତରଙ୍କ ସହ ସମାଜବାଦୀ ପାର୍ଟି ସାଂସଦ ମୁନବର ସଲିମ୍ଙ୍କ ପର୍ସନାଲ ଆସିଷ୍ଟାଣ୍ଟ ଫରହତ୍ ଖାଁ, ରାଜସ୍ଥାନର ନାଗୌର ନିବାସୀ ରମଜାନ ଓ ଜାହାଙ୍ଗୀରଙ୍କୁ ଗିରଫ କରିଥିଲା । ଏହାପରେ ଜୋଧପୁରରୁ ସୋଏବ ନାମକ ଅନ୍ୟ ଜଣେ ଅଭିଯୁକ୍ତକୁ ଗିରଫ କରାଯାଇଥିଲା ।','2016_11_02_21_57_12_Pak.jpg','Newstory','27-01-2017','#000000','',''),(42,'ବିଶ୍ୱସନୀୟତା ଗଣମାଧ୍ୟମ ପାଇଁ ଏକ ବୃହତ ଚ୍ୟାଲେଞ୍ଜ: ପ୍ରଧାନମନ୍ତ୍ରୀ',NULL,'ନୂଆଦିଲ୍ଲୀ : ବର୍ତ୍ତମାନର ବୈଷୟିକ ଯୁଗରେ ବିଶ୍ୱସନୀୟତା ଗଣମାଧ୍ୟମ ପାଇଁ ଏକ ବୃହତ ଚ୍ୟାଲେଞ୍ଜ ବୋଲି କହିଛନ୍ତି ପ୍ରଧାନମନ୍ତ୍ରୀ ନରେନ୍ଦ୍ର ମୋଦି । ଦୈନିକ ଇଂରାଜୀ ସମ୍ବାଦପତ୍ର ଦି ଇଣ୍ଡିଆନ୍ ଏକ୍ସପ୍ରେସ୍ ଦ୍ୱାରା ଆୟୋଜିତ ରାମନାଥ ଗୋଏଙ୍କା ଏକ୍ସେଲେନ୍ସେସ୍ ଇନ୍ ଜର୍ଣ୍ଣାଲିଜିମ୍ ଆୱାର୍ଡ ସମାରୋହରେ ପୁରସ୍କାର ବିତରଣ କରିବା ପରେ ଉଦ୍ବୋଧନ ଦେଇ ମୋଦି ଏହା କହିଛନ୍ତି । ସେ କହିଲେ ଯେ, ପୂର୍ବରୁ ପ୍ରଶିକ୍ଷିତ ଏବଂ ଯୋଗ୍ୟତା ଥିବା ଲୋକମାନେ ସାମ୍ବାଦିକତା କରୁଥିଲେ । ଏବେ ମୋବାଇଲ୍ ଫୋନ୍ରେ ଯେକୌଣସି ମୁହୂର୍ତ୍ତରେ ଫଟୋ ଉଠାଯାଇ ତାଙ୍କୁ ଅପ୍ଲୋଡ୍ କରାଯାଇ ପାରୁଛି ।  ସେ କହିଲେ ଯେ, ଲୋକମାନଙ୍କ ପାଖକୁ ଏବେ ବହୁତ ଖବର ଆସୁଛି । ଏଭଳି ସ୍ଥିତିରେ ବିଶ୍ୱସନୀୟତା ବଜାୟ ରଖିବା ଏକ ବଡ ପ୍ରସଙ୍ଗ ଏବଂ ବର୍ତ୍ତମାନର ଏକ ବୃହତ ଆବଶ୍ୟକତା । ମୋଦି କହିଲେ ଯେ, ଗଣମାଧ୍ୟମ ପାଖରେ ପ୍ରତ୍ୟେକ ଜିନିଷ ଏବଂ ପ୍ରତ୍ୟେକ ବ୍ୟକ୍ତିଙ୍କ ଉପରେ ଟିପ୍ପଣୀ ଦେବାର ସ୍ୱାଧୀନତା ରହିଛି, ତେବେ ଗଣମାଧ୍ୟମ ଉପରେ ଦିଆଯାଉଥିବା ଟିପ୍ପଣୀକୁ ଏହା ପସନ୍ଦ କରୁନାହିଁ । ମୋଦି ଆହୁରି କହିଲେ ଯେ, ସ୍ୱାଧୀନତା ପରେ ସେ ଏଭଳି ଜଣେ ନେତା ଯାହା ସଂପର୍କରେ ଗଣମାଧ୍ୟମରେ ସର୍ବାଧିକ ଚର୍ଚ୍ଚା ହୋଇ ଆସିଛି । ଏଥିପାଇଁ ସେ ସବୁବେଳେ ଗଣମାଧ୍ୟମ ପାଖରେ କୃତଜ୍ଞ ରହିବେ ବୋଲି କହିଛନ୍ତି । ମୋଦି ଗଣମାଧ୍ୟମ ଆଗରେ ଦୁଇଟି ପ୍ରସଙ୍ଗ ଉପସ୍ଥାପନ କରି କହିଲେ ଯେ, ଗଣମାଧ୍ୟମ ଦ୍ୱାରା ସରକାରଙ୍କୁ କରାଯାଉଥିବା ସମାଲୋଚନାକୁ ନେଇ ସେ ଆଦୌ ଚିନ୍ତିତ ନୁହନ୍ତି, କିନ୍ତୁ ସଠିକ୍ ଖବର  ପ୍ରଦାନ କରାଯିବାରେ ତ୍ରୁଟି ନହେବା ଆବଶ୍ୟକ । ମୋଦି କହିଲେ ଯେ, ରାଷ୍ଟ୍ରୀୟ ଏକତାକୁ ପ୍ରାଥମିକତା ଦିଆଯିବା ଆବଶ୍ୟକ କାରଣ ଭାରତ ‘ବିଭିନ୍ନତା ମଧ୍ୟରେ ଏକତା’ ଥିବା ଦେଶ । ନେତମାନେ ବହୁ ତ୍ରୁଟି କରୁଥାଇ ପାରନ୍ତି, ତେବେ ଆପଣମାନେ ରାଷ୍ଟ୍ରୀୟ ଏକତାର ଶକ୍ତିକୁ ମଜଭୁତ କରନ୍ତି ବୋଲି ମୋଦି କହିଥିଲେ । ଏହି ଅବସରରେ ସେ ବିଶ୍ୱବ୍ୟାପୀ ବିଭିନ୍ନ ଦେଶର ଖବର ବିଶ୍ୱନୀୟତାର ସହ ପ୍ରକାଶ କରିବା ପାଇଁ ଭାରତୀୟ ଗଣମାଧ୍ୟମ ସଂଗଠନର ନିର୍ମାଣ ପାଇଁ ଆହ୍ୱାନ କରିଥିଲେ । ମୋଦି କହିଲେ ଯେ, ସବୁ ବୃହତ ଦେଶଗୁଡ଼ିକ ବୌଦ୍ଧିକସ୍ତରରେ ଏକ ମଜଭୁତ ଗଣମାଧ୍ୟମ ସ୍ୱର ନିର୍ମାଣ କରିବା ଦିଗରେ କାର୍ଯ୍ୟ କରୁଛନ୍ତି । ଭାରତ ପାଇଁ ଏହା ଏକ ଚ୍ୟାଲେଞ୍ଜ ବୋଲି ସେ କହିଛନ୍ତି । ଏହି ଅବସରରେ ମୋଦି ପ୍ରିଣ୍ଟି, ଟେଲିଭିଜନ ଏବଂ ଡିଜିଟାଲ ମିଡିଆର ବିଭିନ୍ନ କ୍ଷେତ୍ରରେ ସଫଳତା ହାସଲ କରିଥିବା ସାମ୍ବାଦିକମାନଙ୍କୁ ପୁରସ୍କୃତ କରିଥିଲେ ।','2016_11_03_23_24_22_Modi.jpg','Newstory','27-01-2017','#000000','',''),(43,'ଜାପାନଠାରୁ ହାଇଟେକ୍ ବିମାନ କିଣିବ ଭାରତ',NULL,'ନୂଆଦିଲ୍ଲୀ : ଭାରତ ଖୁବ୍ ଶୀଘ୍ର ଜାପାନଠାରୁ ହାଇଟେକ୍ ୧୨ଏମ୍ଫିବିୟନ୍ ବିମାନ ୟୁଏସ୍-୨ଆଇ କ୍ରୟ କରିବ ବୋଲି ସୂଚନା ମିଳିଛି । ଗଣମାଧ୍ୟମ ରିପୋର୍ଟ ଅନୁସାରେ ଚଳିତ ମାସ ୧୧ ଓ ୧୨ ତାରିଖରେ ପ୍ରଧାନମନ୍ତ୍ରୀ ମୋଦିଙ୍କ ପ୍ରସ୍ତାବିତ ଦୁଇଦିନିଆ ଜାପାନ ଯାତ୍ରା ଅବସରରେ ଏହି ପ୍ରସଙ୍ଗ ଉପରେ ଚୂଡାନ୍ତ ନିଷ୍ପତ୍ତି ନିଆଯିବ । ଭାରତ-ଜାପାନ ମଧ୍ୟରେ ଏହି ଡିଲ୍କୁ ନେଇ ୨୦୧୩ରୁ କଥାବାର୍ତ୍ତା ଜାରି ରହିଛି କିନ୍ତୁ ଦର ଅଧିକ ହୋଇଥିବା କାରଣରୁ ଡିଲ୍ ଅଟକି ରହିଛି । ଏବେ ଜାପାନ ଏହାର ଦରରେ ୭୨୦କୋଟି କମ୍ କରି ଦେଇଥିବା ସୂଚନା ମିଳିଛି । ସରକାରୀ ଅଧିକାରୀଙ୍କ ସୂତ୍ରରୁ ମିଳିଥିବା ଖବର ଅନୁସାରେ ପ୍ରଧାନମନ୍ତ୍ରୀ ମୋଦି ଜାପାନ ଗସ୍ତ ଅବସରରେ ପ୍ରତିପକ୍ଷ ଜାପାନ ପ୍ରଧାନମନ୍ତ୍ରୀ ଶିଂଜୋ ଆବେଙ୍କ ସହ ସିଭିଲ୍ ନ୍ୟୁକ୍ଲିୟର କୋଅପରେସନ୍ ଏଗ୍ରିମେଣ୍ଟ ଚର୍ଚ୍ଚା କରିବାର କାର୍ଯ୍ୟକ୍ରମ ରହିଛି । ଯାହାକି ଅତ୍ୟନ୍ତ ଗୁରୁତ୍ୱପୂର୍ଣ୍ଣ । ସେହିଭଳି ଏହି ଅବସରରେ ଉଭୟ ଦେଶର ନେତାଙ୍କ ମଧ୍ୟରେ ୟୁଏସ୍-୨ଆଇ ବିମାନର ଡିଲ୍ ଉପରେ ମଧ୍ୟ ମୋହର ଲାଗିବାର ଯଥେଷ୍ଟ ସମ୍ଭାବନା ରହିଛି । ଯଦି ଏହା ହୁଏ ତେବେ ଭାରତ-ଜାପାନ ମଧ୍ୟରେ କୌଣସି ପ୍ରକାର ପ୍ରତିରକ୍ଷା ସରଞ୍ଜାମର ଏହା ପ୍ରଥମ ଡିଲ୍ ହେବ । ପ୍ରାୟ ୫ ଦଶନ୍ଧି ଧରି ଜାପାନ ପ୍ରତିରକ୍ଷା ସରଞ୍ଜାମ ରପ୍ତାନୀକୁ ବ୍ୟାନ୍ କରିଥିଲା । ତେବେ ଏବେ ଜାପାନ ଭାରତକୁ ମିଲିଟାରୀ ସରଞ୍ଜାମ ଯୋଗାଇ ଦେବାକୁ ରାଜି ଥିବା ସୂଚନା ମିଳିଛି । \r\nଦକ୍ଷିଣ ଏସିଆରେ ଚୀନର ପ୍ରଭାବ ବୃଦ୍ଧି କାରଣରୁ ଭାରତ ଏବଂ ଜାପାନ ହାତ ମିଳାଇ ସେମାନଙ୍କର ସହଯୋଗୀ ଦେଶ ଆମେରିକା ଏବଂ ଅଷ୍ଟ୍ରେଲିଆ ସହ ମିଶି ଏକ ନୂତନ ଶକ୍ତି ସୃଷ୍ଟି କରିବାକୁ ଚାହୁଁଥିବା ଜଣାପଡ଼ିଛି । ଚୀନର ପ୍ରଭାବ ହ୍ରାସ କରିବାକୁ ଉଭୟ ଭାରତ ଏବଂ ଜାପାନ ଏକତ୍ର ହୋଇ ଆଗକୁ ବଢ଼ିବାକୁ ରାଜିଥିବା ଜଣାପଡ଼ିଛି । ଏହି ପରିପ୍ରେକ୍ଷୀରେ ୟୁଏସ୍-୨ଆଇ ପ୍ରତିରକ୍ଷା ବିମାନ ଡିଲ୍ ଅତ୍ୟନ୍ତ ଗୁରୁତ୍ୱପୂର୍ଣ୍ଣ । ଜାପାନ ଦ୍ୱାରା ପ୍ରସ୍ତୁତ କରାଯାଉଥିବା ଏହି ବିମାନ ଏକ ହାଇଟେକ୍ ବିମାନ ଯାହାକି ଉଭୟ ସ୍ଥଳ ଏବଂ ଜଳପଥରୁ ଉଡ଼ିପାରିବ ଏବଂ ଅବତରଣ କରିପାରିବ । ଏହାକୁ ଅନୁସନ୍ଧାନ କାର୍ଯ୍ୟରେ ନିୟୋଜିତ କରାଯାଇପାରିବ ଏବଂ ଜରୁରୀ ପରିସ୍ଥିତିରେ ଏହି ବିମାନ ୩୦ଜଣ ଯବାନଙ୍କୁ ଘଟଣାସ୍ଥଳକୁ ନେଇଯାଇପାରିବ ।  ଏହି ବିମାନ କ୍ରୟ କରିବା ପାଇଁ ଭାରତ ‘ମେକ୍ ଇଣ୍ଡିଆ’ ସର୍ତ୍ତ ରଖିଛି । ଅର୍ଥାତ୍ ଜାପାନ ଏହାକୁ ଭାରତରେ ସ୍ଥାନୀୟ ପାର୍ଟନରଙ୍କ ସହ ନିର୍ମାଣ କରିବାକୁ ପଡିବ । ଏଥିରେ ଜାପାନ ରାଜିଥିବା ସୂଚନା ମିଳିଛି । ଯଦି ଏହି ଡିଲ୍ ସଫଳ ହୁଏ ତେବେ ଭାରତର ପ୍ରତିରକ୍ଷା ଶକ୍ତି ମଜଭୁତ ହୋଇପାରିବ ।','2016_11_05_23_19_50_US2.jpg','Newstory','27-01-2017','#000000','',''),(44,'ବିଷ ବଳୟରେ ଦିଲ୍ଲୀ','ପ୍ରଦୂଷଣ କାରଣରୁ ୧୮୦୦ ସ୍କୁଲ୍ ବନ୍ଦ,ଭରତରେ ପ୍ରଥମ ଘଟଣା','ନୂଆଦିଲ୍ଲୀ :ଦିଲ୍ଲୀରେ ପ୍ରଦୂଷଣ ନିୟନ୍ତ୍ରଣ ବାହାରକୁ ଚାଲିଯିବା ପରେ ନଗର ନିଗମ ଐତିହାସିକ ନିଷ୍ପତ୍ତି ନେଇ ଶନିବାର ଦିନ ସ୍କୁଲ୍ ବନ୍ଦ କରିବାକୁ ନିର୍ଦ୍ଦେଶ ଦେଇଛନ୍ତି । ପ୍ରଦୂଷଣ କାରଣରୁ ସ୍କୁଲ୍ ବନ୍ଦ ରହିବା ଏହା ପ୍ରଥମ ଘଟଣା । ମହାନଗର ନିଗମର ଏହି ନିଷ୍ପତ୍ତି ଫଳରେ ଦିଲ୍ଲୀର ୧୮୦୦ଟି ସ୍କୁଲ୍ ବନ୍ଦ ରହିଥିବା ସୂଚନା ମିଳିଛି । ଫଳରେ  ଦକ୍ଷିଣ ଦିଲ୍ଲୀ, ଉତ୍ତର ଦିଲ୍ଲୀ ଏବଂ ପୂର୍ବ ଦିଲ୍ଲୀ ନଗର ନିଗମର ୧୦ଲକ୍ଷ ଛାତ୍ର ଶନିବାର ଦିନ ସ୍କୁଲ୍ ପାଠପଢ଼ାରୁ ବଞ୍ଚିତ ହୋଇଛନ୍ତି । ଗତବର୍ଷ ପ୍ରଦୂଷଣ କାରଣରୁ ସ୍କୁଲ୍ର ପାଠପଢ଼ା ସମୟକୁ ତିନିଘଣ୍ଟା କମ୍ କରିଦିଆଯାଇଥିଲା । କିନ୍ତୁ ଚଳିତବର୍ଷ ସ୍କୁଲ୍ ସଂପୂର୍ଣ୍ଣ ବନ୍ଦ ରଖାଯାଇଛି ଯାହାକି ପ୍ରଥମ ଘଟଣା । ପୂର୍ବରୁ କେବେ ପ୍ରଦୂଷଣ କାରଣରୁ ସ୍କୁଲ୍ ବନ୍ଦ କରିବାକୁ ପଡ଼ିନଥିଲା । ପ୍ରକାଶଯୋଗ୍ୟ ରାଜଧାନୀ ଦିଲ୍ଲୀରେ ୧୭ବର୍ଷ ମଧ୍ୟରେ ସର୍ବାଧିକ ପ୍ରଦୂଷଣ ଦେଖାଯାଇଛି । ଭୟଙ୍କର ଧୂଅାଁ ରାଜଧାନୀକୁ ଘେରି ରହିଛି । ଏହି କାରଣରୁ ଶନିବାର ଦିନ ସ୍କୁଲ୍ ବନ୍ଦ ରଖାଯିବାକୁ ନଗରନିଗମ ବାଧ୍ୟ ହୋଇଛି । ଯଦି ପରିସ୍ଥିତିରେ ସୁଧାର ନଆସେ ତେବେ ଛୁଟି ବୃଦ୍ଧି ହେବାର ସମ୍ଭାବନା ରହିଥିବା ଜଣାପଡ଼ିଛି । \r\nଦିଲ୍ଲୀରେ ପ୍ରଦୂଷଣ ବୃଦ୍ଧିକୁ ନିୟନ୍ତ୍ରଣ କରିବା ପାଇଁ କେନ୍ଦ୍ର ଓ ଦିଲ୍ଲୀ ସରକାର କୌଣସି ପଦକ୍ଷେପ ଗ୍ରହଣ କରୁନଥିବା ଦର୍ଶାଇ ନ୍ୟାସନାଲ୍ ଗ୍ରୀନ୍ ଟ୍ରିବ୍ୟୁନାଲ୍ (ଏନ୍ଜିଟି) ଏହାର ନିନ୍ଦା କରିଛି । ଏଥିସହ ଏନ୍ଜିଟି ଦିଲ୍ଲୀ ସରକାର ଓ କେନ୍ଦ୍ର ସରକାରଙ୍କୁ ତୀବ୍ର ଭର୍ତ୍ସନା କରିଛି । ପ୍ରଦୂଷଣ ରୋକିବା ପାଇଁ କି ପଦକ୍ଷେପ ନିଆଯାଇଛି ବୋଲି ସରକାରଙ୍କୁ ପ୍ରଶ୍ନ କରିଛନ୍ତି ଏନ୍ଜିଟି । ଏଥିସହ ୧୦ବର୍ଷ ପୁରୁଣା ଡିଜେଲ୍ ଯାନବାହନକୁ ବନ୍ଦ କରିବା ପାଇଁ ଦିଲ୍ଲୀ ସରକାରଙ୍କୁ ଏନ୍ଜିଟି ନିର୍ଦ୍ଦେଶ ଦେଇଛନ୍ତି । ଦିଲ୍ଲୀରେ ପ୍ରଦୂଷଣ ମାତ୍ର ବୃଦ୍ଧି ପାଇବାକୁ ନେଇ ଏନ୍ଜିଟି ଚାରୋଟି ରାଜ୍ୟ ଦିଲ୍ଲୀ, ହରିୟାଣା, ପଞ୍ଜାବ ଏବଂ ରାଜସ୍ଥାନ ସରକାରଙ୍କ ସଚିବଙ୍କୁ ସମନ୍ କରିଛନ୍ତି ।','2016_11_05_23_30_09_Polution.png','hotNews','27-01-2017','#000000','',''),(45,'ବନ୍ଧୁଙ୍କ ସହ ମିଶି ପ୍ରେମିକାଙ୍କୁ ଗଣଦୁଷ୍କର୍ମ',NULL,'ନୂଆଦିଲ୍ଳୀ: ଦିଲ୍ଳୀର ଜନୈକ ୧୫ ବର୍ଷୀୟା ନାବାଳିକାଙ୍କୁ ଗଣଦୁଷ୍କର୍ମ କରିଥିବା ଅଭିଯୋଗରେ ତିନି ଜଣ ଯୁବକଙ୍କୁ ପୁଲିସ୍‌ ଗିରଫ କରିଛି। ପ୍ରେମିକ ଜଣକ ସାତ ଜଣଙ୍କ ସହ ମିଶି ନାବାଳିକାକୁ ଗଣ ଦୁଷ୍କର୍ମ କରିଥିଲେ । ତିନି ଜଣ ପୁଲିସ୍‌ ହାତରେ ଧରା ପଡ଼ିଥିବା ବେଳେ ଅନ୍ୟ ଚାରି ଜଣ ଫେରାର୍‌ ଅଛନ୍ତି।\r\n ଗତ ଦେଢ଼ ବର୍ଷ ହେବ ରେହାନ ନାମକ ଜଣେ ଯୁବକଙ୍କ ସହ ପୀଡ଼ିତାଙ୍କର ପ୍ରେମ ସଂପର୍କ ରହି ଆସିଥିଲା। ମାତ୍ର ଯୁବକ ଜଣଙ୍କର ସ୍ୱଭାବ ଓ ବ୍ୟବହାର ଭଳ ନଥିବା କାରଣରୁ ଏହି ସଂପର୍କକୁ ନାପସନ୍ଦ କରିଥିଲେ ନାବାଳିକାର ବାପାମାଆ। ଶୁକ୍ରବାର ଦିନ ୪ଟା ବେଳେ ରେହାନ ସଂପୃକ୍ତା ନାବାଳିକାଙ୍କୁ ଏକ ଫିଲ୍ମ ଯିବା କଥା କହି ନିଜ ଘରକୁ ଡାକିଥିଲା। ମାତ୍ର ସେଠାରୁ ସେ ଫିଲ୍ମ ହଲ୍‌କୁ ନେବା ପରିବର୍ତ୍ତେ ତାଙ୍କୁ ନେଇ ଯାଇଥିଲା ପାହାଡ଼ଗଞ୍ଜରେ ଥିବା ଏକ ହୋଟେଲକୁ। ସେଠାରେ ଯୁବକର ଅନ୍ୟ ୬ ଜଣ ବନ୍ଧୁ ମଧ୍ୟ ଉପସ୍ଥିତ ଥିଲେ। ପୀଡ଼ିତାଙ୍କ ବୟାନ ମୁତାବକ, ଯୁବକ ଓ ତା’ର ବନ୍ଧୁମାନେ ମିଶି ନାବାଳିକାକୁ ବଳପୂର୍ବକ ମଦ୍ୟ ପାନ କରାଇବା ପରେ ତା’ ସହ ଗଣଦୁଷ୍କର୍ମ କରିଥିଲେ।\r\nଏହି ଦୁଷ୍କର୍ମ ବ୍ୟତୀତ ରେହାନଙ୍କ ନାମରେ ପୂର୍ବରୁ ଚୋରି, ଡାକୟତି ପରି ଅନେକ ଅନ୍ୟ ଅଭିଯୋଗ ମଧ୍ୟ ପୁଲିସ ପାଖରେ ରହିଛି। ତେବେ ଏହି ଘଟଣାରେ ଲିପ୍ତ ଥିବା ଅଭିଯୁକ୍ତମାନଙ୍କୁ ପୁଲିସ୍‌ ଧରିବା ସହ ତାଙ୍କ ବିରୋଧରେ ଆପିିସି ସେକ୍‌ସନ୍‌ର ୩୭୬(କ) ଦଫା ଲଗାଇଛି। ଫେରାର୍‌ ହୋଇଥିବା ଅଭିଯୁକ୍ତମାନଙ୍କୁ ଧରିବା ପାଇଁ ପୁଲିସ୍‌ ଯୋରସୋର ଚଢ଼ାଉ ଜାରି ରଖିଛି।','2016_11_07_01_04_28_gang.jpg','Newstory','27-01-2017','#000000','',''),(46,'ଆମେରିକାରେ ସଂହାର କରିବ ଆଇଏସ୍',NULL,'ୱଶିଂଟନ:ନିର୍ବାଚନ ପୁର୍ବରୁ ଆମେରିକାରେ ନର ସଂହାର କରିବାକୁ ଆଇଏସ୍ ଆତଙ୍କବାଦୀ ଟାର୍ଗେଟ କରିଛନ୍ତି ।ନିର୍ବାଚନ ଦିନ  ଆମେରିକୀୟ ଭୋଟରମାନଙ୍କୁ ହତ୍ୟା କରିବାକୁ ଧମକ ଦେବା ସହ ମୁସ୍‌ଲିମମାନେ ଭୋଟ୍‌ ପ୍ରକ୍ରିୟାରେ ଭାଗ ନ ନେବାକୁ ଆଇଏସ୍‌ କହିଛି।   ଆଇଏସ୍‌ ଏହାର ଏକ ସମ୍ବାଦପତ୍ର ‘ଅଲ୍‌ ହାୟତ୍‌’ରେ ଏପରି ଧମକ ଦେଇଛି। ଆକେମରିକାର ଉଭୟ ଡେମୋକ୍ରାଟିକ ଓ ରିପବ୍ଲିକାନ ପାର୍ଟି ମୁସ୍‌ଲିମ ବିରୋଧୀ ହୋଇଥିବା ଆଇଏସ୍‌ କହିଛି। ଆଇଏସ୍‌ ଆତଙ୍କବାଦୀମାନେ  ଆମେରିକା ନିର୍ବାଚନକୁ ଭଣ୍ଡୁର କରିବା ସହ ହତ୍ୟାର ତାଣ୍ଡବ  ସୃଷ୍ଟି କରିବେ ବୋଲି ଏଥିରେ କୁହାଯାଇଛି। ସାତ ପୃଷ୍ଠା ବିଶିଷ୍ଟ ଆଇଏସ୍‌ର ଏହି ଧମକପୂର୍ଣ୍ଣ ବାର୍ତ୍ତାରେ ରିପବ୍ଲିକାନ ରାଷ୍ଟ୍ରପତି ପ୍ରାର୍ଥୀ ଡୋନାଲଡ ଟ୍ରମ୍ପ ଓ ଡେମୋକ୍ରାଟିକ  ଉପରାଷ୍ଟ୍ରପତି ପ୍ରାର୍ଥୀଙ୍କ ଫଟୋ ରହିଛି। ଆମେରିକା ନିର୍ବାଚନ ପ୍ରକ୍ରିୟାରେ ବାଧା ସୃଷ୍ଟି କରିବା ଏବଂ ଗଣମାଧ୍ୟମର ଧ୍ୟାନ ଆକର୍ଷଣ ପାଇଁ ଆଇଏସ୍‌ ଏପରି ଆକ୍ରମଣ ଧମକ ଦେଇଥିବା ଆମେରିକା ପ୍ରଶାସନ କହିଛି। ସୂଚନାଯୋଗ୍ୟ, ପୂର୍ବରୁ ଅଲକାଏଦା ମଧ୍ୟ ଆମେରିକା ରାଷ୍ଟ୍ରପତି ନିର୍ବାଚନରେ ଆକ୍ରମଣ ପାଇଁ ଧମକ ଦେଇଥିଲା। ଏହାକୁ ଦୃଷ୍ଟିରେ ରଖି ନିର୍ବାଚନ ସମୟରେ ଯେ କୌଣସି   ଆକ୍ରମଣକୁ ପ୍ରତିହତ କରିବା ପାଇଁ ସୁରକ୍ଷା ବ୍ୟବସ୍ଥା କଡ଼ାକଡ଼ି କରାଯାଇଛି।','2016_11_07_01_24_38_Is.jpg','Newstory','27-01-2017','#000000','',''),(47,'ରାହୁଳଙ୍କୁ ପଦୋନ୍ନତି ମିଳୁନି','ସୋନିଆ ରହିବେ ବସ୍','ନୂଆଦିଲ୍ଲୀ: ରାହୁଲ ଗାନ୍ଧୀଙ୍କୁ କଂଗ୍ରେସ ଦଳର ସଭାପତି ହେବାରୁ ସମ୍ଭବନା ବର୍ତ୍ତମାନ ଦେଖାଯାଉନାହିଁ । କାରଣ ଦଳରେ ସାଙ୍ଗଠନିକ ପ୍ରକ୍ରୀୟା ପୁଣି ଘୁଞ୍ଚିବାର ସମ୍ଭାବନା ସୃଷ୍ଟି ହୋଇଛି । ଏଥିପୂର୍ବରୁ ମଧ୍ୟ ସାଙ୍ଗଠନିକ ନିର୍ବାଚନ ଓ ପଦୋନ୍ନତି ପ୍ରକ୍ରୀୟାକୁ ଆସନ୍ତା ଡିସେମ୍ବର ଶେଷ ପର୍ଯ୍ୟନ୍ତ ଘୁଞ୍ଚାଇ ଦିଆଯାଇଥିଲା । ପଞ୍ଜାବ ଏବଂ ଉତ୍ତରପ୍ରଦେଶ ବିଧାନସଭା ନିର୍ବାଚନ ପରେ ଦଳରେ ସାଙ୍ଗଠନିକ ପରିବର୍ତ୍ତନ କରାଯିବା ପାଇଁ ଦଳର ବରିଷ୍ଠ ନେତୃବୃନ୍ଦ ବିଚାର କରୁଛନ୍ତି ।\r\nସ୍ୱଳ୍ପ ସମୟ ମଧ୍ୟରେ ଦଳରେ ସାଙ୍ଗଠନିକ ନିର୍ବାଚନ କରାଯିବା ସମ୍ଭବ ନଥିବାରୁ ଏହା ଅତିକମରେ ପୁଣି ଏକବର୍ଷ ଘୁଞ୍ଚିବାର ସୂଚନା ମିଳିଛି । ଆସନ୍ତାବର୍ଷ ପ୍ରଥମ ଭାଗରେ ଗୁରୁତ୍ୱପୂର୍ଣ୍ଣ ବିଧାନସଭା ନିର୍ବାଚନ ଅନୁଷ୍ଠିତ ହେବାକୁ ଯାଉଥିବାରୁ ଦଳ ବର୍ତ୍ତମାନ ତାହା ଉପରେ ଫୋକସ କରିବ ବୋଲି କୁହାଯାଉଛି । ସାଂପ୍ରତିକ ପରିସ୍ଥିତିରେ ରାହୁଳଙ୍କ ହାତରେ ଦାୟିତ୍ୱ ନଦେଇ ସୋନିଆ ଗାନ୍ଧୀଙ୍କୁ ସଭାପତି ପଦକୁ ଅବ୍ୟାହତ ରଖିବାକୁ ବହୁ ବରିଷ୍ଠ ନେତା ମତ ଦେଇଛନ୍ତି ।\r\nଆଜି ଅନୁଷ୍ଠିତ କଂଗ୍ରେସ କାର୍ଯ୍ୟକାରୀ କମିଟି ବୈଠକରେ ଏନଡିଟିଭି ଉପରେ ସରକାରଙ୍କ ଦିନିକିଆ କଟକଣା ଏବଂ ଆସନ୍ତା ୧୬ ଠାରୁ ଆରମ୍ଭ ହେବାକୁ ଥିବା ସଂସଦର ଶୀତ ଅଧିବେଶନର ରଣକୌଶଳ ଉପରେ ମୁଖ୍ୟତଃ ଚର୍ଚ୍ଚା ହେବାର ସମ୍ଭାବନା ରହିଛି ।\r\nଦଳରେ ସାଙ୍ଗଠନିକ ନିର୍ବାଚନର ଅବଧି ଅଦ୍ୟାବଧି ପୁରିଗଲାଣି । ସେହିଦୃଷ୍ଟିରୁ ଡିସେମ୍ବର ୩୧ ସୁଦ୍ଧା ନିର୍ବାଚନ ପ୍ରକ୍ରୀୟାକୁ ସଂପୂର୍ଣ୍ଣ କରିବା ପାଇଁ ଦଳ ନିର୍ବାଚନ କମିଶନରଙ୍କ ଠାରୁ ଅନୁମତି ଲୋଡିଥିଲା । ମାତ୍ର ଏହା ସମ୍ଭବ ହେଉନଥିବାରୁ ସେ ସଂପର୍କରେ ମଧ୍ୟ ସିଡବ୍ଲୁସିରେ ଚର୍ଚ୍ଚା ହେବ ବୋଲି ଶୁଣିବାକୁ ମିଳୁଛି । ଏହାଛଡା ସର୍ଜିକାଲ ଷ୍ଟ୍ରାଇକ୍, ୮ ସିମି ଆତଙ୍କବାଦୀ ଏନକାଉଣ୍ଟର ପ୍ରସଙ୍ଗ, ଏବଂ ଟ୍ରିପିଲ ତଲାକ ଉପରେ ମଧ୍ୟ ଆଲୋଚନା କରାଯିବ । ଦଳର ସଭାନେତ୍ରୀ ସୋନିଆ ଗାନ୍ଧୀଙ୍କ ସ୍ୱାସ୍ଥ୍ୟବସ୍ଥା ଭଲ ରହୁନଥିବାରୁ ଏହା ମଧ୍ୟ ଦଳ ପାଇଁ ଚିନ୍ତାର କାରଣ ପାଲଟିଛି ।','2016_11_07_23_30_39_Aicc.jpeg','Newstory','27-01-2017','#000000','','10'),(48,'ଦୁର୍ଗମ ଅଞ୍ଚଳରେ ଫେସ୍ ବୁକ୍ ଯୋଗାଇବ ଇଣ୍ଟରନେଟ',NULL,'ନୂଆଦିଲ୍ଲୀ: ଦେଶର ଦୂରଦୂରାନ୍ତ ଏବଂ ଦୁର୍ଗମ ଅଞ୍ଚଳରେ ଫେସବୁକ୍ ନିଜର ସୌରଶକ୍ତି ସହାୟତାରେ ଚାଲୁଥିବା ବିମାନ ‘ଅକିଲା’ ଯୋଗେ ଶସ୍ତାରେ ମୋବାଇଲ ଇଣ୍ଟରନେଟ ସେବା ଯୋଗାଇ ଦେବା ପାଇଁ ପ୍ରୟାସ ଆରମ୍ଭ କରିଛି । ବିଶ୍ୱର ସବୁଠୁ ବଡ଼ ସୋସିଆଲ ନେଟୱାର୍କ ଫେସବୁକ୍ ଏଥିପାଇଁ ଭାରତୀୟ ଟେଲିଫୋନ କମ୍ପାନୀ ଏବଂ ସରକାରଙ୍କ ସହ ପ୍ରାରମ୍ଭିକ କଥାବାର୍ତ୍ତା କରିଛି । \r\nଫେସ୍ ବୁକ୍ ଏଥିପାଇଁ ନିଜର ଏକ୍ସପ୍ରେସ ହାଇ-ୱେ ପ୍ରକଳ୍ପ ନିମନ୍ତେ ଟେଲିଫୋନ କମ୍ପାନୀ ମାନଙ୍କ ସହ ଟାଇଅପ୍ କରିଛି । ଫେସବୁକ୍ ର ନିର୍ଦ୍ଦେଶକ (କନେକ୍ଟିଭିଟି ପବ୍ଲିକ ପଲିସି) ରବର୍ଟ ପେପରଇଟି କହିଛନ୍ତିଯେ, ଏହାଦ୍ୱାରା ଗ୍ରାମାଞ୍ଚଳରେ ବ୍ରଡ଼ବ୍ର୍ୟାଣ୍ଡ ସେବା ଯୋଗାଇ ଦିଆଯିବ । ବିନା ଫାଇବର ଯୋଗେ କିପରି ପ୍ରତ୍ୟେକ ଲୋକ ଫାଇବର ପରି ସ୍ପିଡ଼ ପାଇ ପାରିବେ ସେଥିପାଇଁ ଟେଲିକମ୍ ଅପରେଟର୍ସ ମାନଙ୍କ ସହ ଆଲୋଚନା କରାଯାଇଛି । \r\nଯେଉଁ ଟେଲିକମ୍ କମ୍ପାନୀମାନେ କମ୍ ଚାହିଦା କାରଣରୁ ଗ୍ରାମାଞ୍ଚଳରେ ଇଣ୍ଟରନେଟ୍ ସେବା ପାଇଁ ଭିତ୍ତିଭୂମି ନିର୍ମାଣ କ୍ଷେତ୍ରରେ ଅସୁବିଧାର ସମ୍ମୁଖିନ ହେଉଛନ୍ତି, ସେମାନେ ମୋବାଇଲ ବ୍ରଡ଼ବ୍ୟାଣ୍ଡ ସେବା ଯୋଗାଇ ଦେବା ନିମନ୍ତେ ଫେସବୁକ୍ର ଏହି ସୁବିଧା ହାସଲ କରିପାରିବେ । ଏଥିପାଇଁ ସର୍ଭିସ ନିମନ୍ତ ପର୍ଯ୍ୟାପ୍ତ ଚାହିଦା ସୃଷ୍ଟି ହେବା ପରେ ଟେଲିକମ୍ କମ୍ପାନୀ ଗୁଡ଼ିକ ନିଜର ଭିତ୍ତିଭୂମି ନିର୍ମାଣ କରିପାରିବେ ଏବଂ ଅକିଲା ବିମାନକୁ ଅନ୍ୟ ସ୍ଥାନକୁ ପଠାଇ ପାରିବେ । \r\nଗତ ଜୁନ୍ ମାସରେ ଫେସବୁକ୍ ଆମେରିକାର ଏରିଜୋନାରେ ଏହି ଅକିଲା ବିମାନର ପରୀକ୍ଷା କରିଥିଲା । ସୌରଶକ୍ତିରେ ଚାଲିଥିବା ଡ଼େଣା ବୋଇ ୭୩୭ ବିମାନ ଠାରୁ ବଡ଼ । ଏହି ବିମାନ ୯୦ଦିନ ପର୍ଯ୍ୟନ୍ତ ଆକାଶରେ ଉଡ଼ି ପାରିବ । ଏହା ୯୬ କିଲୋମିଟର ପରିସୀମାରେ ରହୁଥିବା ଲୋକଙ୍କୁ ଇଣ୍ଟରନେଟ୍ ସିଗନାଲ ଦେଇପାରିବ । ଯେଉଁ ଅଞ୍ଚଳରେ ନ୍ୟାସନାଲ ଅପ୍ଟିକାଲ ଫାଇବର ନେଟୱାର୍କ ନାହିଁ ସେହି ଅଞ୍ଚଳରେ ଇଣ୍ଟରନେଟ୍ ସେବା ଉପଲବ୍ଧ ହୋଇପାରିବ ।','2016_11_08_23_45_30_fb.jpg','Newstory','27-01-2017','#000000','','23'),(49,'ଭୋଟ ପ୍ରକ୍ରିୟା',NULL,'୫୦ଟି ରାଜ୍ୟ ଏବଂ ୱାଂଶିଟନ୍ ସହରର ଭୋଟର ମତଦାନ ପ୍ରକ୍ରିୟାରେ ସାମିଲ ହୋଇଛନ୍ତି । ରାଷ୍ଟ୍ରପତି ଏବଂ ଉପରାଷ୍ଟ୍ରପତି ନିର୍ବାଚନରେ ପ୍ରତ୍ୟେକ ରାଜ୍ୟର ସର୍ବାଧିକ ମତଦାନକୁ ନେଇ ନିର୍ଦ୍ଧିଷ୍ଟ ଇଲୋକ୍ଟ୍ରାଲ କଲେଜ ଗଠନ କରାଯାଇଛି । ଏହି କଲେଜ ନିର୍ଦ୍ଧିଷ୍ଟ ଦଳର ପ୍ରାର୍ଥୀଙ୍କୁ ସମର୍ଥନ କରିଥାଏ । ଏହିପରି ୫୩୮ ଇଲୋକ୍ଟ୍ରାଲ  କଲେଜ ସଦସ୍ୟ ନିର୍ବାଚିତ ହୋଇଥାନ୍ତି । ଜଣେ ପ୍ରାର୍ଥୀଙ୍କୁ ଜିତିବା ପାଇଁ ୨୭୦ଟି ଭୋଟ ଆବଶ୍ୟକ । ଇଲୋକ୍ଟ୍ରାଲ କଲେଜରୁ ନିର୍ବାଚିତ  ସଦସ୍ୟ ହାଉସ ଅଫ ରିପ୍ରେଜେଣ୍ଟେଟିଭରେ ପ୍ରତିନିଧିତ୍ୱ କରିଥାନ୍ତି । ସେହିପରି ପ୍ରତି ରାଜ୍ୟରୁ ୨ ଜଣ ସଦସ୍ୟ ସିନଟେକୁ ନିର୍ବାଚିତ ହୋଇଥାନ୍ତି ।','2016_11_09_00_03_13_Vote.jpg','Newstory','27-01-2017','#000000','',''),(50,'ମହାକାଶରୁ ମିଳିବ ପ୍ରଥମ ଭୋଟ',NULL,'ଭାରତୀୟ ସମୟାନୁସାରେ ମଙ୍ଗଳବାର ରାତି ଠାରୁ ଭୋଟ ଗ୍ରହଣ ଆରମ୍ଭ ହେବ । ତେବେ ପ୍ରଥମ ଥର ପାଇଁ ପୃଥିବୀ ଠାରୁ ହଜାର ହଜାର କିଲୋମିଟର ଦୂରରୁ ମହାକାଶରୁ ଆସିଛି ପ୍ରଥମ ଭୋଟ । ଅର୍ଥାତ୍ ନାସାର ମହାକାଶ ଯାତ୍ରୀ ସେନ୍ କ୍ରିମ୍ବୋ ମହାକାଶରୁ ନିଜର ମତାଧିକାର ସାବ୍ୟସ୍ତ କରିଛନ୍ତି । ସେ ଆନ୍ତର୍ଜାତିକ ମହାକାଶ କେନ୍ଦ୍ରରୁ ମତଦାନ କରିଛନ୍ତି । ସେନ୍ ଋଷର ସେୟଦ ରକେଟରେ ଅନ୍ୟ ଦୁଇ ସହଯୋଗୀ ଋଷ ମହାକାଶଯାତ୍ରୀଙ୍କ ସହ ଗତ ମାସ ୧୯ରେ ଆଇଏସଏସ ପାଇଁ ମହାକାଶ ଯାତ୍ରା କରିଛନ୍ତି । ସେ ଚାରିମାସ ସେଠାରେ ଅବସ୍ଥାନ କରିବେ । ୧୯୯୭ ମସିହା ଠାରୁ ଆମେରିକା ମହାକାଶ ଟେକ୍ସାସ ଆଇନ ଅନୁଯାୟୀ ମହାକାଶରୁ ମତଦାନ କରାଯାଇ ପାରୁଛି । ମହାକାଶରୁ ମତଦାନ କରିବା କ୍ଷେତ୍ରରେ ପ୍ରଥମ ଆମେରିକୀୟ ହେଉଛନ୍ତି ଡ଼େଭିଡ଼ ବ୍ଲଫ୍ । ସେ ମହାକାଶ କେନ୍ଦ୍ର ମୀରରୁ ନିଜର ମତାଧିକାର ସାବ୍ୟସ୍ତ କରିଥିଲେ । ତେବେ ସେ ଏଥିରେ ନିର୍ବାଚନରେ ସେନ୍ କାହା ସପକ୍ଷରେ ମତଦାନ କରିବେ ତାହା ସ୍ପଷ୍ଟ ହୋଇ ପାରି ନାହିଁ ।','2016_11_09_00_05_38_kimbo.jpg','Newstory','27-01-2017','#000000','',''),(51,'କିଏ ହେବେ ରାଷ୍ଟ୍ରପତି',NULL,'ୱାଶିଂଟନ୍: ଆମେରିକା ରାଷ୍ଟ୍ରପତି ନିର୍ବାଚନ ପାଇଁ ସମସ୍ତ ପ୍ରକ୍ରିୟା ଓ ପ୍ରସ୍ତୁତି ଶେଷ ହୋଇଛି । ଆମେରିକାର ପରବର୍ତ୍ତୀ ରାଷ୍ଟ୍ରପତି କିଏ ହେବ? ରିପବ୍ଲିକାନ ପାର୍ଟିର ପ୍ରାର୍ଥୀ ଡ଼ୋନାଲ୍ଡ ଟ୍ରମ୍ପ ନା ଡ଼େମୋକ୍ରାଟ ପାର୍ଟିର ହିଲାରୀ କ୍ଲିଣ୍ଟନ ଏବଂ କିଏ ଆସନ୍ତା ଚାରିବର୍ଷ ପାଇଁ ‘ହ୍ୱାଇଟ ହାଉସ୍’ରେ ବସିବେ ତାହାର ଫଇସଲା କରାଯିବ । ଏହି ପରିପ୍ରେକ୍ଷୀରେ ମଙ୍ଗଳବାର ସନ୍ଧ୍ୟା ଠାରୁ ଭୋଟ ଗ୍ରହଣ ଆରମ୍ଭ ହୋଇ ଯାଇଛି । ଆମେରିକାର ୪୫ତମ ରାଷ୍ଟ୍ରପତି ନିର୍ବାଚନରେ ୫୦ଟି ରାଜ୍ୟରେ ବେଶ୍ ଉତ୍ସାହର ସହ ମତ ଚାଲିଛି । ଏହାର ୪୮ ଘଣ୍ଟା ପରେ ଫଳାଫଳ ଘୋଷଣା କରାଯିବ ଏବଂ ନୂତନ ରାଷ୍ଟ୍ରପତି ଆସନ୍ତା ଜାନୁଆରୀରେ ନିଜର ଦାୟିତ୍ୱ ଗ୍ରହଣ କରିବେ । \r\nଉଭୟ ଟ୍ରମ୍ପ ଏବଂ କ୍ଲିଣ୍ଟନଙ୍କ ମଧ୍ୟରେ କଡ଼ା ମୁକାବିଲା ଚାଲିଛି ଏବଂ ଶେଷ ମୁହୂର୍ତ୍ତରେ ଗତ ସୋମବାର ଦିନ ଉଭୟ ପ୍ରାର୍ଥୀ ନିଜ ନିଜର ମତଦାତାମାନଙ୍କୁ ଆକୃଷ୍ଟ କରିବା ପାଇଁ ଭରପୁର ପ୍ରୟାସ କରିଥିଲେ । ନିର୍ବାଚନ ପୂର୍ବରୁ କରାଯାଇଥିବା ବିଭିନ୍ନ ସର୍ବେକ୍ଷଣ ଟ୍ରମ୍ପଙ୍କ ବିପକ୍ଷରେ ହିଲାରୀ କିଛି ମାତ୍ରାରେ ଅଗ୍ରଣୀ ଥିôବା କୁହାଯାଇଛି । ନିର୍ବାଚନ ପ୍ରଚାର ବେଳେ ଉଭୟ ପ୍ରାର୍ଥୀ ପରସ୍ପର ବିରୋଧରେ ଅନେକ ଅଭିଯୋଗ ଆଣିଥିଲେ । \r\nନିର୍ବାଚନରେ ଜିତିବା ପାଇଁ ଅତିକମ୍ରେ ୨୭୦ଟି ନିର୍ବାଚନ ମଣ୍ଡଳୀର ଭୋଟ ଆବଶ୍ୟକ । ଏନବିସି ନ୍ୟୁଜ ଏବଂ ଦ ୱାଲ୍ଡ ଷ୍ଟ୍ରିଟ୍ ଜର୍ଣ୍ଣାଲ ସର୍ବେକ୍ଷଣରେ ହିଲାରୀ ପ୍ରତିପକ୍ଷ ଟ୍ରମ୍ପଙ୍କ ଠାରୁ ୪ ପ୍ରତିଶତ ଅଙ୍କରେ ଆଗୁଆ ରହିବା କୁହାଯାଇଛି । ଏହି ୱେବସାଇଟ୍ ପକ୍ଷରୁ ପୂର୍ବାନୁମାନରେ କୁହାଯାଇଛିଯେ, ଚଳିତ ନିର୍ବାଚନରେ ହିଲାରୀଙ୍କର ୬୫ ପ୍ରତିଶତ ବିଜୟ ସମ୍ଭାବନା ରହିଛି । \r\nଏହି ନିର୍ବାଚନର ଫଳାଫଳ ଯାହା ହେଉନା କହିଁକି, ଏହା ନିଶ୍ଚିତ ରୂପେ ଇତିହାସ ସୃଷ୍ଟି କରିବ । ନିର୍ବାଚନରେ ଯଦି ହିଲାରୀ ବିଜୟୀ ହୁଅନ୍ତି, ତେବେ ସେ ଆମେରିକା ଇତିହାସରେ ପ୍ରଥମ ମହିଳା ରାଷ୍ଟ୍ରପତି ହେବାର ସୌଭାଗ୍ୟ ଅର୍ଜନ କରିବେ । ଯଦି ଟ୍ରମ୍ପ ବିଜୟୀ ହୁଅନ୍ତି, ତେବେ ସେ ହେବେ ଆମେରିକାର ପ୍ରଥମ ରାଷ୍ଟ୍ରପତି ଯିଏ ଜଣେ ବ୍ୟବସାୟୀ ଏବଂ ଯାହାଙ୍କର କିଛି ରାଜନୈତିକ ଅଭିଜ୍ଞତା କିମ୍ବା ଅନୁଭବ ନାହିଁ ।','2016_11_09_00_07_40_Trump.png','Newstory','27-01-2017','#000000','',''),(52,'ମହାକାଳପଡାରେ ବୈଜୟନ୍ତ-ଅତନୁ ସମର୍ଥକ  ମୁହାଁମୁହିଁ','ଭଦ୍ରକରେ ବିରୋଧୀ ଦଳର କଳାପତାକା ପ୍ରଦର୍ଶନ','ଭୁବନେଶ୍ୱର: ମୁଖ୍ୟମନ୍ତ୍ରୀ ନବୀନ ପଟ୍ଟନାୟକ କେନ୍ଦ୍ରାପଡା  ଏବଂ ଭଦ୍ରକ ଜିଲ୍ଲା ଗସ୍ତରେ ଯାଇ ପ୍ରାୟ ୪୫୦ କୋଟି ଟଙ୍କା ଅଟକଳରେ ବିଭିନ୍ନ ପ୍ରକଳ୍ପର ଉଦଘାଟନ ଓ ଭିତି ପ୍ରସ୍ତର ସ୍ଥାପନ କରିବା ସହ ଭଦ୍ରକର  ଗୋଟିଏ  ଓ କେନ୍ଦ୍ରପଡାର ତିନୋଟି ସ୍ଥାନରେ ଆୟୋଜିତ ସାଧାରଣ ସଭାରେ ଉଦବୋଧନ ଦେଇଥିଲେ । ମହାକାଳପଡା ନିର୍ବାଚନ ମଣ୍ଡଳୀରେ ସାଂସଦ ବୈଜୟନ୍ତ ପଣ୍ଡା  ଓ  ଅତନୁ ସବ୍ୟସାଚୀ ନାୟକଙ୍କ ସମର୍ଥକ ମାନଙ୍କ ମଧ୍ୟରେ ମୁଖ୍ୟମନ୍ତ୍ରୀଙ୍କ ଉପସ୍ଥିତିରେ ମୁହାଁମୁହିଁ ଓ ଧସ୍ତାଧସ୍ତି ହୋଇଥିଲା । ପୋଲିସ ଘେରରେ ସାଂସଦ ବୈଜୟନ୍ତ ପଣ୍ଡା କାର୍ଯ୍ୟକ୍ରମକୁ ଆସିଥିଲେ । ସେ ସାଧାରଣ ଲୋକଙ୍କୁ ହାତ ହଲାଇ ସ୍ୱାଗତ କରିଥିଲ । ଦୁଇ ଗୋଷ୍ଠୀର ନେତା ମାନଙ୍କ ମଧ୍ୟରେ ଧସ୍ତାଧସ୍ତି ହୋଇଥିଲା । ଅନ୍ୟପକ୍ଷରେ ଅତନୁଙ୍କ ସମର୍ଥକ ମାନେ କେନ୍ଦ୍ରାପଡାରେ ବୈଜୟନ୍ତଙ୍କୁ ଠେଲାପେଲା କରିଥିବା ଅଭିଯୋଗ ହୋଇଥିବା ବେଳେ ବୈଜୟନ୍ତଙ୍କ ସମର୍ଥକ ମାନେ ଭୁବନେଶ୍ୱରରେ ଅତନୁଙ୍କ କୁଶପୁତଳିକା ଦାହ କରିଛନ୍ତିି  । ଅନ୍ୟପକ୍ଷରେ ବୈଜୟନ୍ତ ପଣ୍ଡା ଟ୍ୱିଟ୍ କରି କହିଛନ୍ତି ଯେ ଯାହା ଆଶା କରିଥିଲେ ତାହା ହିଁ ହେଲା  । ପ୍ରବଳ ବିରୋଧ ସତ୍ୱେ ମୁଖ୍ୟମନ୍ତ୍ରୀଙ୍କ ସଭାକୁ ସେ ଗଲେ  । ଶହସ୍ରାଧିକ ଲୋକ ତାଙ୍କୁ ହାତ ହଲେଇ ସ୍ୱାଗତ କରିବା ସହିତ ସ୍ୱାଗତ କରିଥିଲେ  । ଅଳ୍ପ କିଛି ଲୋକ ବିରୋଧ କରିଥିଲେ  ।  ମୁଖ୍ୟମନ୍ତ୍ରୀଙ୍କ ସଭାକୁ ନଯିବା ପାଇଁ ଧମକ ଆସିଥିଲେ ମଧ୍ୟ ସେ ଏଥିରେ ଭୟଭୀତ ନୁହେଁ ବୋଲି ଶ୍ରୀ ପଣ୍ଡା ଟ୍ୱିଟ୍ କରିଛନ୍ତି । ଭଦ୍ରକରେ ବିରୋଧୀ ଦଳ ପକ୍ଷରୁ  କଳାପତାକା ପ୍ରଦର୍ଶନ କରାଯାଇଥିଲା । ଏହି ମାମଲାରେ ପୋଲିସ ୫୦ ଜଣଙ୍କୁ ଅଟକ ରଖିଥିବା ଜଣା ପଡିଛି । \r\n୪୫୦ କୋଟି ଟଙ୍କାର ପ୍ରକଳ୍ପ ମଧ୍ୟରୁ କେନ୍ଦ୍ରାପଡା,ରାଜନଗର ଓ ମହାକାଳପଡା ନିର୍ବାଚନମଣ୍ଡଳୀରେ ୩୨୪ କୋଟି ଟଙ୍କାର ପ୍ରକଳ୍ପ ଥିବା ବେଳେ ଭଦ୍ରକରେ ୧୨୬ କୋଟି ଟଙ୍କାର ପ୍ରକଳ୍ପ ରହିଛି । ଉଦଘାଟିତ ପ୍ରକଳ୍ପ ମଧ୍ୟରେ ୧୩୨/୩୨ କେବି ଗ୍ରିଡ  ଷ୍ଟେସନ, ହାଇଲେଭଲ ସେତୁ, ଜିଲ୍ଲା ପରିଷଦ ନୂତନ କାର୍ଯ୍ୟାଳୟ ଗୃହ, ଯାତ୍ରୀ ନିବାସ, ଦକ୍ଷତା ବୃଦ୍ଧି ତାଲିମ କେନ୍ଦ୍ର ରହିଛି । ସେହିପରି ଭଦ୍ରକର ଭଂଡାରିପୋଖରୀ ତହସିଲ ଅଂଚଳରେ ବୈତରଣୀ ନଦୀ ବାମ ପାର୍ଶ ନଦୀବନ୍ଧ ସୁଦୃଢୀକରଣ, ବୈତରଣୀ ଜଳ ନିଷ୍କାସନ ପ୍ରଣାଳୀ ଉନ୍ନତି କରଣ, ମେଗା ପାଇପ ଜଳ ଯୋଗାଣ ପ୍ରକଳ୍ପ, ରାସ୍ତାର ଉନ୍ନତିକରଣ ସମ୍ପର୍କିତ ପ୍ରକଳ୍ପ ଅନ୍ତର୍ଭୁକ୍ତ ।','2016_11_09_01_26_39_Baijayanta.jpg','Newstory','27-01-2017','#000000','',''),(53,'ଟ୍ରମ୍ପ ହେଲେ ମିଷ୍ଟର ପ୍ରେସିଡେଣ୍ଟ','ରିପବ୍ଲିକାନ ପାର୍ଟିର ବିପୁଳ ବିଜୟ','ୱାଶିଂଟନ,୯ ।୧୧ : ଆମେରିକା ରାଷ୍ଟ୍ରପତି ନିର୍ବାଚନରେ ରିପବ୍ଲିକାନ ପାର୍ଟିର ପ୍ରାର୍ଥୀ ଡ଼ୋନାଲ୍ଡ ଟ୍ରମ୍ପ୍ ବିଜୟୀ ହୋଇଛନ୍ତି । ସେ ତାଙ୍କର ପ୍ରତିଦ୍ୱନ୍ଦ୍ୱୀ ହିଲାରୀ କ୍ଲିଣ୍ଟନଙ୍କୁ ଏକ ବଡ ଧରଣର ବ୍ୟବଧାନରେ ହରାଇ ଦେଇଛନ୍ତି । ଏହି ବିଜୟ ସହ ଟ୍ରମ୍ଫ୍ ଆମେରିକା ରାଷ୍ଟ୍ରପତି ନିର୍ବାଚନରେ ଇତିହାସ ସୃଷ୍ଟି କରିଛନ୍ତି । ନିର୍ବାଚନ ହାରିବା ପରେ ଡେମୋକ୍ରାଟ୍ ପ୍ରାର୍ଥୀ ହିଲାରୀ କ୍ଲିଣ୍ଟନ ବିଜୟୀ ପ୍ରାର୍ଥୀ ଟ୍ରମ୍ପ୍୍ଙ୍କୁ ଫୋନ୍ କରି ଅଭିନନ୍ଦନ ଜଣାଇଛନ୍ତି । ଟ୍ରମ୍ପ୍ ହେବେ ଆମେରିକାର ୪୫ତମ ରାଷ୍ଟ୍ରପତି । ନିର୍ବାଚନରେ ଟ୍ରମ୍ପ୍ଙ୍କ ସପକ୍ଷରେ ୨୬୬ଟି ଭୋଟ୍ ପାଇଥିବା ବେଳେ, ହିଲାରୀଙ୍କ ସପକ୍ଷରେ ମିଳିଛି ୨୧୮ଟି ଭୋଟ୍ । ଆମେରିକା ରାଷ୍ଟ୍ରପତି ନିର୍ବାଚନରେ ବିଜୟୀ ହେବା ପାଇଁ ୨୭୦ଟି ଭୋଟର ଆବଶ୍ୟକତା ପଡିଥାଏ ।\r\nବିଜୟ ପରେ ଉତ୍ସାହିତ ଟ୍ରମ୍ପ୍ ଲୋକଙ୍କ ସମ୍ମୁଖକୁ ଆସି ହିଲାରୀ ତାଙ୍କ ଫୋନରେ ବଧାଇ ଜଣାଇଥିବା ପ୍ରକାଶ କରିବା ସହ ସେ ଦୀର୍ଘ ସମୟ ଧରି ଆମେରିକାର ସେବା କରିଥିବାରୁ ହିଲାରୀଙ୍କୁ ଧନ୍ୟବାଦ ଦେଇଥିବା କହିଥିଲେ । ସେ ପୁଣି କହିଥିଲେ ଯେ, ଏହି ବିଜୟ ତାଙ୍କ ପାଇଁ ଓ ଆମେରିକା ପାଇଁ ଅତ୍ୟନ୍ତ ଗୁରୁତ୍ୱପୂର୍ଣ୍ଣ । ସେ ଆମେରିକାବାସୀଙ୍କ ସ୍ୱପ୍ନକୁ ଜାଣିଛନ୍ତି ଏବଂ ମିଳିମିଶି ତାହାକୁ ପୂରଣ କରିବା ପାଇଁ ଉଦ୍ୟମ କରିବେ ବୋଳି କହିଛନ୍ତି । ଆମେରିକାର ଅର୍ଥନୈତିକ ପ୍ରଗତିକୁ ଦୁଇ ଗୁଣ କରିବେ ଏବଂ ସେ କୌଣସି ଲକ୍ଷ୍ୟ ହାସଲ ଉଦ୍ୟମ କରିବେ ବୋଲି କହିଛନ୍ତି ।\r\nରାଷ୍ଟ୍ରପତି ନିର୍ବାଚନ ଜିତିବା ପାଇଁ ନିର୍ବାଚନ ମଣ୍ଡଳ ୫୩୮ ଭୋଟ ମଧ୍ୟରୁ ୨୭୦ ହାସଲ କରିବାର ଜରୁରୀ ଥିଲା । ଗତକାଲି ଅନୁଷ୍ଠିତ ନିର୍ବାଚନରେ ଟ୍ରମ୍ପ୍ଙ୍କ ବିଜୟ ଅତ୍ୟନ୍ତ ଆଶ୍ଚର୍ଯ୍ୟଜନକ ହୋଇଛି । ତାଙ୍କ ବିଜୟରେ ଫ୍ଲୋରିଡା, ଉତ୍ତରକରୋଲିନା ଏବଂ ଓହିୟୋ ପରି ରାଜ୍ୟ ଗୁଡିକର ଗୁରୁତ୍ୱପୂର୍ଣ୍ଣ ଭୂମିକା ରହିଛି । ଆମେରିକାର ୨୪୦ ବର୍ଷର ଇତିହାସରେ ପ୍ରଥମ ମହିଳା ରାଷ୍ଟ୍ରପତି ହେବାର ଆଶା ରଖିଥିବା ହିଲାରୀଙ୍କ ସ୍ୱପ୍ନ ଚୁରମାର୍ ହୋଇଛି । ଟ୍ରମ୍ପ୍ଙ୍କୁ ଭଟ୍ଟା, ଆୟୋବା, ଫ୍ଲୋରିଡା, ଉତ୍ତର କରୋଲିନା, ୱେଷ୍ଟ ଭର୍ଜିନିଆ, ଇଣ୍ଡିଆନା, ଓକ୍ଲାହୋମ୍, ଟେନିସୀ, ଆଲବାମା, ମିସିସିପି, ଟେକ୍ସାସ, କନ୍ସାସ୍, ଦ୍ୟୋମିଙ୍ଗ, ଉତ୍ତର ଓକେଟା, ଦକ୍ଷିଣ ଡକୋଟା, ଉରକାନସାସ୍, ନେବ୍ରାସ୍ଥୋ, ଲୁସୟାନା, ମୋନଟାନା, ଓହିଓ, ମିସୁରୀ ଦର୍ଜିୟା ଏବଂ ଇଦାହୋ ଆଦି ରାଜ୍ୟବାସୀ ବିଜୟୀ କରିଛନ୍ତି । ଅନ୍ୟ ପକ୍ଷରେ ହିଲାରୀଙ୍କୁ ବରମାଣ୍ଟ, ଡିଷ୍ଟ୍ରିକ୍ଟ ଅଫ୍ କଲୋମ୍ବିଆ, ମାସାଚ୍ୟୁଟସ୍, ମେରୀଲୋଁ ଇଲାବର, ଝାଲନୟସ, ରୋଡ଼ ଆଇଲାଣ୍ଡ, ନ୍ୟୁଜର୍ସି, ନ୍ୟୁୟର୍କ, କନେକ୍ଟିକଟ୍, ନ୍ୟୁମେର୍କସକୋ, ଭର୍ଜିନିଆ, କୋଲୋରାଡୋ, କାଲଫର୍ଣ୍ଣିଆ, ଓରେଗାଁନ୍, ୱାଶିଂଟନ୍, ନେବାଡ ଏବଂ ହଜଇ ଆଦି ରାଜ୍ୟରେ ସମର୍ଥନ ମିଳିଛି ।','2016_11_09_20_59_43_Trump.jpg','Newstory','27-01-2017','#000000','','26'),(54,'ଟଙ୍କା ଅଚଳର ନେପଥ୍ୟ ନାୟକ ଅନିଲ ବୋକିଲ',NULL,'ନୂଆଦିଲ୍ଲୀ୍: ଦେଶରେ କଳାଧନ ଠୁଳକାରୀଙ୍କ ବିରୋଧରେ କାର୍ଯ୍ୟାନୁଷ୍ଠାନ ସହ କଳାଧନ କାରବାର ଉପରେ ଅଙ୍କୁଶ ଲଗାଇବା ପାଇଁ ପ୍ରଧାନମନ୍ତ୍ରୀ ନରେନ୍ଦ୍ର ମୋଦି ଯେଉଁ ‘ମାଷ୍ଟରଷ୍ଟ୍ରୋକ୍’ଗ୍ରହଣ କରିଛନ୍ତି ତାହା ପଛର ନାୟକ ହେଉଛନ୍ତି ଅନିଲ ବୋକିଲ । ସେ ଚାରିବର୍ଷ  ପୂର୍ବେ ଏହି ପ୍ରସ୍ତାବ ନରେନ୍ଦ୍ର ମୋଦିଙ୍କୁ ଦେଇଥିଲେ । ସେତେବେଳେ ଶ୍ରୀ ମୋଦି ଗୁଜରାଟର ମୁଖ୍ୟମନ୍ତ୍ରୀ ଥିଲେ ।  ପେଷାରେ ଚାଟାର୍ଡ଼ ଆକାଉଣ୍ଟାଣ୍ଟ ଥିବା ଅନିଲଙ୍କର ‘ଅର୍ଥକ୍ରାନ୍ତି’ ନାମକ ଏକ ଇକୋନୋମିକ୍ ଆଡ଼ଭାଇଜରୀ ସଂସ୍ଥା ରହିଛି । ଅନିଲକେବଳ ଶ୍ରୀ ମୋଦିଙ୍କୁ ଏପରି ପ୍ରସ୍ତାବ ଦେଇ ନଥିଲେ । ସେ ପୂର୍ବତନ ରାଷ୍ଟ୍ରପତି ପ୍ରତିଭା ପାଟିଲ, କଂଗ୍ରେସ ଉପାଧ୍ୟକ୍ଷ ରାହୁଲ ଗାନ୍ଧି, ଯଶବନ୍ତ ସିହ୍ନାଙ୍କ ସମେତ ଅନେକ ମନ୍ତ୍ରୀ, ରାଜ୍ୟପାଳ ଓ ରାଷ୍ଟ୍ରାୟତ ବ୍ୟାଙ୍କ ମାନଙ୍କ ମୁଖ୍ୟମାନଙ୍କୁ ଦେଇଥିଲେ । ସେ ରାଷ୍ଟ୍ରପତି ଭବନ ଠାରେ ତତ୍କାଳୀନ ରାଷ୍ଟ୍ରପତି ପ୍ରତିଭା ପାଟିଲଙ୍କୁ ଦୁଇଥର ସାକ୍ଷାତ କରି ଏହି ପ୍ରସ୍ତାବ ଦେଇଥିଲେ । ସେ ମଧ୍ୟ ସମସ୍ତଙ୍କୁ ଏଥି ନେଇ ଏକ ପତ୍ର ଜରିଆରେ ଉଲ୍ଲେଖ କରି ଏହାକୁ  ସଂସଦରେ ଚର୍ଚ୍ଚା କରିବାକୁ ପ୍ରସ୍ତାବ ଦେଇଥିଲେ । ଏହି ପ୍ରସ୍ତାବ ଯେତେବେଳେ ସେ ସେତେବେଳର ପ୍ରଭାବଶାଳୀ ବ୍ୟକ୍ତି  ରାହୁଲ ଗାନ୍ଧିଙ୍କ ସମ୍ମୁଖରେ ରଖିଥିଲେ, ସେତେବେଳେ ରାହୁଲ ମାତ୍ର କେତେ ସେକେଣ୍ଡେ ଏହା ଶୁଣି ଉଠି ଚାଲି ଯାଇଥିଲୋ\r\nସେ ଯେଉଁ ୫ଟି ପ୍ରସ୍ତାବ ଦେଇଥିଲେ, ସେ ଗୁଡ଼ିକ ଥିଲା ଆମଦାନୀ ଶୁଳ୍କକୁ ଛାଡ଼ି ସମସ୍ତ ପ୍ରକାର ଟିକସ ବ୍ୟବସ୍ଥା ଉଚ୍ଛେଦ କରାଯାଉ । ବଡ଼ ଧରଣର ନେଣଦେଣ  ବ୍ୟାଙ୍କ ଦ୍ୱାରା ଚେକ ଜରିଆରେ କରାଯାଉ । ଏହାଦ୍ୱାରା ବ୍ୟାଙ୍କ ୨ ପ୍ରତିଶତ କର କାଟିବା ବ୍ୟବସ୍ଥା କରୁ । ୫୦ ଟଙ୍କାରୁ  ଅଧିକ ମୂଲ୍ୟର ଟଙ୍କାକୁ ପ୍ରତ୍ୟାହାର କରି ନିଆଯାଉ । ନଗଦ ଟଙ୍କା ଉଠାଣ ପାଇଁ ଏକ ନିର୍ଦ୍ଧିଷ୍ଟ ସୀମା ଲାଗୁ କରାଯାଉ ଏବଂ ସରକାର ରାଜସ୍ୱ ଜମା କରିବା ନିମନ୍ତେ ବ୍ୟାଙ୍କକୁ ହିଁ ଏକମାତ୍ର ସିଷ୍ଟମ ରୂପେ ବ୍ୟବହାର କରାଯାଉ । \r\nଏହାଦ୍ୱାରା ଲୋକଙ୍କ ନିକଟରେ ସୀମିତ ମାତ୍ରାର ନଗଦ ଅର୍ଥ ରହିପାରିବ ।  କାରଣ ଅଧିକ ବଡ଼ ଧରଣର ନେଣ ଦେଣ କ୍ଷେତ୍ରରେ ଲୋକମାନେ ଚେକ ଦ୍ୱାରା ଅର୍ଥ ପୈଠ କିମ୍ବା ଅନ୍ଲାଇନ ଜରିଆରେ କରିବାକୁ ବାଧ୍ୟ ହେବେ । ଫଳରେ ସବୁ ବଡ଼ ଧରଣର କାରବାରର ବିସ୍ତୃତ ସୂଚନା ବ୍ୟାଙ୍କରେ ରହିପାରିବ । ସୁରୋରେ ଚୋରୀ ହୋଇ ପାରିବ ନାହିଁ ଏବଂ ସବୁ ସୂଚନା ସରକାରଙ୍କ ନିକଟରେ ରହିବ । ଏହାଦ୍ୱାରା କଳାଧନକୁ ନିୟନ୍ତ୍ରଣ କରାଯିବା ସହ ଦରବୃଦ୍ଧି ଉପରେ ମଧ୍ୟ ଲଗାମ ଲାଗି ପାରିବ ।','2016_11_10_22_12_36_Anil-Bokil.jpg','Newstory','27-01-2017','#000000','','25'),(55,'ଟ୍ରମ୍ପ ବିରୋଧିଙ୍କ ବିକ୍ଷୋଭ: ପୋଡ଼ାଜଳା',NULL,'ଓକଲାଣ୍ଡ: ଆମେରିକା ରାଷ୍ଟ୍ରପତି ନିର୍ବାଚନ ଫଳାଫଳ ଘୋଷଣା ହେବା ପରେ ହିଲାରୀଙ୍କ ସମର୍ଥକମାନେ ବିରୋଧ ପ୍ରଦର୍ଶନ କରିଛନ୍ତି । କେତେକ ରାଜ୍ୟରେ ଟ୍ରମ୍ପଙ୍କ ବିରୋଧିମାନେ ସ୍ଲୋଗାନ ଦେବା ଆରମ୍ଭ କରାଯାଇଛି । ଏମାନଙ୍କ ମଧ୍ୟରେ ଅଧିକାଂଶ ଯୁବକ ଥିବା ଜଣାଯାଇଛି । କାଲିଫର୍ଣ୍ଣିଆରେ ସବୁଠୁ ଅଧିକ ବିରୋଧ ଦେଖିବାକୁ ମିଳିଛି । ବିକ୍ଷୋଭକାରୀମାନେ ଟ୍ରମ୍ପଙ୍କ କୁଶପତଳିକା ଦାହ କରିବା ସହ ଭଙ୍ଗାରୁଜା ଓ ପୋଡ଼ାଜଳା କରିଛନ୍ତି । କାଲିଫର୍ଣ୍ଣିଆ ବିଶ୍ୱବିଦ୍ୟାଳୟ କ୍ୟାମ୍ପସରେ ମଧ୍ୟ ଛାତ୍ରମାନେ ବିକ୍ଷୋଭ ପ୍ରଦର୍ଶନ କରିଛନ୍ତି । ଅନେକ ସ୍ଥାନରେ ବିରୋଧିମାନେ ରାସ୍ତାରେ ଟାୟାର ଜାଳିଥିବା ଦେଖାଯାଇଛି । ଅନେକ ସ୍ଥାନରେ ରାସ୍ତା ଅବରୋଧ କରାଯାଇଛି ।','2016_11_10_22_15_53_anti trump.jpg','Newstory','27-01-2017','#000000','','26'),(56,'ବିଚରା ଭିକାରୀ ସୀତାରାମ',NULL,'ଇନ୍ଦୋର: ମଧ୍ୟପ୍ରଦେଶର ଦେବାସ ଜିଲ୍ଲାର ସୀତାରାମ ନାମକ ଜଣେ ଦୃଷ୍ଟିହୀନ ଭିକାରୀ ୫ଶହ ଓ ଏକ ହଜାର ଟଙ୍କିଆ ନୋଟ ଅଚଳ ହେବା ପରେ ଏବେ ଚିନ୍ତାରେ ପଡ଼ିଛି । ସୀତାରାମ ନିକଟରେ ଗଚ୍ଛିତ ଅଛି ୯୮ ହଜାର ଟଙ୍କା । ଗତ ୨୦ ବର୍ଷ ମଧ୍ୟରେ ସେ ଏହି ଟଙ୍କା ଜମା କରିଛି । ତେବେ ଟଙ୍କା ଅଚଳ ହେବା ପରେ ସେ କଣ କରିବ ଚିନ୍ତାରେ ପଡ଼ିଛି । ତା ଗଚ୍ଛିତ ଟଙ୍କାକୁ ଛୋଟ ନୋଟରେ ପରିବର୍ତ୍ତନ କରିବାକୁ ସେ ପଞ୍ଚାୟତରେ ଗୁହାରୀ ଜଣାଇଛି । ସେ କହିଛିଯେ, ଏହି ଗଚ୍ଛିତ ଟଙ୍କା ହେଉଛି ତା ସାରା ଜୀବନ ଚଳାଇଥିବା ପରିଶ୍ରମ ଓ ସଂଘର୍ଷର ଫଳ । ଏହି ସବୁ ଟଙ୍କା ଏବେ ଅଳିଆ ଆବର୍ଜନା ପାଲଟି ଗଲା ବୋଲି ଲୋକେ କହୁଛନ୍ତି । ତେଣୁ ସରକାର ଏଥିପାଇଁ ବିଚାର କରନ୍ତୁ ବୋଲିସେ ନିବେଦନ କରିଛି । ଗ୍ରାମ ପଞ୍ଚାୟତ ସଚିବ ଇଜାଜ୍ ପଟେଲ କହିଛନ୍ତିଯେ, ସୀତାରାମ୍ ଶିୟା ସଡ଼କ ପାଶ୍ୱର୍ରେ ଭିକ ମାଗି ଏହି ଟଙ୍କା ସଞ୍ଚୟ କରିଛି । ତା ନିକଟରେ ଏହି ଅର୍ଥ ସୁରକ୍ଷିତ ନୁହେଁ ବୋଲି ଜାଣି ସେ ନିଜର ଜଣେ ବିଶ୍ୱସ୍ତ ନିକଟରେ ଜମା ରଖିଥିଲା । \r\nଦୁଇ ଦଶକ ପୁର୍ବେ ସୀତାରାମ ନିଜ ପତ୍ନୀ ସହ ରାଜସ୍ଥାନରୁ ଦେବାସ ଅଞ୍ଚଳକୁ ଚାଲି ଆସିଥିଲା । ଦୃଷ୍ଟିହୀନ କାରଣରୁ ତାକୁ କେହି ଚାକିରୀ ନଦେବାରୁ ଶେଷରେ ପେଟ ପୋଷିବା ପାଇଁ ଭିକ୍ଷାବୃତ୍ତି ଆଦରି ନେଇଥିଲା । ତାର ବ୍ୟାଙ୍କ ଆକାଉଣ୍ଟ ନାହିଁ । ଏବେ ସେ କଣକରିବ ସେହି ଚିନ୍ତାରେ ବ୍ୟତିବ୍ୟସ୍ତ ଓ ବିବ୍ରତ ।','2016_11_11_22_36_02_sitaram.jpg','Newstory','27-01-2017','#000000','','25'),(57,'ଆଣବିକ ନୀତିକୁ ନେଇ ପାରିକରଙ୍କ ବିବଦମାନ ବୟାନ',NULL,'ନୂଆଦିଲ୍ଲୀ: ଭାରତର ଆଣବିକ ନୀତି ଉପରେ ପ୍ରତିରକ୍ଷା ମନ୍ତ୍ରୀ ମନୋହର ପାରିକର ପ୍ରଶ୍ନ ଉଠାଇ ଏକ ନୂଆ ବିବାଦର ସୂତ୍ରପାତ ଘଟାଇଛନ୍ତି । ଭାରତ ପ୍ରଥମେ କାହା ଉପରେ ଆଣବିକ ଆକ୍ରମଣ କରିବ ନାହିଁ ସଂପର୍କୀତ ନୀତିକୁ ସେ ସମାଲୋଚନା କରି କହିଛନ୍ତିଯେ, ଭାରତ ଏହି ନୀତିକୁ ଜାବୁଡ଼ି ଧରିବା ଉଚିତ ନୁହେଁ । ଭାରତ ଏକ ଦାୟିତ୍ୱ ସଂପନ୍ନ ଆଣବିକ ଶକ୍ତି ସଂପନ୍ନ ରାଷ୍ଟ୍ର ଏବଂ ଏହାର ଦାୟିତ୍ୱହୀନ ବ୍ୟବହାର କରିବ ନାହିଁ ବୋଲି ଭାରତର ନୀତି ହେବା ଉଚିତ । ସେ କହିଛନ୍ତିଯେ, ଦେଶ ପ୍ରତି ବିପଦ ସୃଷ୍ଟି ହେଲେ କିଛି ବିଚାର କରାଯିବା ଅନୁଚିତ । ତେବେ ଏହା ତାଙ୍କର ନିଜସ୍ୱ ମତ ବୋଲି କହିଛନ୍ତି । ଏଠାରେ ସୂଚନା ଯୋଗ୍ୟଯେ, ଆଣବିକ ବୋମା ପରୀକ୍ଷା ବେଳେ, ଭାରତ ବିଶ୍ୱକୁ ପ୍ରତିଶ୍ରୁତି ଦେଇଥିଲାଯେ, ଏହାକୁ ଭାରତ କାହା ବିରୋଧରେ ପ୍ରଥମେ ବ୍ୟବହାର କରିବ ନାହିଁ । ଅନ୍ୟ ପକ୍ଷରେ ଶ୍ରୀ ପାରିକରଙ୍କ ଏହି ବୟାନକୁ କଂଗ୍ରେସ ବିରୋଧ କରିଛି । କଂଗ୍ରେସ କହିଛିଯେ, ଏହା ଦେଶର ପରମାଣୁ ନୀତିର ବିରୁଦ୍ଧାଚରଣ କରୁଛି ।','2016_11_11_22_38_00_Parikar.jpg','Newstory','27-01-2017','#000000','','0'),(58,'ଆଇଏମଏଫର ପ୍ରଂଶସା',NULL,'ୱାଶିଂଟନ୍: ଭ୍ରଷ୍ଟାଚାର ବିରୋଧରେ ସଂଗ୍ରାମ ପାଇଁ ଭାରତ ସରକାର ଗ୍ରହଣ କରିଥିବା ବିମୁଦ୍ରାକରଣ ପଦକ୍ଷେପକୁ ଆନ୍ତର୍ଜାତିକ ମୁଦ୍ରାପାଣ୍ଠି (ଆଇଏମଏଫ) ସ୍ୱାଗତ କରିଛି । ତେବେ ଏହାଦ୍ୱାରା ଦେଶର ଅର୍ଥ ବ୍ୟବସ୍ଥା ଯେପରି ବହୁଳ ମାତ୍ରାରେ ପ୍ରଭାବିତ ନହୁଏ, ସେଥିପ୍ରତି ମଧ୍ୟ ସତର୍କ କରାଇ ଦେଇଛି । ଏ ସଂପର୍କରେ ନିଜର ପ୍ରତିକ୍ରିୟା ରଖି ଆଇଏମଏଫର ମୁଖପାତ୍ର ଗେରୀ ରାଇସ କହିଛନ୍ତିଯେ, ଭାରତୀୟ ଅର୍ଥନୀତିରେ ପ୍ରତ୍ୟହ ନଗଦ କାରବାର ଏକ ଗୁରୁତ୍ୱପୂର୍ଣ୍ଣ ଭୂମିକା ରହିଛି । ତେବେ ଏହି ପଦକ୍ଷେପ ଦ୍ୱାରା ଦେଶର ଅଥନୀତି ଯେତେ କମ୍ ମାତ୍ରାରେ ପ୍ରଭାବିତ ହେବ ତାହା ଦେଖିବା ସରକାରଙ୍କ କର୍ତ୍ତବ୍ୟ ।','2016_11_11_22_51_25_IMF.jpg','Newstory','27-01-2017','#000000','','25'),(59,'ଜାଲ୍ ନୋଟ୍ ଜମା ବେଳେ ଇଂଜିନିୟରିଂ ଛାତ୍ର ଗିରଫ',NULL,'ଭୁୁବନେଶ୍ୱର: ଭାରତୀୟ ଷ୍ଟେଟ୍ ବ୍ୟାଙ୍କର ଖୋର୍ଦ୍ଧା ଶାଖାକୁ ଜାଲ ନୋଟ୍ ଜମା କରିଅ ।ସିଥିବା ଜଣେ ଇଂଜିନୟରିଂ ପଢୁଆ ଛାତ୍ରଙ୍କୁ ପୋଲିସ ଗିରଫ କରିଛି  । ଗିରଫ ଯୁବକ ଜଣକ ହେଲେ ସୁମିତ କୁମାର ଟୁଡୁ  । ସେ ରାଜଧାନୀର ଏକ କଲେଜର ଇଂଜିନିୟରିଂ ଛାତ୍ର  ।  ଭାରତୀୟ ଷ୍ଟେଟ୍ ବ୍ୟାଙ୍କର ଖୋର୍ଦ୍ଧା ଶାଖାକୁ ଜାଲ ନୋଟ୍ ଜମା କରିଅ ।ସିଥିବା ବେଳେ ପୋଲିସ୍ ତାଙ୍କୁ ଗିରଫ କରିବା ସହ ତାଙ୍କ ଠାରୁ ୪୭ ହଜାର ଟଙ୍କାର ଜାଲ୍ ନୋଟ ଜବତ କରିଛି   । ସେଥିମଧ୍ୟରେ ମୋଟ ୫୨ ଟି ଜାଲ୍ ନୋଟ ଥିଲା  । ୪୨ଟି ୧୦୦୦ଟଙ୍କିଅ । ନୋଟ୍ ଓ ୧୦ଟି ୫୦୦ ଟଙ୍କିଅ । ନୋଟ ରହିଛି  । ଗିରଫ ଛାତ୍ର ଜଣକ ତାଙ୍କ ବାପାଙ୍କ ଏକାଉଣ୍ଟରେ ଏହି ଟଙ୍କା ଜମା କରୁଥିଲେ । ତାଙ୍କ ବାପା କେନ୍ଦ୍ରାପଡାର ବ୍ୟାଙ୍କରେ ଆସିଷ୍ଟାଂଟ ମ୍ୟାନେଜର ରହିଛନ୍ତି  । ଖୋର୍ଦ୍ଦା ପୋଲିସ ସହ କେନ୍ଦ୍ରାପଡା ପୋଲିସ ଏହି ଘଟଣାର ତଦନ୍ତ ଆରମ୍ଭ କରିଛି ।','2016_11_11_23_56_56_Fake Notes.jpg','Newstory','27-01-2017','#000000','','0'),(60,'ପ୍ରକାଶ ପାଇବ ରାଜୀବ ହତ୍ୟାକାରିଣୀଙ୍କ ଆତ୍ମକଥା','୨୪ରେ ଲୋକାର୍ପଣ','ଚେନ୍ନାଇ: ପୂର୍ବତନ ପ୍ରଧାନମନ୍ତ୍ରୀ ରାଜୀବ ଗାନ୍ଧିଙ୍କ ହତ୍ୟା ମାମଲାରେ ଦୋଷୀ ସାବ୍ୟସ୍ତ ହୋଇ ଜେଲଦଣ୍ଡ ଭୋଗୁଥିବା ନଳିନୀ ଶ୍ରୀହରଣ ଏବେ ପୁଣି ଚର୍ଚ୍ଚାକୁ  ଆସିଛନ୍ତି । ନଳିଦୀ ହେଉଛନ୍ତି ବିଶ୍ୱର ପ୍ରଥମ ମହିଳା କଏଦୀ, ଯିଏ ୨୫ ବର୍ଷରୁ ଊଦ୍ଧ୍ୱର୍ ସମୟ ହେବ ଜେଲରେ ବନ୍ଦୀ ହୋଇ ରହିଛନ୍ତି । ସେ ଇତି ମଧ୍ୟରେ ଦେଶ ଭିତରେ ୫୦୦ ପୃଷ୍ଠାର ଏକ ଆତ୍ମକଥା ରଚନା କରିଛନ୍ତି । ଆସନ୍ତା ୨୫ ତାରିଖରେ ଏହି ପୁସ୍ତକଟିର ଲୋକାର୍ପଣ ହେବ । ସେ ଏହି ଆତ୍ମକଥା ପୁସ୍ତକରେ ତାଙ୍କର ପ୍ରୀୟଙ୍କା ଗାନ୍ଧୀଙ୍କ ସହ ହୋଇଥିବା ସାକ୍ଷାତ ସଂପର୍କରେ ଉଲ୍ଲେଖ କରିଛନ୍ତି । ପୁସ୍ତକଟି ତାମିଲ ଭାଷାରେ ରଚନା କରାଯାଇଛି । \r\nଏଲଟିଟିଇ ପାଇଁ କାମ କରୁଥିବା ଶ୍ରୀହରଣ (ମୁରୁଗାନ୍)କୁ ନଳିନୀ ୧୯୯୧ ମସିହା ଏପ୍ରିଲ୨୧ରେ ବିବାହ କରିଥିଲେ । ରାଜୀବ ଗାନ୍ଧୀ ହତ୍ୟା ମାମଲାରେ ତାଙ୍କୁ ଗିରଫ କରାଯିବା ସମୟରେ ସେ ଦୁଇମାସର ଗର୍ଭବତୀ ଥିଲେ । ସେ ବର୍ତ୍ତମାନ ତାମିଲନାଡ଼ୁର ଭେଲୋରର ସ୍ୱତନ୍ତ୍ର ଜେଲରୁ ମହିଳା ସେଲରେ ଅନ୍ୟ ୬ ମହିଳାଙ୍କ ସହ ଆଜୀବନ କାରାଦଣ୍ଡ ଭୋଗୁଛନ୍ତି ।\r\nସେ ତାଙ୍କ ଆତ୍ମକଥାରେ ନିଜର ପିଲାଦିନ, ମୁରୁଗାନଙ୍କ ସହ ସଂପର୍କ, କେଉଁ ପରିସ୍ଥିତିରେ ରାଜୀବ ହତ୍ୟାକାଣ୍ଡର ସାକ୍ଷୀ ହେଲେ, ୫ ଦିନ ପର୍ଯ୍ୟନ୍ତ ଲୁଚି ରହିବା, ଗିରଫ ହେବା, ହାଜତରେ ମିଳିଥିବା ନିର୍ଯ୍ୟାତନା, ଜେଲରେ ପ୍ରସବ, ଦଣ୍ଡ ଓ ଜେଲ ଜୀବନ ସଂପର୍କରେ ବିସ୍ତୃତ ଭାବେ ବର୍ଣ୍ଣନା କରିଛନ୍ତି । ଏହି ପୁସ୍ତକର ସବୁଠୁ ଆକର୍ଷଣୀୟ ଅଧ୍ୟାୟ ହେଉଛି ୨୦୦୮ ମସିହା ମାର୍ଚ୍ଚ ୧୯ରେ ରାଜୀବ ତନୟା ପ୍ରୀୟଙ୍କାଙ୍କ ସହ ତାଙ୍କର ୯୦ ମିନିଟ୍ର ସାକ୍ଷାତ ଆଲୋଚନା । ଏହି ସାକ୍ଷାତର କିଛି ଅଂଶ ୨୦୦୮ ମସିହା ଏପ୍ରିଲ ୧୫ରେ ଟାଇମ୍ସ ଅଫ୍ ଇଣ୍ଡିଆରେ ପ୍ରକାଶିତ ହୋଇଥିଲା । \r\nଖୁବ୍ କମ୍ ଲୋକ ଜାଣନ୍ତିଯେ, ନଳିନୀଙ୍କ ମାଙ୍କ ନାମ ମହାତ୍ମା ଗାନ୍ଧି ‘ପଦ୍ମାବତୀ’ ବୋଲି ରଖିଥିଲେ ।  ସେ ଚେନ୍ନାଇର ଏକ ହସପିଟାଲରେ ନର୍ସ ଥିଲେ । ନଳିନୀ ଏକ ଭଡ଼ା ଘର ଖୋଜୁଥିବା ବେଳେ, ଶ୍ରୀହରଣ (ମୁରୁଗାନ୍) ସହ ତାଙ୍କର ସଂପର୍କ ହୋଇଥିଲା । ଏହା ପରେ ଶ୍ରୀଲଙ୍କାର ଶିବରାସନ ସହ ମଧ୍ୟ ପରିଚୟ ହୋଇଥିଲା । ଏହା ପରେ ଶିବରାସନ ଧାନୁକୁ ଆଣିଥିଲା ଯିଏକି ପରବର୍ତ୍ତୀ ସମୟରେ ମାନବ ବୋମାର ଭୂମିକାରେ ଅବତୀର୍ଣ୍ଣ ହୋଇଥିଲା । ସେମାନଙ୍କ ସହ ସଂପର୍କ ଥିଲେ ମଧ୍ୟ ରାଜୀବ ହତ୍ୟା ପାଇଁ ଶିବରାସନ ଓ ଧାନୁ କରିଥିବା ଯୋଜନା ସଂପର୍କରେ ସେ କିଛି ଜାଣି ନଥିଲା ବୋଲି ଏବେ ବି ମଧ୍ୟ କହୁଛି । ପରିସ୍ଥିତି କିପରି ତାଙ୍କୁ ହତ୍ୟାକାରିଣୀ ବୋଲି ପରିଚିତ କରାଇଲା ତାହା ସେ ପ୍ରୀୟଙ୍କାଙ୍କୁ କହିଥିଲା । ପ୍ରୀୟଙ୍କା ତାଠାରୁ ଜାଣିବାକୁ ଚାହିଁଥିଲେଯେ, ହତ୍ୟାକାଣ୍ଡ ପଛରେ ଆଉ କିଏ ସବୁ ଥିଲେ । ଯେହେତୁ ନଳିନୀ ଏଲଟିଟିଇର ସଦସ୍ୟ ନଥିଲା, ତେଣୁ ସେ ପୁରା ଷଡ଼ଯନ୍ତ୍ର ସଂପର୍କରେ ଅଜ୍ଞ ଥିଲା ବୋଲି କହିଛି । \r\nଚେନ୍ନାଇରେ ଜନ୍ମ ଗ୍ରହଣ କରିଥିବା ନଳିନୀଙ୍କର ଇଂରାଜୀ ସାହିତ୍ୟରେ ବେଶ୍ ଦଖଲ ରହିଛି । ଶ୍ରୀହରଣ ତାଙ୍କ ଜୀବନକୁ ପଶି ଆସିବା ସମୟରେ ସେ ଏକ ପ୍ରାଇଭେଟ୍ କମ୍ପାନୀରେ କାର୍ଯ୍ୟ କରୁଥିଲେ । ସେ ଏହି ଆତ୍ମକଥାରେ ସ୍ୱତନ୍ତ୍ର ଟାସ୍କ ଫୋର୍ସ ହାଜତରେ ବିତାଇଥିବା ୫୦ ଦିନର ଘଟଣାକୁ ମଧ୍ୟ ବର୍ଣ୍ଣନା କରିଛନ୍ତି । ଏହାଛଡ଼ା ଜେଲରେ କନ୍ୟା ସନ୍ତାନର ପ୍ରସବ ଘଟଣା ମଧ୍ୟ ଅତ୍ୟନ୍ତ ଲୋମହର୍ଷଣକାରୀ । ଜେଲ ଭିତରେ ସଂପୂର୍ଣ୍ଣ ଉଲଗ୍ନ ରଖିବା, ଛାତିକୁ ମାଡ଼ ମାରିବା, ଅନ୍ୟମାନଙ୍କ ସମ୍ମୁଖରେ ସ୍ୱାମୀ ସହ ଦୈହିକ ସଂପର୍କ ସ୍ଥାପନ କରିବା ପାଇଁ ବାଧ୍ୟ କରିବା, ପୋଲିସର ଗଣବଳାତ୍କାରର ଧମକ ଇତ୍ୟାଦି ଅନେକ ଲୋମଟାଙ୍କୁରା ଘଟଣାକୁ ନଳିନୀ ଏହି ପୁସ୍ତକରେ ସ୍ଥାନ ଦେଇଛନ୍ତି । ଗର୍ଭବତୀ ଅବସ୍ଥାରେ ‘ଆଡ଼ ଡ଼ିଗ୍ରୀ’ ନିର୍ଯ୍ୟାତନା ମଧ୍ୟ ତାଙ୍କୁ ସହ୍ୟ କରିବାକୁ ପଡ଼ିଛି । ତାର ଗର୍ଭ ନଷ୍ଟ ପାଇଁ ପୁଲିସ ଜଣେ ମହିଳା ଡ଼ାକ୍ତରଙ୍କ ନିକଟକୁ ନେଇଥିଲା । ତେବେ ମହିଳା ଡ଼ାକ୍ତର ଜଣକ ଏପରି କରିବେ ନାହିଁ ବୋଲି ପ୍ରକାଶ କରି ପୁଲିସକୁ ଭତ୍ସନା କରିଥିଲେ । ସେହି ମହିଳା ଡ଼ାକ୍ତର ଜଣଙ୍କୁ ସେ ଏବେ ମଧ୍ୟ ଦେବୀ ରୂପେ ସ୍ୱୀକାର କରନ୍ତି ବୋଲି ଏହି ପୁସ୍ତକରେ ଲେଖିଛନ୍ତି । \r\nନଳିନୀର ଝିଅ ‘ଅଭିଥ୍ରା’ ଏବେ ଲଣ୍ଡନରେ ଡ଼ାକ୍ତର ଅଛନ୍ତି । ଜେଲ ଭିତରେ ସେ କିପରି ଭୋକରେ ଛଟପଟ ହେଉଥିଲେ, ତାହାକୁ ମଧ୍ୟ ଏହି ପୁସ୍ତକରେ ବର୍ଣ୍ଣନା କରାଯାଇଛି । ତେବେ ସବୁ ଜେଲ ଅଧିକାରୀ ନିର୍ଦ୍ଦୟ ନୁହନ୍ତି । କେତେଜଣ ତାଙ୍କୁ ଆର୍ଥିକ ସହାୟତା କରିଛନ୍ତି ଏବଂ ତାଙ୍କ ଝିଅକୁ ଅରିଥ୍ରା ପ୍ରଥମେ କୋଏମ୍ବାଟୁର ଏବଂ ପରେ ଶ୍ରୀଲଙ୍କାରେ ଥିବା ନିଜ ସଂପର୍କୀୟ ମାନଙ୍କ ନିକଟକୁ ପଠାଇ ଦେଇଥିବା କଥା ପୁସ୍ତକରେ ଉଲ୍ଲେଖ କରାଯାଇଛି । ଶ୍ରୀଲଙ୍କାରୁ ସେ ବେଆଇନ ଭାବେ ୟୁରୋପ ଚାଲି ଯାଇଥିଲା । ଭାରତ ସରକାର ବ୍ରିଟିସ ସରକାରଙ୍କ ସହଯୋଗ ଓ ପାଠ ପଢ଼ିବା କ୍ଷେତ୍ରରେ ସହାୟତା ଦେବା ପରେ ସେ ଆଜି ଡ଼ାକ୍ତର ହୋଇପାରିଛି । \r\nସେ ଲେଖିଛନ୍ତିଯେ, ତାମିଲନାଡ଼ୁରେ ଜୟଲଳିତା ସରକାର କ୍ଷମତାକୁ ଆସିବା ପରେ ତାଙ୍କ ଜେଲ ଜୀବନରେ କିଛିଟା ପରିବର୍ତ୍ତନ ଆସି ପାରିଥିଲା, ମାତ୍ର ଡ଼ିଏମ୍କେ ଶାସନ କାଳରେ ତାଙ୍କ ଜେଲ ଜୀବନ ଖୁବ୍ କଷ୍ଟକର ଥିଲା ।','2016_11_19_23_18_10_Nalini.jpg','Newstory','27-01-2017','#000000','','38'),(61,'ଦରମା କ୍ୟାସ୍ ଆକାରରେ ଦେବାକୁ ଦାବି',NULL,'ନୂଆଦିଲ୍ଲୀ:ଲୋକସଭା ଓ ରାଜ୍ୟସଭାର କର୍ମଚାରୀମାନଙ୍କୁବେତନ ଦେବା ପାଇଁ ୫କୋଟିଟଙ୍କା ନଗଦ ଆକାରରେ ରଖିବା ନିମନ୍ତେ ଲୋକସଭା-ରାଜ୍ୟସଭା ସଚିବାଳୟ ପକ୍ଷରୁ ଭାରତୀୟ ଷ୍ଟେବ୍ୟାଙ୍କକୁ ଅନୁରୋଧ କରାଯାଇଛି । ଫଳରେ ସଚିବାଳୟରେ କାମ କରୁଥିବା କର୍ମଚାରୀମାନଙ୍କୁ ନଗଦ୍ ଆକାରରେ ବେତନ ଦେବା ସମ୍ଭବ ହୋଇ ପାରିବ ।  କାରଣ ଧାଡ଼ିରେ ଛିଡ଼ା ନହୋଇ ନଗଦ ଆକାରରେ ବେତନ ନେବା ପାଇଁ କର୍ମଚାରୀମାନେ ଦାବି କରିଛନ୍ତି । \r\nଲୋକସଭା-ରାଜ୍ୟସଭା ସଚିବାଳୟରେ କାମ କରୁଥିବା ୪ ହଜାର ନଗଦ ଆକାରରେ ବେତନ ଦେବାକୁ ଦାବି କରିଛନ୍ତି । ଫଳରେ ରିଜର୍ଭ ବ୍ୟାଙ୍କ ଏଥିପାଇଁ ନିର୍ଦ୍ଦେଶ ଦେବାକୁ ଏସବିଆଇ ବ୍ରାଞ୍ଚମ୍ୟାନେଜର ଅନୁରୋଧ କରିଛନ୍ତି । ୪ ହଜାର ସଂସଦ କର୍ମଚାରୀଙ୍କର ଷ୍ଟେଟବ୍ୟାଙ୍କରେ ଆକାଉଣ୍ଟ ରହିଛି । ତେବେ ଏତେ ବଡ଼ ପରିମାଣର ନଗଦ ଟଙ୍କା ବାହାର କରି ପ୍ରଦାନକରିବାରେ କୌଣସି ଅସୁବିଧା ନାହିଁ ବୋଲି ବ୍ୟାଙ୍କର ଜଣେ କର୍ମଚାରୀ କହିଛନ୍ତି । ତେବେ ଏ ସଂପର୍କରେ ଚୁଡ଼ାନ୍ତ ନିଷ୍ପତ୍ତି ରିଜର୍ଭ ବ୍ୟାଙ୍କ ଉପରେ ନିର୍ଭର କରୁଛି ବୋଲି ସେ କହିଛନ୍ତି ।','2016_11_20_22_10_30_cash.jpg','Newstory','27-01-2017','#000000','','25'),(62,'ଆସନ୍ତାବର୍ଷ ମିଳିବ ବହୁତ ଛୁଟି','ସରକାରୀ ଚାକିରିଆଙ୍କ ପାଇଁ ଫାଇଦା','ନୂଆଦିଲ୍ଲୀ,୨୧ ।୧୧: ଆଉ ଗୋଟିଏ ମାସ ଅନ୍ତରରେ ଆସୁଛି ନୂଆବର୍ଷ । ନୂଆବର୍ଷ କାହାପାଇଁ କଣ ନେଇ ଆସୁଛି, ତାହା ହୁଏତ କହିବା ମୁଷ୍କିଲ, ମାତ୍ର ସରକାରୀ କର୍ମଚାରୀଙ୍କୁ ପାଇଁ ଆଣୁଛି ଅପାର ଆନନ୍ଦ ଓ ମସ୍ତିର ସମୟ । କାରଣ ଟିକିଏ ହିସାବରେ ଚାଲିଲେ ଆସନ୍ତା ବର୍ଷ ମିଳିବ ଏକାଧିକ ଛୁଟି ।\r\n୨୦୧୭ ମସିହାର ପ୍ରଥମ ଛୁଟି ମକର ସଂକ୍ରାନ୍ତି ଶନିବାର ପଡୁଛି । ଫଳରେ ଚାକିରିଆମାନଙ୍କ ପାଇଁ ପୁରା ଦୁଇଦିନ ଛୁଟି । ଜାନୁଆରୀର ଚତୁର୍ଥ ସପ୍ତାହ ଗୁରୁବାର ପଡୁଛି ସାଧାରଣତନ୍ତ୍ର ଦିବସ । ତେଣୁ ଶୁକ୍ରବାରଟି ନିଜ ‘ବସ୍’କୁ ପ୍ରଭାବିତ କରି ଛୁଟୀ ନେଇପାରିଲେ ପୁରା ଚାରିଦିନ ଛୁଟି ମିଳିଯିବ । ସେହିପରି ଫେବୃୟାରୀ ୨୪ ଶୁକ୍ରବାର ପଡିବ ମହାଶିବରାତ୍ରୀ । ସେହିପରି ମାର୍ଚ ୧୩ ସୋମାବର ଦିନ ହୋଲି ଉତ୍ସବ ପଡୁଛି । ତେଣୁ ସୋମବାର ଦିନ ଛୁଟି ପଡିଲେ ତାହାର ଗୁରୁତ୍ୱ କେତେ, ତାହା ଆଉ ବଖାଣିବାର ଆବଶ୍ୟକତା ନାହିଁ । ଏପ୍ରିଲରେ ଗୁଡଫାଇଡେ । ମେ’ ୧ରେ ଶ୍ରମିକ ଦିବସ ମଧ୍ୟ ସୋମବାର ପଡୁଛି । ସେହିପରି ଜଣେ ସରକାରୀ କର୍ମଚାରୀ ଯଦି ଅଗଷ୍ଟ ୧୪ ତାରଖଟି ଛୁଟୀ ନେବ ତେବେ ତାଙ୍କୁ ଅଗଷ୍ଟ ୧୨ ରୁ ୧୫ ତାରିଖ ପର୍ଯନ୍ତ ଛୁଟି ମିଳିଯିବ । ସେହିପରି ଅଗଷ୍ଟ ୨୫ ଶୁକ୍ରବାର ପଡୁଛି ଗଣେଶ ଚତୁର୍ଥୀ । ସବୁଠୁ ଫାଇଦା ଦଶହାରା ସମୟରେ ମିଳିପାରିବ । ଜଣକୁ କେବଳ ତିନି ଦିନ ଛୁଟୀ ନେବାକୁ ହେବ । ଏହା ହୋଇପାରିଲେ ଦଶହରା ସମୟରେ ପୁରା ଦଶଦିନ ଛୁଟି ମିଳିଯିବ । ଏହି ସମୟରେ ମହାଷ୍ଟମୀ, ବିଜୟାଦଶମୀ, ଗାନ୍ଧୀ ଜୟନ୍ତୀ, ବାଲ୍ମିକୀ ଜୟନ୍ତୀ ଆଦି ଛୁଟି ଦିନେ ଦୁଇଦିନ ଅନ୍ତରରେ ପଡୁଛି । ଠିକ୍ ରୂପେ ପ୍ଲାନିଙ୍ଗ କଲେ ସେପ୍ଟେମ୍ବର ୨୯ ରୁ ଅକ୍ଟୋବର ୮ ପର୍ଯ୍ୟନ୍ତ ଲମ୍ବା ଛୁଟୀ ମିଳିଯିବ ।\r\nଦୀପାବଳିରେ ମଧ୍ୟ ଲମ୍ବା ଛୁଟୀ ମଧ୍ୟ ମିଳିବ । ବୁଧବାର ୭ ଶୁକ୍ରବାର ଅଫିସ ଛୁଟି । ଏହାପରେ ଶନି ଓ ରବିବାର ଛୁଟି ମିଳିଯିବ । ସେହିପରି ଆସନ୍ତା ବର୍ଷ ବଡଦିନ ପର୍ବ ମଧ୍ୟ ସୋମବାର ପଡୁଛି । ତେଣୁ ପ୍ଳାନିଙ୍ଗ କଲେ ଡିସେମ୍ବର ୨୩ ରୁ ୨୫ ପର୍ଯ୍ୟନ୍ତ ଛୁଟି ମିଳିପାରିବ ।','2016_11_21_18_02_51_Govt service.jpg','Newstory','27-01-2017','#ff0000','','0'),(63,'ଯାହାକୁ ରଖିବେ ଅନନ୍ତ...','ଆୟୁଷ ପାଇଲା ଲମ୍ବା ଆୟୁଷ','କାନପୁୁର,୨୧ ।୧୧: ଯାହାକୁ ରଖିବେ ଅନନ୍ତ, କି କରିପାରେ ବଳବନ୍ତ । ଏହି ଉକ୍ତିର ସାର୍ଥକତା ଦେଖିବାକୁ ମିଳିଛି କାନପୁର ଟ୍ରେନ ଦୁର୍ଘଟଣା ସ୍ଥଳରେ । ଏହି ଭୟାବହ ଟ୍ରେନ ଦୁର୍ଘଟଣାରେ ୧୪୫ ରୁ ଉଦ୍ଧ୍ୱର୍ ଯାତ୍ରୀଙ୍କ ମୃତ୍ୟୁ ହୋଇଯାଇଛି । ଅନେକ ଗୁୁରୁତର  ଅବସ୍ଥାରେ ହସପିଟାଲ ବେଡରେ ମୃତ୍ୟୁସହ ସଂଗ୍ରାମ କରୁଛନ୍ତି । ତେବେ ଗୋଟିଏ ଶିଶୁକୁ ଚାପି ହୋଇଯାଇଥିବା ଟ୍ରେନ ବଗି ଭିତରୁ ଅକ୍ଷତ ଅବସ୍ଥାରେ ଉଦ୍ଧାର କରାଯାଇଛି । ଅନ୍ୟପକ୍ଷରେ ଶିଶୁଟିର ମା ଗୁରୁତର ଅବସ୍ଥାରେ ହସପିଟାଲରେ ଚିକିତ୍ସାଧିନ ରହିଛି । \r\nଆଜମଗଡର ବାସିନ୍ଦା ରାଜୁ ଗୁପ୍ତା ମୁମ୍ବାଇରେ କାମ କରି ପରିବାର ଭରଣପୋଷଣ କରେ । ତାଙ୍କ ସହ ପତ୍ନୀ ରୀନା ଓ ତିନି ବର୍ଷର ପୁଅ ଆୟୁଷ ମଧ୍ୟ ମୁମ୍ବାଇରେ ରୁହନ୍ତି । ବିବାହର ବହୁବର୍ଷ ପର୍ଯ୍ୟନ୍ତ ନିଃସନ୍ତାନ ଥିଲେ । ଶେଷରେ ସେମନେ କୁଳଦେବୀଙ୍କ ଆଶ୍ରୟ ନେଇଥିଲେ । ସେମାନଙ୍କର ସନ୍ତାନଟିଏ ହେଲେ ତିନି ବର୍ଷ ପରେ ମାଙ୍କ ନିକଟରେ ମାନସିକ ପୂରଣ କରିବେ ବୋଲି ଶପଥ ନେଇଥିଲେ । ପୁଅକୁ ତିନିବର୍ଷ ହେବା ପରେ ସେମାନେ ଆଜମଗଡ ନିଜ ପୈତୃକ ଗାଁକୁ ଆସୁଥିଲେ ମାଙ୍କ ପୂଜା କରିବା ପାଇଁ । ମାତ୍ର ଟ୍ରେନଟି ଦୁର୍ଘଟଣାଗ୍ରସ୍ତ ହେଲା । ବହୁ ଲୋକ ମୃତାହତ ହେଲେ । କିନ୍ତୁ ମା ଙ୍କ ଅପାର କୃପାରୁ ଆୟୁଷଙ୍କୁ ଲମ୍ବା ଆୟୁଷ ମିଳିପାରିଛି ।','2016_11_21_18_10_10_train.jpeg','Newstory','27-01-2017','#8000ff','','44'),(64,'ଆଫଗାନ ମହିଳାଙ୍କୁ ଠକିଲା ଭାରତୀୟ ପୁରୁଷ','ସାଢ଼େ ୪ଲକ୍ଷ ଅଚଳ ଟଙ୍କା ଧରାଇ ଚମ୍ପଟ','ନୂଆଦିଲ୍ଲୀ: ବିମୁଦ୍ରୀକରଣର ଠିକ୍ ପରଦିନ କାବୁଲର ଏକ ଡ଼୍ରାଏଫ୍ରୁଟ୍ ବ୍ୟବସାୟ କରୁଥିବା ମହିଳାଙ୍କୁ ଜଣେ ଭାରତୀୟ ଧୋକା ଦେଇଥିବା ଖବର ମିଳିଛି । ଏହି ବ୍ୟକ୍ତି ଜଣକ ସଂପୃକ୍ତକାବୁଲ ମହିଳାଙ୍କୁ ସାଢ଼େ୪ ଲକ୍ଷ ଟଙ୍କାର ୫ଶହ ଏବଂ ହଜାର ଟଙ୍କିଆ ଭାରତୀୟ ନୋଟ ଦେଇ ଡ଼୍ରାଏଫୁଟ କିଣି ନେଇଥିଲା । ଘଟଣାର ଦୁଇଦିନ ପରେ ସଂପୃକ୍ତ ମହିଳା ଜଣକ ଧୋକା ଖାଇଥିବା ଜାଣିବାକୁ ପାଇଥିଲେ । ସେହି ପୁରୁଣା ନୋଟକୁ ବଦଳାଇବା ପାଇଁ ମହିଳା ଜଣଙ୍କୁ ଏବେ ଭାରତ ଆସିବାକୁ ପଡ଼ିଛି । ତେବେ ସେତିକିରେ ମଧ୍ୟ ଦୁଃଖ ଶେଷ ହୋଇନାହିଁ, ମହିଳା ଜଣକ ଦିଲ୍ଲୀର ଇନ୍ଦରା ଗାନ୍ଧି ଆନ୍ତର୍ଜାତିକ ବିମାନ ବନ୍ଦରରେ ଅବତରଣ କରିବା ପରେ କଷ୍ଟମ ଅଧିକାରୀମାନେ ତାଙ୍କୁ ଅଟକ ରଖିଛନ୍ତି । ଏବେ ଘଟଣାର ତଦନ୍ତ କରାଯାଉଛି । \r\nକଷ୍ଟମ ବିଭାଗ ସୂତ୍ରରୁ ଜଣା ଯାଇଛିଯେ, ୪୦ ବର୍ଷ ବୟସ୍କ ଏହି ମହିଳା ଜଣକ ବିଜିନେସ ଭିସାରେ ଭାରତ ଆସିଛନ୍ତି ।  ସେ ଦୀର୍ଘ ୧୦ ବର୍ଷ ହେବ କାଉଣ୍ଟରରେ ଡ଼୍ରାଏଫ୍ରୁଟ୍ ବ୍ୟବସାୟ କରୁଛନ୍ତି ।  ମହିଳା ଜଣକ କହିଛନ୍ତିଯେ, ଆଫଗାନିସ୍ତାନରେ ଡ଼୍ରାଏଫୁଟ୍ କିମ୍ବା ଅନ୍ୟାନ୍ୟ ସାମଗ୍ରୀ କିଣାରେ ଭାରତୀୟ ମୁଦ୍ରା ପ୍ରଚଳନ କହୋଇ ଆସୁଛିୀ । ସେ କହିଛନ୍ତିଯେ, ଗତ ୯ ତାରିଖରେ ଜଣେ ଭାରତୀୟ ତାଙ୍କ ନିକଟକୁ ଆସି ଦିଲ୍ଲୀର ବାସିନ୍ଦା ବୋଲି କହିବା ସହ ମୋଟା ଅଙ୍କର ଜିନିଷ କ୍ରୟ ପାଇଁ ଚାହିଁଥିଲା । ଫଳରେ ସେ ତାଙ୍କୁ ହୋଇସେଲ ଦରରେ ଡ଼୍ରାଏଫ୍ରୁଟ ଯୋଗାଇ ଦେଇଥିଲେ । ଏହା ବ୍ୟତୀତ ତାଙ୍କ ନିକଟରେ ମଧ୍ୟ ୫ ଲକ୍ଷ ଟଙ୍କାର ଭାରତୀୟ ନୋଟ ଥିଲା । ସେ ଯେବେ ଜାଣିଲେ ଯେ, ଏହି ସବୁ ନୋଟକୁ ଭାରତ ସରକାର ଅଚଳ ଘୋଷଣା କରିଛନ୍ତି । ସେତେବେଳ ତାଙ୍କ ଚିନ୍ତା ବଢ଼ିଯାଇଥିଲା । ସେ ସମସ୍ତ ଟଙ୍କା ଧରି ଭାରତ ଆସିଛନ୍ତି ବଦଳାଇବା ପାଇଁ ବୋଲି କଷ୍ଟମ ଅଧିକାରୀମାନଙ୍କ ଆଗରେ ପ୍ରକାଶ କରିଛନ୍ତି ।','2016_11_23_21_20_53_IG.jpg','Newstory','27-01-2017','#8000ff','','38'),(65,'ମଦୁରାଇରୁ ୩ ଅଲ୍କାଏଦା ଆତଙ୍କବାଦୀ ଗିରଫ',NULL,'ଚେନ୍ନାଇ,୨୮ା୧୧: ଏକାଧିକ କୋର୍ଟରେ ବୋମା ମାଡ କରିଥିବା ୩ଜଣ ଅଲ୍କାଏଦା ଆତଙ୍କବାଦୀଙ୍କୁ ତାମିଲନାଡୁ ପୋଲିସ୍ ଏବଂ ରାଷ୍ଟ୍ରୀୟ ଯାଞ୍ଚ ଏଜେନ୍ସିର ମିଳିତ ଅପରେସନ ବଳରେ ମଦୁରାଇ ସହରରୁ ଗିରଫ କରାଯାଇଛି । ସେମାନେ  ମହୀଶୁର, ଚିତ୍ତୋର, କୋଲ୍ଲମ, ନେଲୋର ଏବଂ ମାଲାପୁରମ୍ ଆଦି କୋର୍ଟରେ ବୋମା ବିସ୍ଫୋରଣ କରିଥିବା ଜଣାଯାଇଛି । ସେମାନେ ମଧ୍ୟ ପ୍ରଧାନମନ୍ତ୍ରୀ ନରେନ୍ଦ୍ର ମୋଦି ଓ ଅନ୍ୟ କେତେଜଣ ଭିଆଇପିଙ୍କୁ ଟାର୍ଗେଟ୍ କରି ଯୋଜନା କରିଥିଲେ ବୋଲି ପୋଲିସ୍ ଜାଣିପାରିଛି । ଗିରଫ ୩ ଆତଙ୍କବାଦୀ ହେଲେ ଏମ୍.ଖାରିସ୍, ଆସିଫ ସୁଲତାନ ମହମ୍ମଦ ଏବଂ ଆବ୍ବାସ ଅଲ୍ଲୀ । ସେମାନଙ୍କ ନିକଟରୁ ବିପୁଳ ମାତ୍ରାର ବିସ୍ଫୋରକ ସାମଗ୍ରୀ ଜବତ ହୋଇଛି । ଏହି ୩ଜଣ ତାମିଲନାଡୁରେ ଅଲ୍କାଏଦା ନେଟ୍ୱାର୍କ ଚଳାଇଥିଲେ ।','2016_11_28_22_28_18_NIA.jpg','Newstory','27-01-2017','#000000','','51'),(66,'ଭାରତ ଜାଣିଲାନି ‘ଭାରତବନ୍ଦ’',NULL,'ବିମୁଦ୍ରାକରଣ ପଦକ୍ଷେପକୁ ବିରୋଧ କରି କଂଗ୍ରେସ ସମେତ ତୃଣମୂଳ କଂଗ୍ରେସ, ବହୁଜନ ସମାଜ ପାର୍ଟି, ସମାଜବାଦୀ ପାର୍ଟି, ଓ ବାମଦଳ ଦଳ ପକ୍ଷରୁ ଆଜି ଜନ ଆକ୍ରୋଶ ଦିବସ ପାଳନ କରାଯାଇଛି। ମାତ୍ର ସାଧାରଣ ଜନତାଙ୍କ ସମର୍ଥନ ଅଭାବରୁ  ଏହା  ଏକପ୍ରକାର ବିଫଳ ହୋଇଛି।। ବିରୋଧୀ ଦଳଗୁଡ଼ିକ ଏକାଠି ହୋଇ ଏହି ପ୍ରସଂଗରେ ସଂସଦରେ ସରକାରଙ୍କୁ ଘେରୁଥିବା ବେଳେ ଆଜି କିନ୍ତୁ ଅଲଗା ଅଲଗା ଆଭିମୁଖ୍ୟ ପ୍ରଦର୍ଶନ କରିଥିଲେ। ପଶ୍ଚିମବଂଗ, ତ୍ରିପୁରା ଓ କେରଳ ଭଳି ରାଜ୍ୟ ବାମ ସଂଗଠନ ପକ୍ଷରୁ ଭାରତ ବନ୍ଦ ଡାକରା ଦିଆଯାଇଥିଲା। ତେଣୁ ଏସବୁ ରାଜ୍ୟରେ ବନ୍ଦର ପ୍ରଭାବ ଅନୁଭୂତ ହୋଇଥିବା ଅନ୍ୟ ସବଠୁ ଜୀବନଯାତ୍ରା ସ୍ୱାଭାବିକ ଥିଲା। କଂଗ୍ରେସ ଓ  ତୃଣକଂଗ୍ରେସ ବନ୍ଦ ପାଳନ ନକରି ପ୍ରତିବାଦ ଓ ବିକ୍ଷୋଭ ପ୍ରଦର୍ଶନରେ ସୀମିତ ଥିଲେ। \r\n କେରଳରେ ଶାସକ ଏଲଡିଏଫ  ମୁଦ୍ରାଅଚଳକୁ ବିରୋଧ କରି ବନ୍ଦ ଡାକରା ଦେଇଥିଲା। ସିପିଆଇ(ଏମ)ର ବିଭିନ୍ନ ବିଧାୟକଙ୍କ ସମେତ ମନ୍ତ୍ରୀମଣ୍ଡଳର ଅନେକ ସଦସ୍ୟ ବନ୍ଦରେ ସାମିଲ ହେବା ସହ ବିକ୍ଷୋଭ ପ୍ରଦର୍ଶନ କରିଥିଲେ। ଫଳରେ ଏଠାରେ ବିଭିନ୍ନ କାର୍ଯ୍ୟାଳୟ ଶିକ୍ଷାନୁଷ୍ଠାନ ପ୍ରାୟତ ବନ୍ଦ ରହିଥିଲା। ସରକାରୀ ପରିବହନ ବ୍ୟବସ୍ଥା ଓ ଘରୋଇ ବସ୍‌ ଚଳାଚଳ ସଂପୂର୍ଣ୍ଣ ବନ୍ଦ ଥିବା ବେଳେ ଅଟୋ ଓ ଅନ୍ୟ ଘରୋଇ ପରିବହନ ଓ ରେଳ ଚଳାଚଳ ଉପରେ କୌଣସି ରୋକ ଲଗାଯାଇ ନଥିଲା। \r\n ସେହିପରି ତ୍ରିପୁରାରେ ବାମଦଳଙ୍କ ୧୨ ଘଣ୍ଟିଆ ବନ୍ଦ ଡାକରା ଯୋଗୁଁ ଜନଜୀବନ ବେଶ ପ୍ରଭାବିତ ହୋଇଥିଲା। ବିଭିନ୍ନ ସରକାରୀ ଓ ବେସରକାରୀ କାର୍ଯ୍ୟାଳୟ ସହ ଶିକ୍ଷାନୁଷ୍ଠାନ ଏବଂ ବ୍ୟାଙ୍କ ବନ୍ଦ ରହିଥିଲା। ସେହିପରି ଟ୍ରେନ ଓ ଅନ୍ୟାନ୍ୟ ପରିବହନ ବ୍ୟବସ୍ଥା ଉପରେ ବନ୍ଦର  ପ୍ରଭାବ ଦେଖିବାକୁ ମିଳିଥିଲା। ଫଳରେ ଏଠାରେ ଜନଜୀବନ ଏକପ୍ରକାର ଠପ୍‌ ହୋଇଯାଇଥିଲା। ତେବେ ଅନ୍ୟ କୌଣସି ରାଜ୍ୟରେ ବନ୍ଦର କୌଣସି ପ୍ରଭାବ ପରିଲକ୍ଷିତ ହୋଇ ନଥିଲା। ପଶ୍ଚିମବଂଗରେ ମଧ୍ୟ ବନ୍ଦର କୌଣସି ପ୍ରଭାବ ପଡ଼ିନଥିଲା। ବାମ ଦଳପକ୍ଷରୁ ବନ୍ଦ ଡାକରା ଦିଆ ଯାଇଥିବା ବେଳେ ଶାସକ ତୃଣମୂଳ କଂଗ୍ରେସ ଏହାର ବିରୋଧ କରିଥିଲା। ମୁଖ୍ୟମନ୍ତ୍ରୀ ମମତା ବାନାର୍ଜୀ ବିମୁଦ୍ରାକରଣକୁ ବିରୋଧ କରି କେନ୍ଦ୍ର ସରକାରଙ୍କ ଉପରେ ବର୍ଷିଥିଲେ। ସେହିପରି ବିହାରରେ ଟ୍ରେନ ଚଳାଚଳ ଉପରେ କିଛି ପରିମାଣରେ ବନ୍ଦ ପ୍ରଭାବ ଦେଖିବାକୁ ମିଳିଥିବା ବେଳେ ଅନ୍ୟ ସବୁଠି ସ୍ଥିତି ସ୍ୱାଭାବିକ ଥିଲା। ଜେଡୟୁ ବନ୍ଦ ପାଳନକୁ ସମର୍ଥନ କରି ନଥିବା ସହ ମୁଖ୍ୟମନ୍ତ୍ରୀ ନୀତୀଶ କୁମାର କେନ୍ଦ୍ର ସରକାରଙ୍କ ବିମୁଦ୍ରାକରଣକୁ ସମର୍ଥନ କରିଥିଲେ। ତାମିଲନାଡ଼ୁ ଚେନ୍ନାଇରେ ଡିଏମ୍‌କେ ନେତା ଏମ୍‌କେ ଷ୍ଟାଲିନ ଓ ତାଙ୍କ ସମର୍ଥକଙ୍କୁ ବିକ୍ଷୋଭ ପ୍ରଦର୍ଶନ ବେଳେ ପୁଲିସ ଅଟକ ରଖିଥିଲା।','2016_11_28_23_32_44_Bharat band.jpeg','Newstory','27-01-2017','#000000','','56'),(67,'ବିଜେପି ସାଂସଦ ଓ ବିଧାୟକ ହରଡଘଣାରେ','ବ୍ୟାଙ୍କ ଆକାଉଣ୍ଟ ଷ୍ଟେଟମେଣ୍ଟ୍ ମାଗିଲେ ପ୍ରଧାନମନ୍ତ୍ରୀ','ନୂଆଦିଲ୍ଲୀ,୨୯ ।୧୧ : ବିଜେପିର ସବୁ ସାଂସଦ ଓ ବିଧାୟକ ମାନଙ୍କୁ ନିଜ ନିଜ ବ୍ୟାଙ୍କ ଆକାଉଣ୍ଟର ଷ୍ଟେଟମେଣ୍ଟ୍ ଦେବାକୁ ପଡିବ । ନଭେମ୍ବର ୮ରୁ ଡିସେମ୍ବର ୩୧ ମଧ୍ୟରେ ସେମାନଙ୍କ ଆକାଉଣ୍ଟରେ କେତେ ପରିମାରର ଟଙ୍କା ନେଣଦେଣ ହୋଇଛି ତାହାର ବିସ୍ତୃତ ବିବରଣୀ ଦେବାକୁ ପ୍ରଧାନମନ୍ତ୍ରୀ ନରେନ୍ଦ୍ର ମୋଦି ନିର୍ଦ୍ଦେଶ ଦେଇଛନ୍ତି । ସାଂସଦ ଓ ବିଧାୟକ ମାନେ ସେମାନଙ୍କ ବ୍ୟାଙ୍କ ଷ୍ଟେଟମେଣ୍ଟ୍ ଦଳର ସଭାପତି ଅମିତ ଶାହାଙ୍କୁ ପ୍ରଦାନ କରିବାକୁ କୁହାଯାଇଛି । ବିଜେପି ସଂସଦୀୟ ଦଳ ବୈଠକରେ ପ୍ରଧାନମନ୍ତ୍ରୀ ଶ୍ରୀ ମୋଦି ଯୋଗଦେଇ କହିଛନ୍ତି ଯେ ଆୟକର ସଂଶୋଧନ ବିଲ୍ କଳାଧନକୁ ଧଳା କରିବା ପାଇଁ ଅଣାଯାଉନାହିଁ । ବରଂ ଗରୀବଙ୍କ ଠାରୁ ଲୁଟ୍ କରାଯାଇଥିବା ଅର୍ଥ, ସେମାନଙ୍କ କଲ୍ୟାଣରେ ବିନିଯୋଗ ପାଇଁ ଉଦ୍ଦିଷ୍ଟ । ଏହି ଅବରସରେ ସେ ଦଳୀୟ ସାଂସଦ ମାନଙ୍କୁ କହିଛନ୍ତି ଯେ, ସେମାନେ ନିଜ ନିଜ ଅଞ୍ଚଳରେ ବ୍ୟବସାୟ ମାନଙ୍କୁ ନଗଦ ଟଙ୍କା କାରବାରକୁ ନିରୁତ୍ସାହିତ କରିବା କାର୍ଯ୍ୟରେ ଲାଗିପଡନ୍ତୁ । ବିମୁଦ୍ରୀକରଣ ପରେ ଡିସେମ୍ବର ୩୦ ମଧ୍ୟରେ ବ୍ୟାଙ୍କ ଆକାଉଣ୍ଟରେ କେତେ ଟଙ୍କା ନେଣଦେଣ ହୋଇଛି, ସେସବୁର ଯାଞ୍ଚ କରାଯିବ ।','2016_11_29_18_24_48_Modi.jpg','hotNews','27-01-2017','#000000','','25');

